<body>
<div id="main-container">
    <!-- ===== Control Panel ===== -->
    <div id="controls-container">
        <!-- Time -->
        <div class="slider-group">
            <label for="slider-time">time</label>
            <input type="range" id="slider-time" min="0" max="25" step="0.01" value="7.0">
            <span id="value-time">7.00</span>
        </div>
        <!-- Wavelength -->
        <div class="slider-group">
            <label for="slider-wavelength">wavelength</label>
            <input type="range" id="slider-wavelength" min="1" max="8" step="0.01" value="4.0">
            <span id="value-wavelength">4.00</span>
        </div>
        <!-- Longitudinal Amplitude -->
        <div class="slider-group">
            <label for="slider-longitudinal-amplitude">longitudinal amplitude</label>
            <input type="range" id="slider-longitudinal-amplitude" min="0" max="3" step="0.01" value="2.0">
            <span id="value-longitudinal-amplitude">2.00</span>
        </div>
        <!-- Latitudinal Amplitude -->
        <div class="slider-group">
            <label for="slider-latitudinal-amplitude">latitudinal amplitude</label>
            <input type="range" id="slider-latitudinal-amplitude" min="0" max="3" step="0.01" value="1.0">
            <span id="value-latitudinal-amplitude">1.00</span>
        </div>
    </div>

    <!-- ===== Visualization Canvas ===== -->
    <div id="canvas-container"></div>
</div>

<script>
/* ------------------------------
   Global variables & DOM Cache
--------------------------------*/
let sliderTime, sliderWavelength, sliderLongAmp, sliderLatAmp;
let displayTime, displayWavelength, displayLongAmp, displayLatAmp;

const COLS = 10;
const ROWS = 6;
let eqPoints = [];        // equilibrium (x0,y0)
let currPositions = [];   // current particle positions
let t = 7.0;              // internal time variable
let timeSliderActive = false;

/* ------------------------------
   p5.js Setup
--------------------------------*/
function setup(){
    const canvas = createCanvas(700, 450);
    canvas.parent('canvas-container');

    /* ----- Cache DOM ----- */
    sliderTime      = document.getElementById('slider-time');
    sliderWavelength= document.getElementById('slider-wavelength');
    sliderLongAmp   = document.getElementById('slider-longitudinal-amplitude');
    sliderLatAmp    = document.getElementById('slider-latitudinal-amplitude');

    displayTime     = document.getElementById('value-time');
    displayWavelength= document.getElementById('value-wavelength');
    displayLongAmp  = document.getElementById('value-longitudinal-amplitude');
    displayLatAmp   = document.getElementById('value-latitudinal-amplitude');

    /* ----- Slider Events ----- */
    sliderTime.addEventListener('input', () => {
        t = parseFloat(sliderTime.value);
        updateDisplay(displayTime, t);
    });
    sliderWavelength.addEventListener('input', () => {
        updateDisplay(displayWavelength, sliderWavelength.value);
    });
    sliderLongAmp.addEventListener('input', () => {
        updateDisplay(displayLongAmp, sliderLongAmp.value);
    });
    sliderLatAmp.addEventListener('input', () => {
        updateDisplay(displayLatAmp, sliderLatAmp.value);
    });

    /* Pause/resume animation when time slider is held */
    ['mousedown','pointerdown','touchstart'].forEach(evt=>{
        sliderTime.addEventListener(evt, ()=> timeSliderActive = true);
    });
    ['mouseup','pointerup','touchend','mouseleave'].forEach(evt=>{
        sliderTime.addEventListener(evt, ()=> timeSliderActive = false);
    });
    window.addEventListener('mouseup', ()=> timeSliderActive=false);   // safety

    /* Initial value displays */
    updateDisplay(displayTime, t);
    updateDisplay(displayWavelength, sliderWavelength.value);
    updateDisplay(displayLongAmp, sliderLongAmp.value);
    updateDisplay(displayLatAmp, sliderLatAmp.value);

    /* ----- Build Equilibrium Grid ----- */
    const sidePad = 50;
    const topPad  = 50;
    const usableW = width  - 2*sidePad;
    const usableH = height - 2*topPad;

    for(let r=0; r<ROWS; r++){
        for(let c=0; c<COLS; c++){
            const x = sidePad + (usableW/(COLS-1))*c;
            const y = topPad  + (usableH/(ROWS-1))*r;
            eqPoints.push({x,y});
        }
    }
}

/* ------------------------------
   p5.js Draw Loop
--------------------------------*/
function draw(){
    background('#E0F0FF');

    /* ---- Time Update ---- */
    if(!timeSliderActive){
        t += 0.03;
        if(t > parseFloat(sliderTime.max)) t -= parseFloat(sliderTime.max);  // wrap
        sliderTime.value = t.toFixed(2);
        updateDisplay(displayTime, t);
    }

    /* ---- Read Parameters ---- */
    const wavelength   = parseFloat(sliderWavelength.value);
    const longAmpInput = parseFloat(sliderLongAmp.value);
    const latAmpInput  = parseFloat(sliderLatAmp.value);
    const k = TWO_PI / wavelength;

    /* ---- Draw Ellipses & Compute Particle Positions ---- */
    currPositions = [];
    strokeWeight(2);
    noFill();

    eqPoints.forEach((p, idx) => {
        const col = idx % COLS;
        const row = Math.floor(idx / COLS);

        const decay = Math.exp(- (row / (ROWS-1)) * 1.5);
        const ampX = longAmpInput * decay * 20;
        const ampY = latAmpInput  * decay * 20;
        const phase = k * p.x - t;

        /* Elliptical path */
        stroke('#8F998F');
        ellipse(p.x, p.y, ampX*2, ampY*2);

        /* Particle position */
        const dx = ampX * Math.cos(phase);
        const dy = ampY * Math.sin(phase);
        currPositions.push({x: p.x + dx, y: p.y + dy});
    });

    /* ---- Draw Dashed Connecting Lines ---- */
    stroke('#0000FF');
    drawingContext.setLineDash([5,5]);
    for(let r=0; r<ROWS; r++){
        for(let c=0; c<COLS-1; c++){
            const i1 = r*COLS + c;
            const i2 = r*COLS + c + 1;
            line(currPositions[i1].x, currPositions[i1].y,
                 currPositions[i2].x, currPositions[i2].y);
        }
    }
    drawingContext.setLineDash([]);

    /* ---- Draw Particles ---- */
    noStroke();
    fill('#FF0000');
    currPositions.forEach(pos => circle(pos.x, pos.y, 8));
}

/* ------------------------------
   Helpers
--------------------------------*/
function updateDisplay(span, val){
    span.textContent = parseFloat(val).toFixed(2);
}
</script>
</body>
</html>
