        <div class="slider-block">
            <label>T<sub>min</sub> (°C) <span id="value-tmin">25</span></label>
            <input id="slider-tmin" type="range" min="0" max="50" step="1" value="25">
        </div>

        <div class="slider-block">
            <label>T<sub>max</sub> (°C) <span id="value-tmax">75</span></label>
            <input id="slider-tmax" type="range" min="51" max="100" step="1" value="75">
        </div>

        <!-- Model parameters -->
        <h3>model parameters</h3>
        <div class="slider-block">
            <label>T<sub>ref</sub> (°C) <span id="value-tref">35</span></label>
            <input id="slider-tref" type="range" min="0" max="100" step="1" value="35">
        </div>

        <div class="slider-block">
            <label>c (°C<sup>-1</sup>) <span id="value-c">0.025</span></label>
            <input id="slider-c" type="range" min="0.01" max="0.1" step="0.001" value="0.025">
        </div>

        <div class="slider-block">
            <label>b (°C) <span id="value-b">273.16</span></label>
            <input id="slider-b" type="range" min="200" max="350" step="0.01" value="273.16">
        </div>

        <div class="slider-block">
            <label>a (°C) <span id="value-a">4000</span></label>
            <input id="slider-a" type="range" min="1000" max="10000" step="1" value="4000">
        </div>
    </div>

    <!-- RIGHT COLUMN -->
    <div id="right-col">
        <div id="plot-top"></div>
        <div id="plot-bottom"></div>
    </div>
</div>

<script>
// ------------------ Helpers ------------------
function linspace(start, end, num){
    if(num===1) return [start];
    const step = (end - start)/(num - 1);
    return Array.from({length:num}, (_,i)=> start + i*step);
}

function linearRegression(x, y){
    const n = x.length;
    let sumx=0,sumy=0,sumxy=0,sumx2=0;
    for(let i=0;i<n;i++){
        sumx+=x[i];
        sumy+=y[i];
        sumxy+=x[i]*y[i];
        sumx2+=x[i]*x[i];
    }
    const slope = (n*sumxy - sumx*sumy)/(n*sumx2 - sumx*sumx);
    const intercept = (sumy - slope*sumx)/n;

    // R²
    const ymean = sumy/n;
    let ssTot=0, ssRes=0;
    for(let i=0;i<n;i++){
        const yPred = slope*x[i]+intercept;
        ssTot += (y[i]-ymean)**2;
        ssRes += (y[i]-yPred)**2;
    }
    const r2 = 1 - ssRes/ssTot;
    return {slope, intercept, r2};
}

// ------------------ DOM elements ------------------
const btnArrhenius = document.getElementById('btn-arrhenius');
const btnExponential = document.getElementById('btn-exponential');
const sliders = {
    n: document.getElementById('slider-n'),
    tmin: document.getElementById('slider-tmin'),
    tmax: document.getElementById('slider-tmax'),
    tref: document.getElementById('slider-tref'),
    c: document.getElementById('slider-c'),
    b: document.getElementById('slider-b'),
    a: document.getElementById('slider-a')
};
const spans = {
    n: document.getElementById('value-n'),
    tmin: document.getElementById('value-tmin'),
    tmax: document.getElementById('value-tmax'),
    tref: document.getElementById('value-tref'),
    c: document.getElementById('value-c'),
    b: document.getElementById('value-b'),
    a: document.getElementById('value-a')
};

let activeModel = 'Arrhenius'; // default

// ------------------ Event listeners ------------------
btnArrhenius.addEventListener('click', ()=>{setActiveModel('Arrhenius')});
btnExponential.addEventListener('click', ()=>{setActiveModel('exponential')});

Object.keys(sliders).forEach(key=>{
    sliders[key].addEventListener('input', ()=>{
        // update span
        spans[key].textContent = sliders[key].value;
        if(key==='tmin'){enforceTmaxConstraint();}
        updatePlots();
    });
});

// enforce T_max > T_min
function enforceTmaxConstraint(){
    const tminVal = Number(sliders.tmin.value);
    sliders.tmax.min = tminVal + 1;
    if(Number(sliders.tmax.value) <= tminVal){
        sliders.tmax.value = tminVal + 1;
        spans.tmax.textContent = sliders.tmax.value;
    }
}

// model buttons active styling
function setActiveModel(model){
    activeModel = model;
    if(model==='Arrhenius'){
        btnArrhenius.classList.add('active-button');
        btnExponential.classList.remove('active-button');
    }else{
        btnExponential.classList.add('active-button');
        btnArrhenius.classList.remove('active-button');
    }
    updatePlots();
}

// ------------------ Core Update Function ------------------
function updatePlots(){
    const n       = Number(sliders.n.value);
    const T_min   = Number(sliders.tmin.value);
    const T_max   = Number(sliders.tmax.value);
    const T_ref   = Number(sliders.tref.value);
    const c_param = Number(sliders.c.value);
    const b_param = Number(sliders.b.value);
    const a_param = Number(sliders.a.value);

    const Tarray  = linspace(T_min, T_max, n);

    let yData=[];
    let bottomX=[], bottomY=[], fitLineX=[], fitLineY=[];
    let topFitY=[];
    let titleTop='', titleBottom='';
    let regression, fitted_c, fitted_a;

    if(activeModel==='Arrhenius'){
        // generate data with Arrhenius
        yData = Tarray.map(T=> Math.exp(-a_param/(T + b_param)) / Math.exp(-a_param/(T_ref + b_param)));

        // exponential fit: x = T - T_ref, y = ln(y)
        bottomX = Tarray.map(T=> T - T_ref);
        bottomY = yData.map(y=> Math.log(y));

        // force through origin? We'll use regression but display y=c*x (intercept assumed 0)
        regression = linearRegression(bottomX, bottomY);
        fitted_c = regression.slope.toFixed(5);

        // bottom plot fit line (through origin)
        fitLineX = linspace(Math.min(...bottomX), Math.max(...bottomX), 100);
        fitLineY = fitLineX.map(x => regression.slope*x);

        // top plot fitted exponential curve
        topFitY = Tarray.map(T=> Math.exp(regression.slope*(T - T_ref)));

        titleTop    = `exponential model fit to Arrhenius data<br>c = ${fitted_c}    R² = ${regression.r2.toFixed(4)}`;
        titleBottom = `linear fit of the exponential model    R² = ${regression.r2.toFixed(4)}`;

        drawTopPlot(Tarray, yData, topFitY, titleTop, 'k(T)/k(T_ref)', 'T (°C)', '#d62728');
        drawBottomPlot(bottomX, bottomY, fitLineX, fitLineY, titleBottom, 'T - T_ref', 'log(k(T)/k(T_ref))', '#2ca02c', true);
    }else{ // exponential model generates data
        // generate data with exponential model
        yData = Tarray.map(T=> Math.exp(c_param*(T - T_ref)));

        // Arrhenius fit: x = 1/(T + b), y = ln(y)
        bottomX = Tarray.map(T=> 1/(T + b_param));
        bottomY = yData.map(y=> Math.log(y));

        regression = linearRegression(bottomX, bottomY);
        fitted_a = (-regression.slope).toFixed(2);

        // bottom plot fit line
        fitLineX = linspace(Math.min(...bottomX), Math.max(...bottomX), 100);
        fitLineY = fitLineX.map(x => regression.slope*x + regression.intercept);

        // top plot fitted Arrhenius curve
        topFitY = Tarray.map(T=> Math.exp(-(-regression.slope)/(T + b_param)) / Math.exp(-(-regression.slope)/(T_ref + b_param)));

        titleTop    = `Arrhenius model fit to exponential data<br>a = ${fitted_a}    R² = ${regression.r2.toFixed(4)}`;
        titleBottom = `linear fit of the Arrhenius model    R² = ${regression.r2.toFixed(4)}`;

        drawTopPlot(Tarray, yData, topFitY, titleTop, 'k(T)/k(T_ref)', 'T (°C)', '#d62728');
        drawBottomPlot(bottomX, bottomY, fitLineX, fitLineY, titleBottom, '1/(T + b)', 'log(k(T)/k(T_ref))', '#1f77b4', false);
    }
}

// ------------------ Plotting ------------------
function drawTopPlot(T, yData, yFit, title, yLabel, xLabel, color){
    const traceData = {
        x: T,
        y: yData,
        mode: 'markers',
        marker:{color:color},
        name:'data'
    };
    const traceFit = {
        x: T,
        y: yFit,
        mode: 'lines',
        line:{color:color},
        name:'fit'
    };
    const layout={
        title:{text:title, font:{size:14}},
        xaxis:{title:xLabel},
        yaxis:{title:yLabel},
        margin:{l:60,r:20,t:60,b:50}
    };
    Plotly.react('plot-top',[traceData, traceFit], layout, {responsive:true});
}

function drawBottomPlot(xData, yData, xFit, yFit, title, xLabel, yLabel, color, addZeroLines){
    const traceData = {
        x: xData,
        y: yData,
        mode:'markers',
        marker:{color:color},
        name:'transformed data'
    };
    const traceFit = {
        x: xFit,
        y: yFit,
        mode:'lines',
        line:{color:color},
        name:'linear fit'
    };
    const shapes=[];
    if(addZeroLines){
        shapes.push(
            {type:'line',x0:0,x1:0,y0:Math.min(...yData),y1:Math.max(...yData),line:{dash:'dot',color:'#888'}},
            {type:'line',x0:Math.min(...xData),x1:Math.max(...xData),y0:0,y1:0,line:{dash:'dot',color:'#888'}}
        );
    }
    const layout={
        title:{text:title, font:{size:14}},
        xaxis:{title:xLabel},
        yaxis:{title:yLabel},
        margin:{l:60,r:20,t:60,b:50},
        shapes:shapes
    };
    Plotly.react('plot-bottom',[traceData, traceFit], layout, {responsive:true});
}

// initial setup
enforceTmaxConstraint();
updatePlots();
</script>
</body>
</html>
