};

/* ----------------------------- UTILITY FUNCTIONS ----------------------------- */
function sanitize(text){
    return text.toLowerCase().replace(/[^a-z]/g,'');
}

function letterFreq(text){
    const freq=new Array(26).fill(0);
    for(const ch of text){
        const idx=ch.charCodeAt(0)-97;
        if(idx>=0 && idx<26) freq[idx]++;
    }
    const total=text.length;
    return freq.map(c=>total?c/total*100:0);
}

function caesarEncrypt(text,shift){
    const s=shift%26;
    return text.replace(/[a-z]/g, ch=>{
        const code=(ch.charCodeAt(0)-97+s)%26+97;
        return String.fromCharCode(code);
    });
}

function vigenereEncrypt(text,keyword,autokey=false){
    if(!keyword) keyword="a";
    keyword=sanitize(keyword);
    if(!keyword) keyword="a";
    let key=keyword;
    if(autokey) key+=text;
    let result="";
    for(let i=0;i<text.length;i++){
        const p=text.charCodeAt(i)-97;
        const k=key.charCodeAt(i%key.length)-97;
        const c=(p+k)%26;
        result+=String.fromCharCode(c+97);
    }
    return result;
}

/* ----------------------------- PLOTLY CONFIG ----------------------------- */
const letters=[...'abcdefghijklmnopqrstuvwxyz'];

function plotFreq(divId,freqArr){
