    const dataPairs=letters.map((ltr,i)=>({letter:ltr,freq:freqArr[i]}))
                           .sort((a,b)=>b.freq-a.freq);
    const x=dataPairs.map(d=>d.letter);
    const y=dataPairs.map(d=>d.freq);

