            x1:25.5,
            y0:100/26,
            y1:100/26,
            line:{color:'red',width:2,dash:'dash'}
        }]
    };
    Plotly.newPlot(divId,[trace],layout,{displayModeBar:false,staticPlot:true});
}

/* ----------------------------- MAIN UPDATE FUNCTION ----------------------------- */
function updateVisualization(){
    const messageKey=document.getElementById('select-message').value;
    const cipher=document.getElementById('select-cipher').value;
    const keywordInput=document.getElementById('input-keyword');
    const sliderOffset=document.getElementById('slider-offset');
    const offsetValueSpan=document.getElementById('offset-value');

    // Enable/disable controls
