        keywordInput.disabled=true;
        keywordInput.style.opacity=.5;
    }else{
        sliderOffset.disabled=true;
        offsetValueSpan.style.opacity=.5;
        keywordInput.disabled=false;
        keywordInput.style.opacity=1;
    }

    offsetValueSpan.textContent=sliderOffset.value;

    const fullPlainText=messages[messageKey];
    const processedPlain=sanitize(fullPlainText);

    // Original snippet
    document.getElementById('original-text').textContent=fullPlainText.slice(0,80)+'...';

    // Frequency of original
    const origFreq=letterFreq(processedPlain);
    plotFreq('original-chart',origFreq);

    // Encryption
    let cipherText="";
    if(cipher==='Caesar'){
        const shift=parseInt(sliderOffset.value,10);
        cipherText=caesarEncrypt(processedPlain,shift);
    }else if(cipher==='De Vigenere'){
        cipherText=vigenereEncrypt(processedPlain,keywordInput.value,false);
    }else if(cipher==='Autokey'){
        cipherText=vigenereEncrypt(processedPlain,keywordInput.value,true);
    }

    document.getElementById('encrypted-text').textContent=cipherText;

    // Frequency of encrypted
    const encFreq=letterFreq(cipherText);
    plotFreq('encrypted-chart',encFreq);
}

/* ----------------------------- EVENT LISTENERS ----------------------------- */
document.getElementById('select-message').addEventListener('change',updateVisualization);
document.getElementById('select-cipher').addEventListener('change',updateVisualization);
document.getElementById('input-keyword').addEventListener('input',updateVisualization);
document.getElementById('slider-offset').addEventListener('input',updateVisualization);

/* ----------------------------- INITIAL RENDER ----------------------------- */
window.addEventListener('DOMContentLoaded',updateVisualization);
</script>
</body>
