<body>
<div id="app-container">

  <!-- CONTROL PANEL ---------------------------------------------------------->
  <div id="control-panel">

    <!-- λ slider -->
    <div class="control-group">
      <label for="slider-lambda">permeability ratio, λ</label>
      <input type="range" id="slider-lambda" min="-8" max="-4" step="0.01" value="-5">
      <span id="value-lambda">0.00001</span>
    </div>

    <!-- ω slider -->
    <div class="control-group">
      <label for="slider-omega">storativity ratio, ω</label>
      <input type="range" id="slider-omega" min="-3" max="-1" step="0.01" value="-2">
      <span id="value-omega">0.01</span>
    </div>

    <!-- toggle buttons -->
    <div class="control-group toggle-group">
      <label>option</label>
      <button id="btn-pressure-drop" class="active">pressure drop</button>
      <button id="btn-difference">difference</button>
    </div>
  </div>

  <!-- PLOT AREA ------------------------------------------------------------->
  <div id="plot-container"></div>

</div>

<script>
(function(){
  /* --------------------------------------------------------------------- *
   * Cached DOM elements
   * --------------------------------------------------------------------- */
  const sliderLambda = document.getElementById('slider-lambda');
  const sliderOmega  = document.getElementById('slider-omega');
  const spanLambda   = document.getElementById('value-lambda');
  const spanOmega    = document.getElementById('value-omega');
  const btnPressure  = document.getElementById('btn-pressure-drop');
  const btnDiff      = document.getElementById('btn-difference');
  const plotContainer= document.getElementById('plot-container');

  /* --------------------------------------------------------------------- *
   * Helper: formatting numbers
   * --------------------------------------------------------------------- */
  function fmt(val){
    return (val<1e-4 || val>=1e4) ? val.toExponential(2) : (+val.toPrecision(5));
  }

  /* --------------------------------------------------------------------- *
   * Generate logarithmically spaced τ array (size 200)
   * --------------------------------------------------------------------- */
  const N_TAU = 200;
  const tauArr = Array.from({length:N_TAU}, (_,i)=>{
    const exp = i*(10/(N_TAU-1));              // 10 decades from 10^0 to 10^10
    return Math.pow(10,exp);
  });

  /* --------------------------------------------------------------------- *
   * FACTORIAL & COMBINATIONS  (small n only, memoised)
   * --------------------------------------------------------------------- */
  const factMemo=[1];
  function factorial(n){
    if(factMemo[n]!=null) return factMemo[n];
    let last=factMemo.length-1, res=factMemo[last];
    for(let i=last+1;i<=n;i++){
      res*=i; factMemo[i]=res;
    }
    return res;
  }
  function choose(n,k){
    if(k<0||k>n) return 0;
    return factorial(n)/(factorial(k)*factorial(n-k));
  }

  /* --------------------------------------------------------------------- *
   * Stehfest coefficients (computed once, N even)
   * --------------------------------------------------------------------- */
  const N_STEHFEST = 12;          // typically 10 or 12
  const V = (function (){
    const coeff = new Array(N_STEHFEST+1);
    for(let k=1;k<=N_STEHFEST;k++){
      let sum=0;
      const jMin = Math.floor((k+1)/2);
      const jMax = Math.min(k, N_STEHFEST/2);
      for(let j=jMin;j<=jMax;j++){
        const term = Math.pow(j, N_STEHFEST/2) *
                     choose(2*j, j) *
                     choose(j, k-j) *
                     Math.pow(-1, j + N_STEHFEST/2);
        sum += term;
      }
      coeff[k]=sum;
    }
    return coeff;
  })();

  /* --------------------------------------------------------------------- *
   * Laplace-domain function:  P̄(s)/s
   * Uses mathjs besselk
   * --------------------------------------------------------------------- */
  function laplaceF(s, lambda, omega){
    const f_s = (omega*(1-omega)*s + lambda)/((1-omega)*s + lambda);
    const arg = Math.sqrt(s * f_s);
    const K0  = math.besselk(arg,0);
    const K1  = math.besselk(arg,1);
    // avoid division by zero
    const denom = (arg===0||K1===0)?1e-30:(arg*K1);
    const Pbar = K0/denom;
    return Pbar / s;
  }

  /* --------------------------------------------------------------------- *
   * Stehfest numerical inverse Laplace transform
   * --------------------------------------------------------------------- */
  function stehfestInvert(t, lambda, omega){
    if(t===0) return 0;
    const ln2 = Math.log(2);
    let sum=0;
    for(let k=1;k<=N_STEHFEST;k++){
      const s = k*ln2/t;
      sum += V[k]*laplaceF(s, lambda, omega);
    }
    return ln2/t * sum;
  }

  /* --------------------------------------------------------------------- *
   * Build full ΔP(τ) curve
   * --------------------------------------------------------------------- */
  function buildPressureCurve(lambda, omega){
    return tauArr.map(tau=>stehfestInvert(tau, lambda, omega));
  }

  /* --------------------------------------------------------------------- *
   * Update both sliders' numeric display
   * --------------------------------------------------------------------- */
  function updateNumericDisplays(lambda, omega){
    spanLambda.textContent = fmt(lambda);
    spanOmega.textContent  = fmt(omega);
  }

  /* --------------------------------------------------------------------- *
   * Main routine to (re)draw plot
   * --------------------------------------------------------------------- */
  let firstPlot = true;
  let cachedTrace = null;
  function updatePlot(){
    // 1. read controls
    const expLambda = parseFloat(sliderLambda.value);
    const expOmega  = parseFloat(sliderOmega.value);
    const lambda    = Math.pow(10,expLambda);
    const omega     = Math.pow(10,expOmega);
    const option    = btnPressure.classList.contains('active')?'pressure':'difference';

    // 2. display numbers
    updateNumericDisplays(lambda, omega);

    // 3. calculate pressure drop curve
    const yPressure = buildPressureCurve(lambda, omega);

    // 4. prepare y depending on option
    const yData = (option==='pressure') ? yPressure
                : yPressure.map((v,i)=> v - 0.5*(Math.log(tauArr[i]) + 0.8091));

    // 5. trace & layout
    const trace = {
      x: tauArr,
      y: yData,
      mode:'lines',
      line:{color:'red',width:2},
      hovertemplate:'τ=%{x:.3e}<br>y=%{y:.4f}<extra></extra>'
    };

    const commonLayout = {
      xaxis:{
        title:'dimensionless time, τ',
        type:'log',
        range:[0,10],
        dtick:2,
        gridcolor:'#ddd'
      },
      margin:{l:80,r:50,b:60,t:60},
      paper_bgcolor:'#fff',
      plot_bgcolor:'#fff',
      hovermode:'closest'
    };

    let layout;
    if(option==='pressure'){
      layout = {
        ...commonLayout,
        title:'pressure drawdown at the wellbore',
        yaxis:{
          title:'dimensionless pressure drop, ΔP',
          range:[0,12],
          gridcolor:'#ddd'
        }
      };
    }else{
      layout = {
        ...commonLayout,
        title:'deviation from the asymptote',
        yaxis:{
          title:'dimensionless difference, ΔP − 1/2 (ln(τ) + 0.8091)',
          range:[0,3.5],
          gridcolor:'#ddd'
        }
      };
    }

    // 6. draw / react
    if(firstPlot){
      Plotly.newPlot(plotContainer, [trace], layout, {responsive:true});
      firstPlot=false;
    }else{
      Plotly.react(plotContainer, [trace], layout);
    }
  }

  /* --------------------------------------------------------------------- *
   * Event listeners
   * --------------------------------------------------------------------- */
  sliderLambda.addEventListener('input', updatePlot);
  sliderOmega .addEventListener('input', updatePlot);

  function toggleOption(e){
    const btn = e.target;
    if(btn.classList.contains('active')) return;
    btnPressure.classList.toggle('active');
    btnDiff.classList.toggle('active');
    updatePlot(); // layout changes require full update
  }
  btnPressure.addEventListener('click', toggleOption);
  btnDiff.addEventListener('click', toggleOption);

  /* --------------------------------------------------------------------- *
   * Initialize
   * --------------------------------------------------------------------- */
  updatePlot();

})(); // IIFE
</script>
</body>
</html>
