<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Forest Growth Curve Visualization</title>

<style>
body{
  display:flex;
  font-family:sans-serif;
  margin:0;
}
#left-column{flex:2;padding:20px;box-sizing:border-box;}
#right-column{flex:1;padding:20px;box-sizing:border-box;}
#canvas-container{width:100%;}
#results-container{margin-top:10px;font-size:1.1em;}
#data-table{border-collapse:collapse;width:100%;}
#data-table th,#data-table td{border:1px solid #000;padding:8px;text-align:center;}
#data-table th{background:#f2f2f2;}
</style>
</head>

<body>
<!-- -------------- LEFT COLUMN ---------------- -->
<div id="left-column">
  <div id="canvas-container"></div>
  <div id="results-container">
    <p>rejuvenation bias <i>u</i> = <span id="results-u">22.7319</span></p>
    <p>site productivity <i>p</i> = <span id="results-p">58.2729</span></p>
  </div>
</div>

<!-- -------------- RIGHT COLUMN --------------- -->
<div id="right-column">
  <table id="data-table">
    <thead>
      <tr><th>age</th><th>volume</th></tr>
    </thead>
    <tbody id="data-table-body"></tbody>
  </table>
</div>

<!-- -------------- SCRIPTS -------------------- -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
<script>
/* -------------------- GLOBAL STATE -------------------- */
let dataPoints=[
  {age:30,volume:289},
  {age:35,volume:445},
  {age:40,volume:565},
  {age:45,volume:704},
  {age:50,volume:833},
  {age:55,volume:924}
];

let pParam=58.2729,uParam=22.7319;

/* -------------------- PLOT SETTINGS -------------------- */
const xMin=20,xMax=80,yMin=0,yMax=1500;
const margin=60, pointR=6;
const canvasW=500,canvasH=500;
let selectedPoint=null;

/* -------------------- P5 SETUP/DRAW -------------------- */
function setup(){
  const c=createCanvas(canvasW,canvasH);
  c.parent('canvas-container');
  noLoop();            // redraw only when needed
  recalculate();       // build table & initial draw
}

function draw(){
  background('#FFFFE0');
  drawGrid();
  drawAxes();
  drawLabels();
  drawCurve();
  drawPoints();
}

/* -------------------- DRAW HELPERS -------------------- */
function dataToPxX(x){return map(x,xMin,xMax,margin,width-margin);}
function dataToPxY(y){return map(y,yMin,yMax,height-margin,margin);}
function pxToDataX(px){return map(px,margin,width-margin,xMin,xMax);}
function pxToDataY(py){return map(py,height-margin,margin,yMin,yMax);}

function drawGrid(){
  stroke(200);strokeWeight(1);
  drawingContext.setLineDash([4,4]);
  for(let x=20;x<=80;x+=10){
    const px=dataToPxX(x); line(px,margin,px,height-margin);
  }
  for(let y=0;y<=1500;y+=200){
    const py=dataToPxY(y); line(margin,py,width-margin,py);
  }
  drawingContext.setLineDash([]);
}

function drawAxes(){
  stroke(0);strokeWeight(2);
  line(margin,margin,margin,height-margin);         // y-axis
  line(margin,height-margin,width-margin,height-margin); // x-axis

  noStroke();fill(0);textAlign(CENTER,TOP);
  for(let x=20;x<=80;x+=10){
    const px=dataToPxX(x);
    stroke(0);line(px,height-margin-5,px,height-margin+5);
    noStroke();text(x,px,height-margin+8);
  }
  textAlign(RIGHT,CENTER);
  for(let y=0;y<=1500;y+=200){
    const py=dataToPxY(y);
    stroke(0);line(margin-5,py,margin+5,py);
    noStroke();text(y,margin-8,py);
  }
}

function drawLabels(){
  fill(0);noStroke();
  textSize(16);textAlign(CENTER,CENTER);
  text('Forest Growth Curve',width/2,margin/2);
  textSize(12);text('Stand age, yr',width/2,height-margin/2);

  push();
  translate(margin/2,height/2);rotate(-HALF_PI);
  text('Wood volume, m³ ha⁻¹',0,0);
  pop();

  push();
  translate(width-margin/2,height/2);rotate(-HALF_PI);
  text('4/5 law',0,0);
  pop();
}

function drawPoints(){
  fill('#0000CD');stroke(0);strokeWeight(1);
  dataPoints.forEach(pt=>{
    ellipse(dataToPxX(pt.age),dataToPxY(pt.volume),pointR*2);
  });
}

function drawCurve(){
  if(pParam<=0)return;
  stroke('#0000CD');strokeWeight(2);noFill();
  beginShape();
  for(let x=uParam;x<=xMax;x+=0.5){
    const y=pParam*Math.pow(x-uParam,0.8);
    const px=dataToPxX(x),py=dataToPxY(y);
    vertex(px,py);
  }
  endShape();
}

/* -------------------- INTERACTION -------------------- */
function mousePressed(){
  if(!insidePlot(mouseX,mouseY))return;

  // try to pick existing point
  for(const pt of dataPoints){
    if(dist(mouseX,mouseY,dataToPxX(pt.age),dataToPxY(pt.volume))<=pointR+2){
      selectedPoint=pt;return;
    }
  }

  // otherwise add new
  dataPoints.push({
    age: clamp(pxToDataX(mouseX),xMin,xMax),
    volume: clamp(pxToDataY(mouseY),yMin,yMax)
  });
  recalculate();
}

function mouseDragged(){
  if(selectedPoint){
    selectedPoint.age   =clamp(pxToDataX(mouseX),xMin,xMax);
    selectedPoint.volume=clamp(pxToDataY(mouseY),yMin,yMax);
    recalculate(false); // quick redraw with current params
  }
}

function mouseReleased(){
  if(selectedPoint){selectedPoint=null;recalculate();}
}

function doubleClicked(){
  if(!insidePlot(mouseX,mouseY))return;
  for(let i=dataPoints.length-1;i>=0;i--){
    const pt=dataPoints[i];
    if(dist(mouseX,mouseY,dataToPxX(pt.age),dataToPxY(pt.volume))<=pointR+2){
      dataPoints.splice(i,1);recalculate();return;
    }
  }
}

function insidePlot(px,py){
  return px>=margin&&px<=width-margin&&py>=margin&&py<=height-margin;
}

function clamp(v,min,max){return Math.max(min,Math.min(max,v));}

/* -------------------- MATH / UI UPDATE -------------------- */
function recalculate(refit=true){
  if(dataPoints.length<2){pParam=0;uParam=0;}
  else if(refit){fitModel();}

  // update numbers
  document.getElementById('results-u').textContent=uParam.toFixed(4);
  document.getElementById('results-p').textContent=pParam.toFixed(4);

  // update table
  const tb=document.getElementById('data-table-body');
  tb.innerHTML='';
  dataPoints.forEach(pt=>{
    const tr=document.createElement('tr');
    tr.innerHTML=`<td>${pt.age.toFixed(2)}</td><td>${pt.volume.toFixed(2)}</td>`;
    tb.appendChild(tr);
  });

  redraw();
}

/* ------ Simple gradient-descent fit for  volume = p*(age-u)^(4/5) ------ */
function fitModel(){
  let p=50,u=1,lr=1e-6;
  const minAge=Math.min(...dataPoints.map(d=>d.age));

  for(let iter=0;iter<1000;iter++){
    let dEp=0,dEu=0,E=0;

    dataPoints.forEach(pt=>{
      const diffAge=pt.age-u;
      if(diffAge<=0)return;           // invalid, skip
      const pred=p*Math.pow(diffAge,0.8);
      const err=pred-pt.volume;
      E+=err*err;

      dEp+=2*err*Math.pow(diffAge,0.8);
      dEu+=2*err*p*0.8*Math.pow(diffAge,-0.2)*(-1);
    });

    p-=lr*dEp;
    u-=lr*dEu;

    if(u>=minAge)u=minAge-0.0001;     // keep u below min age
    if(p<0)p=0.001;

    if(E<1e-6)break;
  }
  pParam=p;uParam=u;
}
</script>
</body>
</html>
