        padding:10px;
        box-sizing:border-box;
    }
    canvas{
        border:1px solid #999999;
    }
</style>
</head>

<body>
<div id="demo-container">
    <!-- CONTROL PANEL -->
    <div id="controls">
        <label for="angle-slider">vertices sorted by angle&nbsp;</label>
        <input type="range" id="angle-slider" min="0" max="38" step="1" value="3">
        <span id="angle-value">3</span>
        <button id="reset-button">+</button>
    </div>

    <!-- CANVAS -->
    <div id="canvas-container"></div>
</div>

<script>
/* ----------------------- GLOBAL STATE ----------------------- */
const outerPoly = [
    [50, 50], [550, 50], [550, 550], [50, 550]
];
const holes = [
    [[100, 100], [250, 100], [250, 150], [200, 150], [200, 200], [100, 200]],
    [[300, 100], [450, 100], [450, 250], [400, 250], [400, 200], [300, 200]],
    [[100, 350], [250, 350], [250, 500], [200, 500], [200, 450], [100, 450]],
    [[300, 400], [450, 400], [450, 500], [300, 500]],
    [[300, 300], [350, 300], [350, 350], [300, 350]]
];
const ALL_VERTICES = [...outerPoly, ...holes.flat()];
const TOTAL_VERTICES = ALL_VERTICES.length; // 38

// Slider & UI elements
const angleSlider = document.getElementById("angle-slider");
const angleValue  = document.getElementById("angle-value");
const resetButton = document.getElementById("reset-button");

/* ----------------------- p5.js SKETCH ----------------------- */
let canvas;
let sourcePt = {x:275, y:275};
let dragging = false;

/* Edge list – each element: {a:{x,y}, b:{x,y}} */
const edges = [];
function buildEdges(){
    function pushEdges(arr){
        for(let i=0;i<arr.length;i++){
            const curr = arr[i];
            const next = arr[(i+1)%arr.length];
            edges.push({a:{x:curr[0],y:curr[1]}, b:{x:next[0],y:next[1]}});
        }
    }
    pushEdges(outerPoly);
    holes.forEach(h=>pushEdges(h));
}
buildEdges();

/* Utility – Point in polygon */
function pointInPoly(pt, poly){
    let inside=false;
    for(let i=0,j=poly.length-1;i<poly.length;j=i++){
        const xi=poly[i][0], yi=poly[i][1];
        const xj=poly[j][0], yj=poly[j][1];
        const intersect=((yi>pt.y)!=(yj>pt.y)) && 
            (pt.x < (xj - xi)*(pt.y - yi)/(yj - yi) + xi);
        if(intersect) inside = !inside;
    }
    return inside;
}
function isValidSource(pt){
    if(!pointInPoly(pt, outerPoly)) return false;
    for(const hole of holes){
        if(pointInPoly(pt, hole)) return false;
    }
    return true;
}

/* Intersection of ray (S + t*D, t>=0) and segment AB
   returns {t,u,x,y} where u in [0,1] on segment, t>=0 on ray, or null */
function intersectRaySeg(S,D,A,B){
    const EPS=1e-9;
    const vx = B.x - A.x;
    const vy = B.y - A.y;
    const dx = D.x;
    const dy = D.y;
    const denom = dx*vy - dy*vx;
    if(Math.abs(denom) < EPS) return null; // Parallel
    const ax = A.x - S.x;
    const ay = A.y - S.y;
    const t = (ax*vy - ay*vx)/denom;
    const u = (ax*dy - ay*dx)/denom;
    if(t>=0 && u>=0 && u<=1){
        return {t,u,x:S.x + t*dx, y:S.y + t*dy};
    }
    return null;
}

/* Compute visibility polygon & auxiliary data */
function computeVisibility(numSteps){
    const S = sourcePt;
    /* Prepare vertices with angle */
    const vertsWithAng = ALL_VERTICES.map( (v,idx)=> {
        const ang = Math.atan2(v[1]-S.y, v[0]-S.x);
        return {x:v[0], y:v[1], angle:ang, idx};
    }).sort( (p,q)=> p.angle - q.angle );

    /* If angle range spans -π to π, ensure monotonic increasing by adjusting */
    // Add 2π to negative angles to make them [0,2π)
    vertsWithAng.forEach(v=>{ if(v.angle<0) v.angle += Math.PI*2; });
    vertsWithAng.sort((a,b)=>a.angle - b.angle);

    const visPts = [];
    const intersectedEdges = [];
    const processed = Math.min(numSteps, TOTAL_VERTICES);

    for(let i=0;i<processed;i++){
        const v = vertsWithAng[i];
        const D = {x: v.x - S.x, y: v.y - S.y};
        let nearest = null;
        let nearestEdgeIndex = -1;
        for(let e=0;e<edges.length;e++){
            const inter = intersectRaySeg(S,D,edges[e].a, edges[e].b);
            if(inter){
                if(nearest===null || inter.t < nearest.t){
                    nearest = inter;
                    nearestEdgeIndex = e;
                }
            }
        }
        if(nearest){
            visPts.push({x:nearest.x, y:nearest.y});
            intersectedEdges.push(nearestEdgeIndex);
        }
    }
    return {
        vertsWithAng,
        visPts,
        intersectedEdges,
        processed
    };
}

/* ---------------- p5.js Core ---------------- */
function setup(){
    canvas = createCanvas(600,600);
    canvas.parent("canvas-container");
}

function draw(){
    background(255);
    // UI read
    const sliderVal = +angleSlider.value;
    angleValue.textContent = sliderVal;

    // Compute visibility data
    const data = computeVisibility(sliderVal);
    const vis = data.visPts;

    /* ---------- Draw static polygons ---------- */
    noStroke();
    fill(255);
    beginShape();
    outerPoly.forEach(v=>vertex(v[0],v[1]));
    holes.forEach(h=>{
        beginContour();
        h.forEach(v=>vertex(v[0], v[1]));
        endContour();
    });
    endShape(CLOSE);

    /* Polygon outline */
    stroke(170); strokeWeight(1); noFill();
    beginShape();
    outerPoly.forEach(v=>vertex(v[0],v[1]));
    endShape(CLOSE);
    holes.forEach(h=>{
        beginShape();
        h.forEach(v=>vertex(v[0],v[1]));
        endShape(CLOSE);
    });

    /* Vertices dots */
    noStroke();
    fill('#ff0000');
    ALL_VERTICES.forEach(v=>{
        ellipse(v[0],v[1],4,4);
    });

    /* ---------- Visibility Polygon ---------- */
    if(sliderVal>0 && vis.length){
        if(sliderVal < TOTAL_VERTICES){
            // committed polygon up to N-1
            if(vis.length>1){
                stroke('rgba(170,170,170,0.5)'); strokeWeight(1); fill('#FFFF99');
                beginShape();
                vis.slice(0,vis.length-1).forEach(p=>vertex(p.x,p.y));
                endShape(CLOSE);
            }
            // active triangle
            if(vis.length>=1){
                const curr = vis[vis.length-1];
                const prev = vis.length>1 ? vis[vis.length-2] : curr;
                stroke('rgba(170,170,170,0.5)'); strokeWeight(1); fill('#FFCC66');
                beginShape();
                vertex(sourcePt.x, sourcePt.y);
                vertex(prev.x, prev.y);
                vertex(curr.x, curr.y);
                endShape(CLOSE);

                // Ray
                stroke('#0000FF'); strokeWeight(1);
                line(sourcePt.x, sourcePt.y, curr.x, curr.y);

                // Sweep Arc
                const startAng = data.vertsWithAng[0].angle;
                const endAng   = data.vertsWithAng[vis.length-1].angle;
                noFill(); stroke('#0000FF'); strokeWeight(1.5);
                const radius = 40;
                push();
                translate(sourcePt.x, sourcePt.y);
                const a1 = startAng;
                const a2 = endAng;
                // ensure arc is drawn in correct direction
                if(a2 > a1){
                    arc(0,0,radius*2,radius*2,a1,a2);
                }else{
                    arc(0,0,radius*2,radius*2,a1,a2 + TWO_PI);
                }
                pop();

                // Highlight intersected edge
                const edgeIdx = data.intersectedEdges[vis.length-1];
                if(edgeIdx>=0){
                    stroke('#00CC00'); strokeWeight(3);
                    const e = edges[edgeIdx];
                    line(e.a.x, e.a.y, e.b.x, e.b.y);
                }
            }
        }else{
            // Completed polygon
            stroke('rgba(170,170,170,0.5)'); strokeWeight(1); fill('#FFCC66');
            beginShape();
            vis.forEach(p=>vertex(p.x,p.y));
            endShape(CLOSE);
        }
    }

    /* ---------- Source Point ---------- */
    noStroke();
    fill('#0000FF');
    ellipse(sourcePt.x, sourcePt.y, 8,8);
    stroke('#0000FF'); noFill();
    ellipse(sourcePt.x, sourcePt.y, 12,12);
}

/* ---------------- Interaction ---------------- */
function mousePressed(){
    const d = dist(mouseX, mouseY, sourcePt.x, sourcePt.y);
    if(d < 10){
        dragging = true;
    }
}
function mouseDragged(){
    if(dragging){
        const newPt = {x:mouseX, y:mouseY};
        if(isValidSource(newPt)){
            sourcePt.x = newPt.x;
            sourcePt.y = newPt.y;
        }
    }
}
function mouseReleased(){
    dragging = false;
}

/* ---------------- UI Events ---------------- */
angleSlider.addEventListener('input', ()=>{
   // angleValue updated each frame in draw()
});

resetButton.addEventListener('click', ()=>{
