<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Power-Series vs RK4 Solver</title>

<!-- Plotly CDN -->
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

<style>
    html,body{
        height:100%;
        margin:0;
        font-family:Arial,Helvetica,sans-serif;
    }
    #main-container{
        display:flex;
        height:100%;
    }
    /* ---- Control panel ---- */
    #control-panel{
        width:260px;
        padding:20px;
        box-sizing:border-box;
        background:#f4f4f4;
        border-right:1px solid #ccc;
    }
    #control-panel label{
        display:block;
        font-weight:bold;
        margin-bottom:4px;
    }
    #control-panel input[type="range"]{
        width:100%;
    }
    #control-panel .slider-block{
        margin-bottom:25px;
    }
    #control-panel span{
        float:right;
        font-weight:bold;
    }
    /* ---- Visualization ---- */
    #visualization-area{
        flex:1;
        display:flex;
        flex-direction:column;
    }
    #plot-container{
        flex:1;
    }
</style>
</head>
<body>

<div id="main-container">

    <!-- -------- Control Panel -------- -->
    <div id="control-panel">
        <!-- Expansion order -->
        <div class="slider-block">
            <label for="slider-order">expansion order <span id="value-order">5</span></label>
            <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
        </div>

        <!-- a = f(0) -->
        <div class="slider-block">
            <label for="slider-a">f(0) <span id="value-a">2.0</span></label>
            <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
        </div>

        <!-- b = f'(0) -->
        <div class="slider-block">
            <label for="slider-b">f'(0) <span id="value-b">0.0</span></label>
            <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
        </div>
    </div>

    <!-- -------- Visualization Area -------- -->
    <div id="visualization-area">
        <div id="plot-container"></div>
    </div>

</div>

<script>
(() => {
    /* ---------- Utility ---------- */
    const factorial = (n) => {
        let f = 1;
        for(let i=2;i<=n;i++) f*=i;
        return f;
    };

    /* ---------- Core Update Function ---------- */
    function updatePlot(){
        /* 1. Read inputs */
        const order = +document.getElementById('slider-order').value;
        const a     = +document.getElementById('slider-a').value;
        const b     = +document.getElementById('slider-b').value;

        /* 2. Update value displays */
        document.getElementById('value-order').textContent = order;
        document.getElementById('value-a').textContent     = a.toFixed(1);
        document.getElementById('value-b').textContent     = b.toFixed(1);

        /* 3. Calculate derivatives f^(k)(0) up to 5 */
        const fD = [];
        fD[0] = a;
        fD[1] = b;
        // Guard against division by zero
        const tiny = 1e-12;
        const denom = Math.abs(a) < tiny ? tiny : a;

        // f''(0)
        fD[2] = -(fD[1] + 1.5 * fD[0]) / denom;

        // f'''(0)
        fD[3] = -(fD[2]*fD[1] + fD[2] + 1.5*fD[1]) / denom;

        // f''''(0)
        fD[4] = -(2*fD[3]*fD[1] + Math.pow(fD[2],2) + fD[3] + 1.5*fD[2]) / denom;

        // f'''''(0)
        fD[5] = -(3*fD[4]*fD[1] + 4*fD[3]*fD[2] + fD[4] + 1.5*fD[3]) / denom;

        /* 3b. Polynomial coefficients c_k */
        const coeffs = fD.map((d,k)=>d/factorial(k));

        /* 4. Generate power series data */
        const xVals = [];
        const yApprox = [];
        const Npts = 200;
        const xmax = 1.5;
        for(let i=0;i<=Npts;i++){
            const x = i/Npts * xmax;
            let sum = 0;
            for(let k=0;k<=order;k++){
                sum += coeffs[k]*Math.pow(x,k);
            }
            xVals.push(x);
            yApprox.push(sum);
        }

        /* 5. Numerical solution via RK4 */
        const h = xmax/Npts;
        let y1 = a, y2 = b;  // y1=f, y2=f'
        const yNum = [y1];
        for(let i=1;i<=Npts;i++){
            const x = (i-1)*h;

            const derivs = (x,y1,y2)=>{
                return {
                    dy1: y2,
                    dy2: -(y2 + 1.5*y1)/y1
                };
            };

            const k1 = derivs(x,y1,y2);

            const k2In = derivs(x + h/2,
                                y1 + h*k1.dy1/2,
                                y2 + h*k1.dy2/2);

            const k3In = derivs(x + h/2,
                                y1 + h*k2In.dy1/2,
                                y2 + h*k2In.dy2/2);

            const k4In = derivs(x + h,
                                y1 + h*k3In.dy1,
                                y2 + h*k3In.dy2);

            y1 += h/6*(k1.dy1 + 2*k2In.dy1 + 2*k3In.dy1 + k4In.dy1);
            y2 += h/6*(k1.dy2 + 2*k2In.dy2 + 2*k3In.dy2 + k4In.dy2);

            yNum.push(y1);
        }

        /* 6. Agreement region */
        let xLimit = xmax;
        for(let i=0;i<=Npts;i++){
            if(Math.abs(yApprox[i]-yNum[i])>=0.01){
                xLimit = xVals[i];
                break;
            }
        }

        /* 7. Create polynomial string */
        const polyTerms = [];
        for(let k=0;k<=order;k++){
            const c = coeffs[k];
            if(Math.abs(c) < 1e-10) continue; // skip zeros
            let term = (Math.abs(c) < 1e-5 ? c.toExponential(2) : c.toFixed(5)).replace(/\.?0+$/,"");
            // sign handling will be added later
            if(k===0){
                term = term;
            }else if(k===1){
                term += " x";
            }else{
                term += ` x^${k}`;
            }
            polyTerms.push({k,term,coeff:c});
        }

        // Assemble with signs
        let titleStr = "f(x) ≈ ";
        if(polyTerms.length===0){
            titleStr += "0";
        }else{
            polyTerms.forEach((obj,idx)=>{
                const {coeff:c,term} = obj;
                const sign = c>=0 ? (idx===0?"":" + ") : " - ";
                titleStr += sign + term.replace(/^-/, ""); // drop leading - inside term
            });
        }

        /* 8. Plotly react update */
        const traceRed = {
            x: xVals,
            y: yApprox,
            type: 'scatter',
            mode: 'lines',
            name: 'Power series',
            line: {color:'red', width:2}
        };
        const traceGreen = {
            x: xVals,
            y: yNum,
            type: 'scatter',
            mode: 'lines',
            name: 'RK4 numerical',
            line: {color:'green', width:3}
        };
        const layout = {
            title: {text: titleStr},
            xaxis: {title:'x', range:[0,xmax], zeroline:false},
            yaxis: {title:'f(x)', range:[0,4], zeroline:false},
            margin:{l:50,r:20,t:60,b:40},
            shapes:[{
                type:'rect',
                xref:'x',
                yref:'paper',
                x0:0, y0:0,
                x1:xLimit, y1:1,
                fillcolor:'lightblue',
                opacity:0.4,
                layer:'below',
                line:{width:0}
            }]
        };

        Plotly.react('plot-container',[traceRed,traceGreen],layout,{responsive:true});
    }

    /* ---------- Event bindings ---------- */
    window.addEventListener('DOMContentLoaded',()=>{
        // Initial empty plot (avoids flashing)
        Plotly.newPlot('plot-container',[],{});
        // Bind sliders
        ['slider-order','slider-a','slider-b'].forEach(id=>{
            document.getElementById(id).addEventListener('input',updatePlot);
        });
        // Draw first plot
        updatePlot();
    });
})();
</script>
</body>
</html>
