</head>
<body>
<div id="main-container">

    <!-- CONTROL PANEL -->
    <div id="controls">
        <!-- text selection -->
        <div id="text-control">
            <label for="select-text">text</label>
            <select id="select-text">
                <option>To Be Or Not To Be</option>
                <option>The quick brown fox jumps over the lazy dog</option>
                <option>It was the best of times, it was the worst of times</option>
            </select>
        </div>

        <!-- view buttons -->
        <div id="view-controls">
            <label>plot</label>
            <button id="btn-plot-2d" class="active">distribution 2D</button>
            <button id="btn-plot-3d">keyboard 3D</button>

            <label style="margin-left:20px">keyboard</label>
            <button id="btn-keyboard-qwerty" class="active">QWERTY</button>
            <button id="btn-keyboard-dvorak">Dvorak</button>
        </div>
    </div>

    <!-- VISUALIZATION -->
    <div id="canvas-container">
        <canvas id="visualization-canvas"></canvas>
    </div>
</div>

<!-- ===== THREE.JS ===== -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>

<script>
/* -------------------------------------------------------------
   GLOBALS & STATE
------------------------------------------------------------- */
const canvas          = document.getElementById('visualization-canvas');
const renderer        = new THREE.WebGLRenderer({canvas, antialias:true});
const scene           = new THREE.Scene();
const ambientLight    = new THREE.AmbientLight(0xffffff, 0.8);
const dirLight        = new THREE.DirectionalLight(0xffffff, .6);
dirLight.position.set(10,20,10);

scene.background = new THREE.Color(0xffffff);
scene.add(ambientLight);
scene.add(dirLight);

let camera, animId;
let currentPlot      = '2d';      // '2d' or '3d'
let currentLayout    = 'qwerty';  // 'qwerty' or 'dvorak'
let currentText      = document.getElementById('select-text').value;

/* -------------------------------------------------------------
   EVENT LISTENERS
------------------------------------------------------------- */
// dropdown
document.getElementById('select-text').addEventListener('change',(e)=>{
    currentText = e.target.value;
    rebuildScene();
});

// plot buttons
document.getElementById('btn-plot-2d').addEventListener('click',()=>{
    if(currentPlot !== '2d'){
        setActive('btn-plot-2d',['btn-plot-3d']);
        currentPlot='2d';
        rebuildScene();
    }
});
document.getElementById('btn-plot-3d').addEventListener('click',()=>{
    if(currentPlot !== '3d'){
        setActive('btn-plot-3d',['btn-plot-2d']);
        currentPlot='3d';
        rebuildScene();
    }
});

// keyboard layout
document.getElementById('btn-keyboard-qwerty').addEventListener('click',()=>{
    if(currentLayout!=='qwerty'){
        setActive('btn-keyboard-qwerty',['btn-keyboard-dvorak']);
        currentLayout='qwerty';
        if(currentPlot==='3d') rebuildScene();
    }
});
document.getElementById('btn-keyboard-dvorak').addEventListener('click',()=>{
    if(currentLayout!=='dvorak'){
        setActive('btn-keyboard-dvorak',['btn-keyboard-qwerty']);
        currentLayout='dvorak';
        if(currentPlot==='3d') rebuildScene();
    }
});

// window resize
window.addEventListener('resize', onWindowResize);

/* -------------------------------------------------------------
   INITIALIZE
------------------------------------------------------------- */
onWindowResize();
rebuildScene();

/* -------------------------------------------------------------
   FUNCTIONS
------------------------------------------------------------- */
// Activate button helper
function setActive(onId, offIds){
    document.getElementById(onId).classList.add('active');
    offIds.forEach(id=>document.getElementById(id).classList.remove('active'));
}

// Resize renderer/camera
function onWindowResize(){
    const width  = canvas.clientWidth;
    const height = canvas.clientHeight;
    renderer.setSize(width, height, false);

    if(camera){
        if(camera.isPerspectiveCamera){
            camera.aspect = width/height;
        }else if(camera.isOrthographicCamera){
            const frustum = 15;
            camera.left   = -frustum * (width/height);
            camera.right  =  frustum * (width/height);
            camera.top    =  frustum;
            camera.bottom = -frustum;
        }
        camera.updateProjectionMatrix();
    }
}

// Main rebuild
function rebuildScene(){
    cancelAnimationFrame(animId);
    // clear scene children except lights
    for(let i=scene.children.length-1;i>=0;i--){
        const obj = scene.children[i];
        if(obj !== ambientLight && obj !== dirLight){
            scene.remove(obj);
        }
    }

    const freqMap = calculateFrequencies(currentText);
    if(currentPlot==='2d'){
        build2D(freqMap);
    }else{
        build3D(freqMap, currentLayout);
    }
    animate();
}

/* -------------------------------------------------------------
   FREQUENCY CALCULATION
------------------------------------------------------------- */
function calculateFrequencies(text){
    const map = {};
    const clean = text.toUpperCase().replace(/[^A-Z]/g,'');
    for(let i=0;i<26;i++){
        map[String.fromCharCode(65+i)] = 0;
    }
    for(let char of clean){
        map[char]++;
    }
    return map; // {A:2, B:1,...}
}

/* -------------------------------------------------------------
   BUILD 2D BAR CHART
------------------------------------------------------------- */
function build2D(freqMap){
    // camera
    const width  = canvas.clientWidth;
    const height = canvas.clientHeight;
    const frustum = 15;
    camera = new THREE.OrthographicCamera(
        -frustum * (width/height),
         frustum * (width/height),
         frustum,
        -frustum,
        0.1, 1000
    );
    camera.position.set(0,10,20);
    camera.lookAt(0,0,0);

    // axes grid (y)
    const maxFreq = Math.max(...Object.values(freqMap));
    const yMax = Math.max(5, Math.ceil(maxFreq/5)*5); // nearest 5
    const step = yMax/5;

    const axisGeom = new THREE.BufferGeometry();
    const points = [];
    // y-axis line
    points.push( new THREE.Vector3(-14,0,0), new THREE.Vector3(-14,yMax,0) );
    // horizontal lines
    for(let y=0;y<=yMax;y+=step){
        points.push( new THREE.Vector3(-14,y,0), new THREE.Vector3(14,y,0) );
        // label
        const sprite = makeTextSprite(String(y), '#000', 60);
        sprite.position.set(-14.8,y,0);
        sprite.scale.set(1,1,1);
        scene.add(sprite);
    }
    axisGeom.setFromPoints(points);
    const axis = new THREE.LineSegments(axisGeom, new THREE.LineBasicMaterial({color:0x888888}));
    scene.add(axis);

    // bars
    const barWidth=1;
    const gap=0.3;
    const letters = Object.keys(freqMap);
    const startX = - (letters.length*(barWidth+gap))/2 + (barWidth/2);
    const maxHeight = yMax;
    letters.forEach((letter,idx)=>{
        const count = freqMap[letter];
        const h = count; // 1 unit per count
        const color = colormap(count/maxHeight);
        const geom = new THREE.BoxGeometry(barWidth,h||0.05,1);
        const mat  = new THREE.MeshStandardMaterial({color:color});
        const bar  = new THREE.Mesh(geom,mat);
        bar.position.set(startX + idx*(barWidth+gap), h/2, 0);
        scene.add(bar);

        // letter label sprite
        const letterSprite = makeTextSprite(letter,'#000',80);
        letterSprite.position.set(bar.position.x, -0.7, 0);
        letterSprite.scale.set(1,1,1);
        scene.add(letterSprite);
    });
}

/* -------------------------------------------------------------
   BUILD 3D KEYBOARD
------------------------------------------------------------- */
function build3D(freqMap, layout){
    // camera
    camera = new THREE.PerspectiveCamera(45, canvas.clientWidth/canvas.clientHeight, 0.1, 1000);
    camera.position.set(0,25,35);
    camera.lookAt(0,0,0);

    // keyboard layouts coordinates
    const layoutData = {
        qwerty: [
            {row:0, keys:"QWERTYUIOP"},
            {row:1, keys:"ASDFGHJKL"},
            {row:2, keys:"ZXCVBNM"}
        ],
        dvorak: [
            {row:0, keys:"PYFGCRL"},
            {row:1, keys:"AOEUIDHTNS"},
            {row:2, keys:"QJKXBMWVZ"}
        ]
    };
    // row colors
    const rowColors = [0x9acd32, 0x20b2aa, 0x9370db]; // top, home, bottom

    const keyRadius = 1;
    const keyHeightBase = 0.4;
    const rowSpacing = 3;
    const keySpacing = 2.2;

    // build cylinders
    layoutData[layout].forEach((rowObj,rowIdx)=>{
        const str = rowObj.keys;
        const rowLen = str.length;
        const startX = -((rowLen-1)*keySpacing)/2;
        for(let i=0;i<rowLen;i++){
            const letter = str[i];
            const freq   = freqMap[letter] || 0;
            const h      = keyHeightBase + freq*0.6; // scale
            const geom   = new THREE.CylinderGeometry(keyRadius, keyRadius, h, 32, 1, false);
            const mat    = new THREE.MeshStandardMaterial({color:rowColors[rowIdx]});
            const cyl    = new THREE.Mesh(geom, mat);
            const xPos   = startX + i*keySpacing;
            const zPos   = rowIdx * rowSpacing;
            cyl.position.set(xPos, h/2, zPos);
            scene.add(cyl);

            // letter label
            const sprite = makeTextSprite(letter,'#000',100);
            sprite.position.set(xPos, h + 0.3, zPos);
            sprite.scale.set(1.2,1.2,1.2);
            scene.add(sprite);
        }
    });

    // base grid
    const gridHelper = new THREE.GridHelper(30,30,0xcccccc,0xeeeeee);
    gridHelper.rotation.x = Math.PI/2;
    scene.add(gridHelper);
}

/* -------------------------------------------------------------
   ANIMATION LOOP
------------------------------------------------------------- */
function animate(){
    animId = requestAnimationFrame(animate);
    renderer.render(scene,camera);
}

/* -------------------------------------------------------------
   HELPERS
------------------------------------------------------------- */
// Colormap - blue -> purple -> orange -> red
function colormap(t){ // t in [0,1]
    const c = new THREE.Color();
    if(t<0.33){
        // blue to purple
        const k = t/0.33;
        c.setRGB(0   +k*0.5,   0   , 1-k*0.3);
    }else if(t<0.66){
        // purple to orange
        const k = (t-0.33)/0.33;
        c.setRGB(0.5 +k*0.5,   0   +k*0.5, 0.7-k*0.7);
    }else{
        // orange to red
        const k = (t-0.66)/0.34;
        c.setRGB(1   , 0.5 -k*0.5, 0);
    }
    return c;
}

// Create text sprite using canvas
function makeTextSprite(message,color,sizePx){
    const canvas2d = document.createElement('canvas');
    const context  = canvas2d.getContext('2d');
    context.font = `bold ${sizePx}px Arial`;
    const metrics = context.measureText(message);
    const textW   = metrics.width;
    const textH   = sizePx;

    canvas2d.width  = textW;
    canvas2d.height = textH;

    context.font = `bold ${sizePx}px Arial`;
    context.fillStyle = color;
    context.textBaseline = 'top';
    context.fillText(message,0,0);

    const texture = new THREE.CanvasTexture(canvas2d);
    texture.needsUpdate = true;
    const material = new THREE.SpriteMaterial({map:texture, transparent:true});
    const sprite   = new THREE.Sprite(material);
    const scaleFactor = 0.02; // world units per pixel
    sprite.scale.set(canvas2d.width*scaleFactor, canvas2d.height*scaleFactor,1);
    return sprite;
}
</script>
</body>
</html>
