<script>
/********************************************************************
 *                       GLOBAL STATE
 ********************************************************************/
let isPlaying = false;
let particles = [];
const margin = 40;
let maxTime = 5;

/********************************************************************
 *                       DOM ELEMENTS
 ********************************************************************/
const elTime     = document.getElementById('slider-time');
const elTrail    = document.getElementById('slider-trail');
const elPoints   = document.getElementById('slider-points');
const elPAmp     = document.getElementById('slider-p-amp');
const elPWL      = document.getElementById('slider-p-wl');
const elSAmp     = document.getElementById('slider-s-amp');
const elSWL      = document.getElementById('slider-s-wl');

const lblTime   = document.getElementById('label-time');
const lblTrail  = document.getElementById('label-trail');
const lblPoints = document.getElementById('label-points');
const lblPAmp   = document.getElementById('label-p-amp');
const lblPWL    = document.getElementById('label-p-wl');
const lblSAmp   = document.getElementById('label-s-amp');
const lblSWL    = document.getElementById('label-s-wl');

const btnPlayPause = document.getElementById('btn-play-pause');
const btnReset     = document.getElementById('btn-reset');

/********************************************************************
 *                       p5.js SKETCH
 ********************************************************************/
new p5((p)=>{

    let canvas;

    p.setup = function(){
        // Remove placeholder canvas to avoid duplicates
        const placeholder = document.getElementById('p5-canvas');
        if(placeholder) placeholder.remove();

        canvas = p.createCanvas(800, 400);
        canvas.parent('canvas-container');
        canvas.id('p5-canvas');

        p.frameRate(60);
        initParticles(parseInt(elPoints.value));

        attachEvents();
        updateLabelsAll();
    };

    p.draw = function(){
        p.background(255);

        // Axis
        drawAxes();

        // Update time while playing
        if(isPlaying){
            let t = parseFloat(elTime.value);
            t += 0.02;          // time step
            if(t > maxTime) t = 0;
            elTime.value = t.toFixed(2);
            lblTime.textContent = t.toFixed(2);
        }

        // Retrieve parameters
        const time   = parseFloat(elTime.value);
        const tLen   = parseInt(elTrail.value);
        const pAmp   = parseFloat(elPAmp.value);
        const pWL    = parseFloat(elPWL.value);
        const sAmp   = parseFloat(elSAmp.value);
        const sWL    = parseFloat(elSWL.value);

        // Draw particles
        particles.forEach(prt=>{
            // Wave displacements
            const dx = pAmp * Math.sin(2*Math.PI*(prt.x0 / pWL - time));
            const dy = sAmp * Math.sin(2*Math.PI*(prt.x0 / sWL - time));

            const xFinal = prt.x0 + dx;
            const yFinal = dy;

            // Save history
            prt.history.push({x:xFinal, y:yFinal});
            while(prt.history.length > tLen) prt.history.shift();

            // Draw trail
            if(tLen>0){
                p.noStroke();
                p.fill(106,90,205,80); // semi-transparent slateblue
                prt.history.forEach(pt=>{
                    const px = mapX(pt.x);
                    const py = mapY(pt.y);
                    p.circle(px, py, 4);
                });
            }

            // Vertical guide line
            p.stroke(176,196,222); // lightsteelblue
            p.strokeWeight(1);
            const vx = mapX(xFinal);
            p.line(vx, margin, vx, p.height-margin);

            // Particle
            p.noStroke();
            p.fill('#6A5ACD');
            p.circle(vx, mapY(yFinal), 8);
        });
    };

    /********************** Helper Functions **************************/
    function mapX(x){ return p.map(x, 0, 5, margin, p.width - margin); }
    function mapY(y){ return p.map(y, -0.5, 0.5, p.height - margin, margin); }

    function drawAxes(){
        p.stroke(0);
        p.strokeWeight(1);
        // X axis
        p.line(margin, p.height/2, p.width-margin, p.height/2);
        // Y axis
        p.line(margin, margin, margin, p.height-margin);

        p.fill(0);
        p.noStroke();
        p.textSize(12);

        // X ticks
        for(let i=0;i<=5;i++){
            const x = mapX(i);
            p.line(x, p.height/2-5, x, p.height/2+5);
            p.text(i, x-3, p.height/2+18);
        }
        // Y ticks
        for(let y=-0.4;y<=0.4;y+=0.2){
            const py = mapY(y);
            p.line(margin-5, py, margin+5, py);
            p.text(y.toFixed(1), margin-30, py+4);
        }
    }

    function initParticles(n){
        particles.length = 0;
        const step = 5/(n-1);
        for(let i=0;i<n;i++){
            const x0 = i*step;
            particles.push({x0:x0, history:[]});
        }
    }

    /********************* Event Handling *****************************/
    function attachEvents(){
        // Sliders
        [
            [elTime,  lblTime,   ( )=>{}],
            [elTrail, lblTrail,  ( )=>{}],
            [elPoints,lblPoints, ()=>{ initParticles(parseInt(elPoints.value)); }],
            [elPAmp,  lblPAmp,   ()=>{}],
            [elPWL,   lblPWL,    ()=>{}],
            [elSAmp,  lblSAmp,   ()=>{}],
            [elSWL,   lblSWL,    ()=>{}]
        ].forEach(arr=>{
            const [slider,label,extra] = arr;
            slider.addEventListener('input', ()=>{
                label.textContent = slider.value;
                if(slider!==elTime) { /* other sliders trigger immediate redraw */ }
                if(slider===elTime && !isPlaying){
                    // manual adjustment while paused already handled by value read
                }
                extra();
            });
        });

        // Play / Pause button
        btnPlayPause.addEventListener('click',()=>{
            isPlaying = !isPlaying;
            btnPlayPause.textContent = isPlaying ? 'Pause' : 'Play';
        });

        // Reset button
        btnReset.addEventListener('click', ()=>{
            resetAll();
        });
    }

    function resetAll(){
        isPlaying = false;
        btnPlayPause.textContent = 'Play';

        // Reset sliders to defaults
        elTime.value   = 0;   lblTime.textContent   = 0;
        elTrail.value  = 5;   lblTrail.textContent  = 5;
        elPoints.value = 50;  lblPoints.textContent = 50;
        elPAmp.value   = 0.2; lblPAmp.textContent   = 0.2;
        elPWL.value    = 3;   lblPWL.textContent    = 3;
        elSAmp.value   = 0.1; lblSAmp.textContent   = 0.1;
        elSWL.value    = 1;   lblSWL.textContent    = 1;

        initParticles(parseInt(elPoints.value));
    }

    function updateLabelsAll(){
        lblTime.textContent   = elTime.value;
        lblTrail.textContent  = elTrail.value;
        lblPoints.textContent = elPoints.value;
        lblPAmp.textContent   = elPAmp.value;
        lblPWL.textContent    = elPWL.value;
        lblSAmp.textContent   = elSAmp.value;
        lblSWL.textContent    = elSWL.value;
    }

}); /* ----------- end p5 sketch ----------- */
</script>
</body>
</html>
