<script>
/* ─────────────────────────────────────────────────────────
   Helper: geophysical displacement model (simplified demo)
   Returns displacements in meters
   --------------------------------------------------------*/
function calculateDisplacements(x, y, dipRad, depth, faultType, nu=0.25){
    const b = 1; // slip magnitude (m)
    const r = Math.sqrt(x*x + y*y + depth*depth) + 1e-9; // avoid zero
    let ux=0, uy=0, uz=0;

    switch(faultType){
        case 'tensile':
            ux = b * (depth / Math.pow(r,3)) * x;
            uy = b * (depth / Math.pow(r,3)) * y;
            uz = b * (1 / r) * Math.sin(dipRad);
            break;
        case 'strike-slip':
            ux = -b * (y / Math.pow(r,2));
            uy =  b * (x / Math.pow(r,2));
            uz = 0;
            break;
        case 'normal':
            ux = b * (depth / Math.pow(r,3)) * x;
            uy = b * (depth / Math.pow(r,3)) * y;
            uz = -b * (1 / r) * Math.sin(dipRad);
            break;
        default:
            break;
    }
    return {ux, uy, uz};
}

/* ─────────────────────────────────────────────────────────
   DOM references
   --------------------------------------------------------*/
const sliderDip   = document.getElementById('slider-dip');
const sliderDepth = document.getElementById('slider-depth');
const sliderLimit = document.getElementById('slider-limit');

const spanDip   = document.getElementById('value-dip');
const spanDepth = document.getElementById('value-depth');
const spanLimit = document.getElementById('value-limit');

const btnComponent = {
    x: document.getElementById('btn-component-x'),
    y: document.getElementById('btn-component-y'),
    z: document.getElementById('btn-component-z')
};
const btnFault = {
    tensile:     document.getElementById('btn-fault-tensile'),
    'strike-slip': document.getElementById('btn-fault-strike-slip'),
    normal:      document.getElementById('btn-fault-normal')
};

/* ─────────────────────────────────────────────────────────
   Initial State
   --------------------------------------------------------*/
let state = {
    dip: Number(sliderDip.value),          // degrees
    depth: Number(sliderDepth.value),      // meters
    limit: Number(sliderLimit.value),      // meters
    component: 'z',                        // 'x','y','z'
    faultType: 'tensile'                   // 'tensile','strike-slip','normal'
};

/* ─────────────────────────────────────────────────────────
   UI helpers
   --------------------------------------------------------*/
function setActiveButton(group, key){
    for(const k in group){
        if(k === key){
            group[k].classList.add('active');
        }else{
            group[k].classList.remove('active');
        }
    }
}

/* ─────────────────────────────────────────────────────────
   Plotting
   --------------------------------------------------------*/
let gridCache = {}; // cache for grid coordinates to reuse if limit unchanged

function generateGrid(limit){
    if(gridCache[limit]) return gridCache[limit];
    const size = 50;
    const xCoords = [];
    const yCoords = [];
    const step = (2*limit)/(size-1);
    for(let i=0;i<size;i++){
        xCoords.push(-limit + i*step);
        yCoords.push(-limit + i*step);
    }
    gridCache[limit] = {xCoords,yCoords, size};
    return gridCache[limit];
}

function updatePlot(){
    const dipRad = state.dip * Math.PI/180;
    const {xCoords, yCoords, size} = generateGrid(state.limit);
    const z_values = [];

    for(let i=0;i<size;i++){
        const row = [];
        for(let j=0;j<size;j++){
            const x = xCoords[j];
            const y = yCoords[i]; // note: swap for Plotly orientation
            const {ux,uy,uz} = calculateDisplacements(x,y,dipRad,state.depth,state.faultType);
            let val;
            switch(state.component){
                case 'x': val = ux; break;
                case 'y': val = uy; break;
                default:  val = uz; break;
            }
            row.push(val*1000); // convert to mm
        }
        z_values.push(row);
    }

    const data = [{
        type:'surface',
        x: xCoords,
        y: yCoords,
        z: z_values,
        colorscale:[['0','#FFA500'],['1','#FFD700']],
        showscale:false,
        contours:{
            x:{show:true,color:'black',width:0.5},
            y:{show:true,color:'black',width:0.5},
            z:{show:false}
        },
        lighting:{
            ambient:0.8,
            diffuse:0.8,
            specular:0.2
        }
    }];

    const layout = {
        title:'',
        scene:{
            xaxis:{title:'x (m)',range:[-state.limit,state.limit]},
            yaxis:{title:'y (m)',range:[-state.limit,state.limit]},
            zaxis:{title:'z (mm)',autorange:true},
            camera:{eye:{x:1.8,y:1.8,z:0.8}}
        },
        margin:{l:20,r:20,b:20,t:20},
    };

    Plotly.react('plot',data,layout,{responsive:true});
}

/* ─────────────────────────────────────────────────────────
   Event Handlers
   --------------------------------------------------------*/
function sliderHandler(){
    state.dip   = Number(sliderDip.value);
    state.depth = Number(sliderDepth.value);
    state.limit = Number(sliderLimit.value);

    spanDip.textContent   = state.dip;
    spanDepth.textContent = state.depth;
    spanLimit.textContent = state.limit;

    updatePlot();
}

sliderDip.addEventListener('input',sliderHandler);
sliderDepth.addEventListener('input',sliderHandler);
sliderLimit.addEventListener('input',sliderHandler);

btnComponent.x.addEventListener('click',()=>{ state.component='x'; setActiveButton(btnComponent,'x'); updatePlot();});
btnComponent.y.addEventListener('click',()=>{ state.component='y'; setActiveButton(btnComponent,'y'); updatePlot();});
btnComponent.z.addEventListener('click',()=>{ state.component='z'; setActiveButton(btnComponent,'z'); updatePlot();});

btnFault['tensile'].addEventListener('click',()=>{ state.faultType='tensile'; setActiveButton(btnFault,'tensile'); updatePlot();});
btnFault['strike-slip'].addEventListener('click',()=>{ state.faultType='strike-slip'; setActiveButton(btnFault,'strike-slip'); updatePlot();});
btnFault['normal'].addEventListener('click',()=>{ state.faultType='normal'; setActiveButton(btnFault,'normal'); updatePlot();});

/* ─────────────────────────────────────────────────────────
   Initialization
   --------------------------------------------------------*/
window.addEventListener('load',()=>{
    // set default active buttons
    setActiveButton(btnComponent,'z');
    setActiveButton(btnFault,'tensile');

    // initial plot
    updatePlot();
});
</script>

</body>
</html>
