<script>
/* ----------  DOM references ---------- */
const sliderT   = document.getElementById('slider-t');
const sliderK   = document.getElementById('slider-k');
const checkAxes = document.getElementById('checkbox-axes');
const tDisplay  = document.getElementById('t-value-display');
const kDisplay  = document.getElementById('k-value-display');
const formulaDiv= document.getElementById('formula-display');

/* ----------  Constants ---------- */
const zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711,
               21.2116, 24.3525, 27.4935, 30.6346];

/* ----------  State ---------- */
let currentT = parseFloat(sliderT.value);
let currentK = parseInt(sliderK.value);
let showAxes = checkAxes.checked;

/* ----------  Utility: Bessel J0 approximation ---------- */
function besselJ0(x){
    const ax = Math.abs(x);
    let y,ans1,ans2;
    if(ax < 8.0){
        y = x*x;
        ans1 = 57568490574.0 + y*(-13362590354.0 + y*(651619640.7
              + y*(-11214424.18 + y*(77392.33017 + y*(-184.9052456)))));
        ans2 = 57568490411.0 + y*(1029532985.0 + y*(9494680.718
              + y*(59272.64853 + y*(267.8532712 + y*1.0))));
        return ans1/ans2;
    }else{
        const z = 8.0/ax;
        y = z*z;
        const xx = ax - 0.785398164;
        ans1 = 1.0 + y*(-0.1098628627e-2 + y*(0.2734510407e-4
              + y*(-0.2073370639e-5 + y*0.2093887211e-6)));
        ans2 = -0.1562499995e-1 + y*(0.1430488765e-3
              + y*(-0.6911147651e-5 + y*(0.7621095161e-6
              - y*0.934935152e-7)));
        return Math.sqrt(0.636619772/ax) *
               (Math.cos(xx)*ans1 - z*Math.sin(xx)*ans2);
    }
}

/* ----------  Update UI helper ---------- */
function updateDisplays(){
    tDisplay.textContent = currentT.toFixed(2);
    kDisplay.textContent = currentK;
}

/* ----------  Update formula with MathJax ---------- */
function updateFormula(){
    const z_k = zeros[currentK-1];
    const omega = z_k / 2;
    formulaDiv.innerHTML = `\\(\\displaystyle \\omega_{${currentK}} = ${omega.toFixed(2)}\\,\\sqrt{g/l}\\)`;
    if(window.MathJax){ MathJax.typesetPromise([formulaDiv]); }
}

/* ----------  Event listeners ---------- */
sliderT.addEventListener('input', e=>{
    currentT = parseFloat(e.target.value);
    updateDisplays();
});
sliderK.addEventListener('input', e=>{
    currentK = parseInt(e.target.value);
    updateDisplays();
    updateFormula();
});
checkAxes.addEventListener('change', e=>{
    showAxes = e.target.checked;
});

/* ----------  Initial display ---------- */
updateDisplays();
updateFormula();

/* ----------  p5.js sketch ---------- */
const sketch = (p)=>{

    const logicalXMin = -2, logicalXMax = 2;
    const logicalYMin = -1.2, logicalYMax = 0;

    let canvasW,canvasH,scaleX,scaleY;

    p.setup = function(){
        canvasW = Math.min(800, document.getElementById('canvas-container').clientWidth - 40);
        canvasH = 480;
        const cnv = p.createCanvas(canvasW, canvasH);
        cnv.parent('canvas-container');

        scaleX = canvasW / (logicalXMax - logicalXMin);
        scaleY = canvasH / (logicalYMax - logicalYMin);
        p.frameRate(40);
    };

    /* map logical x to pixel */
    function mapX(x){ return (x - logicalXMin) * scaleX; }
    /* map logical y to pixel */
    function mapY(y){ return (logicalYMax - y) * scaleY; }

    p.drawAxes = function(){
        p.push();
        p.stroke(120);
        p.strokeWeight(1);
        p.drawingContext.setLineDash([5,5]);

        /* horizontal axis y=0 */
        p.line(mapX(logicalXMin), mapY(0), mapX(logicalXMax), mapY(0));
        /* vertical axis x=0 */
        p.line(mapX(0), mapY(logicalYMax), mapX(0), mapY(logicalYMin));

        p.drawingContext.setLineDash([]);

        /* ticks & labels */
        p.textSize(12);
        p.fill(80);
        p.noStroke();
        p.textAlign(p.CENTER, p.TOP);
        for(let x=-2; x<=2; x++){
            if(x===0) continue;
            const px = mapX(x);
            p.line(px, mapY(0)-4, px, mapY(0)+4);
            p.text(x, px, mapY(0)+6);
        }
        p.textAlign(p.RIGHT,p.CENTER);
        for(let y=-0.2; y>=-1.0; y-=0.2){
            const py = mapY(y);
            p.line(mapX(0)-4, py, mapX(0)+4, py);
            p.text(y.toFixed(1), mapX(0)-6, py);
        }
        p.pop();
    };

    p.drawString = function(){
        const k = currentK;
        const t = currentT;
        const z_k = zeros[k-1];
        const omega = z_k / 2;
        const amplitude = 0.8;
        const timeFactor = Math.cos(omega * t);

        p.push();
        p.stroke('#1f5aff');
        p.strokeWeight(2);
        p.noFill();
        p.beginShape();
        for(let y_norm=0; y_norm<=1.0001; y_norm+=0.01){
            const bessel_arg = z_k * Math.sqrt(y_norm);
            const x_pos = amplitude * timeFactor * besselJ0(bessel_arg);
            const y_pos = -y_norm;
            const px = mapX(x_pos);
            const py = mapY(y_pos);
            p.vertex(px, py);
        }
        p.endShape();
        p.pop();
    };

    p.draw = function(){
        p.background('#f0f0f0');

        if(showAxes){
            p.drawAxes();
        }

        p.drawString();
    };
};

new p5(sketch);
</script>

</body>
</html>
