<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
<script>
/* -------------------- STATE -------------------- */
const layeringPermutations = [
    [1,2,3,4],
    [4,3,2,1],
    [1,3,2,4],
    [2,4,1,3],
    [3,1,4,2]
];
let layeringOrderIndex = 0;

/* Default values for reset */
const defaultOpacities = [0.7,0.7,0.7,0.7];
const defaultColors    = ["#00ff00","#0000ff","#ff0000","#000000"];

/* -------------------- UTILS -------------------- */
function hexToRgb(hex){
    hex = hex.replace('#','');
    if(hex.length===3){
        hex = hex.split('').map(x=>x+x).join('');
    }
    const bigint = parseInt(hex,16);
    return {
        r:(bigint>>16)&255,
        g:(bigint>>8)&255,
        b:bigint&255
    };
}
function updateLayeringDisplay(){
    document.getElementById('layering-display').textContent =
        layeringPermutations[layeringOrderIndex].join(', ');
}

/* -------------------- P5 SKETCH -------------------- */
let sketchInstance;
const s = (p)=>{
    let positions=[];
    p.setup = ()=>{
        const cnv = p.createCanvas(560,420);
        cnv.parent('canvas-container');
        p.noLoop();
        // Precompute circle positions & sizes relative to canvas
        positions = [
            {cx: p.width*0.35, cy: p.height*0.4 , d: p.width*0.4},
            {cx: p.width*0.65, cy: p.height*0.4 , d: p.width*0.4},
            {cx: p.width*0.5 , cy: p.height*0.65, d: p.width*0.4},
            {cx: p.width*0.5 , cy: p.height*0.45, d: p.width*0.3}
        ];
        p.redraw();
    };
    p.draw = ()=>{
        p.background('#f0f0f0');
        const order = layeringPermutations[layeringOrderIndex];
        // Draw circles according to the current order
        order.forEach(idx=>{
            const pos = positions[idx-1];
            const colorHex = document.getElementById(`color-picker${idx}`).value;
            const opacity  = parseFloat(document.getElementById(`slider-opacity${idx}`).value);
            const rgb = hexToRgb(colorHex);
            p.noStroke();
            p.fill(rgb.r,rgb.g,rgb.b, opacity*255);
            p.ellipse(pos.cx,pos.cy,pos.d);
        });
        // Draw labels (always on top)
        p.fill(0);
        p.textAlign(p.CENTER,p.CENTER);
        p.textSize(32);
        for(let i=0;i<4;i++){
            p.text(`${i+1}`, positions[i].cx, positions[i].cy);
        }
    };
};

/* -------------------- EVENT HANDLERS -------------------- */
function attachControlEvents(){
    // Opacity sliders & color pickers
    for(let i=1;i<=4;i++){
        document.getElementById(`slider-opacity${i}`)
                .addEventListener('input', ()=> sketchInstance.redraw());
        document.getElementById(`color-picker${i}`)
                .addEventListener('input', ()=> sketchInstance.redraw());
    }
    // Layering buttons
    document.getElementById('btn-layer-next').addEventListener('click',()=>{
        layeringOrderIndex = (layeringOrderIndex+1)%layeringPermutations.length;
        updateLayeringDisplay();
        sketchInstance.redraw();
    });
    document.getElementById('btn-layer-prev').addEventListener('click',()=>{
        layeringOrderIndex--;
        if(layeringOrderIndex<0) layeringOrderIndex=layeringPermutations.length-1;
        updateLayeringDisplay();
        sketchInstance.redraw();
    });
    // Reset button
    document.getElementById('btn-reset').addEventListener('click',()=>{
        for(let i=1;i<=4;i++){
            document.getElementById(`slider-opacity${i}`).value = defaultOpacities[i-1];
            document.getElementById(`color-picker${i}`).value   = defaultColors[i-1];
        }
        layeringOrderIndex = 0;
        updateLayeringDisplay();
        sketchInstance.redraw();
    });
}

/* -------------------- INITIALIZE -------------------- */
window.addEventListener('DOMContentLoaded',()=>{
    updateLayeringDisplay();
    attachControlEvents();
    sketchInstance = new p5(s);
});
</script>
</body>
</html>
