<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Fugacity Coefficient – SRK / PR Demo</title>
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<style>
    body{
        font-family:Arial,Helvetica,sans-serif;
        margin:0;
        padding:0 10px;
        background:#f5f5f5;
    }
    #app-container{
        max-width:900px;
        margin:0 auto;
    }
    #control-panel{
        background:#ffffff;
        padding:10px;
        border-radius:6px;
        margin-top:10px;
        display:flex;
        flex-wrap:wrap;
        gap:20px;
        align-items:center;
    }
    .eos-button{
        padding:6px 20px;
        border:2px solid #888;
        background:#eee;
        border-radius:4px;
        cursor:pointer;
        font-weight:bold;
    }
    .eos-button.selected{
        border-color:red;
        background:#ffdede;
    }
    #plot-container{
        margin-top:15px;
        background:#dcdcdc;
        padding:10px;
        border-radius:6px;
    }
</style>
</head>
<body>
<div id="app-container">
    <!-- CONTROL PANEL -->
    <div id="control-panel">
        <!-- EoS buttons -->
        <div id="eos-buttons">
            <button id="btn-srk" class="eos-button selected">SRK</button>
            <button id="btn-pr"  class="eos-button">PR</button>
        </div>
        <!-- Aspen checkbox -->
        <div id="aspen-control">
            <input type="checkbox" id="checkbox-aspen" checked>
            <label for="checkbox-aspen">comparison with Aspen Plus</label>
        </div>
        <!-- Pressure slider -->
        <div id="pressure-control">
            <label for="slider-pressure">pressure P&nbsp;</label>
            <input type="range" id="slider-pressure" min="1" max="20" step="1" value="10">
            <span id="display-pressure">10</span>&nbsp;bar
        </div>
    </div>

    <!-- PLOT AREA -->
    <div id="plot-container"></div>
</div>

<script>
/* ---------- CONSTANTS ---------- */
const Tc  = 365.57; // K
const Pc  = 46.65;  // bar
const w   = 0.144;  // acentric factor
const R   = 0.0831446; // L·bar/(mol·K)

let currentEOS = 'SRK';           // 'SRK' | 'PR'
let currentP   = 10;              // bar
let aspenOn    = true;            // checkbox state

/* ---------- UI ELEMENT REFERENCES ---------- */
const btnSRK   = document.getElementById('btn-srk');
const btnPR    = document.getElementById('btn-pr');
const chkAspen = document.getElementById('checkbox-aspen');
const sliderP  = document.getElementById('slider-pressure');
const displayP = document.getElementById('display-pressure');
const plotDiv  = document.getElementById('plot-container');

/* ---------- UTILITY: Solve cubic and return all real roots ---------- */
function cubicRealRoots(a, b, c){
    /* Solves x³ + a x² + b x + c = 0 */
    const shift = a/3;
    const p = b - a*a/3;
    const q = 2*a*a*a/27 - a*b/3 + c;
    const discriminant = (q*q)/4 + (p*p*p)/27;
    let roots = [];
    if(discriminant > 0){                     // one real root
        const sqrtD = Math.sqrt(discriminant);
        const u = Math.cbrt(-q/2 + sqrtD);
        const v = Math.cbrt(-q/2 - sqrtD);
        roots.push( u + v - shift );
    }else if(Math.abs(discriminant) < 1e-12){ // multiple real roots
        const u = Math.cbrt(-q/2);
        roots.push( 2*u - shift );
        roots.push( -u - shift );
    }else{                                    // three distinct real roots
        const r = Math.sqrt(-p*p*p/27);
        const phi = Math.acos( -q/(2*Math.sqrt(-p*p*p/27)) );
        const m = 2*Math.sqrt(-p/3);
        roots.push( m * Math.cos(phi/3)           - shift );
        roots.push( m * Math.cos((phi+2*Math.PI)/3) - shift );
        roots.push( m * Math.cos((phi+4*Math.PI)/3) - shift );
    }
    return roots;
}

/* ---------- EOS-SPECIFIC FUNCTIONS ---------- */
function calcAlphaSRK(Tr){
    const m = 0.480 + 1.574*w - 0.176*w*w;
    return (1 + m*(1-Math.sqrt(Tr)))**2;
}
function calcAlphaPR(Tr){
    const m = 0.37464 + 1.54226*w - 0.26992*w*w;
    return (1 + m*(1-Math.sqrt(Tr)))**2;
}

/* Compute φ array for temperature range */
function calcFugacityData(EOS, P){
    const Tvals = [];
    const phivals = [];
    for(let T=300; T<=500; T+=2){  // 2 K step gives smooth line with 101 points
        const Tr = T / Tc;
        let A,B,lnphi;
        if(EOS==='SRK'){
            const a  = 0.42748 * R*R * Tc*Tc / Pc;
            const b  = 0.08664 * R * Tc / Pc;
            const alpha = calcAlphaSRK(Tr);
            const a_eff = a * alpha;
            A = a_eff * P / (R*R*T*T);
            B = b * P / (R*T);

            // Coefficients of cubic: Z³ - Z² + (A - B - B²)Z - A B = 0
            const roots = cubicRealRoots(-1, (A - B - B*B), -A*B )
                          .filter(z=>z> B+1e-12); // physically meaningful
            const Z = Math.max(...roots);
            lnphi = Z - 1 - Math.log(Z - B) - (A/B)*Math.log(1 + B/Z);
        }else{ // PR
            const a  = 0.45724 * R*R * Tc*Tc / Pc;
            const b  = 0.07780 * R * Tc / Pc;
            const alpha = calcAlphaPR(Tr);
            const a_eff = a * alpha;
            A = a_eff * P / (R*R*T*T);
            B = b * P / (R*T);

            // Cubic: Z³ - (1-B)Z² + (A - 3B² - 2B)Z - (A B - B² - B³) = 0
            const coeffA = -(1 - B);
            const coeffB = (A - 3*B*B - 2*B);
            const coeffC = -(A*B - B*B - B*B*B);
            const roots = cubicRealRoots(coeffA, coeffB, coeffC)
                          .filter(z=>z> B+1e-12);
            const Z = Math.max(...roots);
            const sqrt2 = Math.SQRT2;
            lnphi = Z - 1 - Math.log(Z - B) -
                    (A / (2*sqrt2*B)) *
                    Math.log( (Z + (1+sqrt2)*B)/(Z + (1-sqrt2)*B) );
        }
        const phi = Math.exp(lnphi);
        Tvals.push(T);
        phivals.push(phi);
    }
    return {x:Tvals, y:phivals};
}

/* ---------- ASPEN FIXED DATA ---------- */
const aspenData = {
    x:[300,310,320,330,340,350,360,370,380,390,400],
    y:[0.868,0.881,0.891,0.900,0.909,0.917,0.924,0.930,0.937,0.942,0.946]
};

/* ---------- PLOT INITIALIZATION ---------- */
function initPlot(){
    const eosTrace = createEOSTrace(currentEOS, currentP);
    const aspenTrace = {
        x:aspenData.x,
        y:aspenData.y,
        type:'scatter',
        mode:'markers',
        marker:{color:'green',symbol:'square',size:8},
        name:'Aspen Plus',
        visible: (currentP===10 && aspenOn) ? true : 'legendonly'
    };
    const layout = {
        paper_bgcolor:'#dcdcdc',
        plot_bgcolor:'#ffffff',
        margin:{l:60,r:20,t:20,b:60},
        xaxis:{
            title:'temperature (K)',
            range:[295,505],
            showgrid:true,
            gridcolor:'#e5e5e5',
            minor:{ticklen:3,gridcolor:'#f3f3f3'}
        },
        yaxis:{
            title:'gas-phase fugacity coefficient',
            range:[0.68,1.02],
            showgrid:true,
            gridcolor:'#e5e5e5',
            minor:{ticklen:3,gridcolor:'#f3f3f3'}
        },
        legend:{orientation:'h',y:-0.2}
    };
    Plotly.newPlot(plotDiv, [eosTrace, aspenTrace], layout, {responsive:true});
}

/* Build EOS trace for given state */
function createEOSTrace(EOS, P){
    const data = calcFugacityData(EOS, P);
    const color = (EOS==='SRK') ? 'red' : 'blue';
    return {
        x:data.x,
        y:data.y,
        type:'scatter',
        mode:'lines',
        line:{color:color,width:2},
        name:EOS
    };
}

/* ---------- UPDATE PLOT ---------- */
function updatePlot(){
    const newEOSTrace = createEOSTrace(currentEOS, currentP);
    const showAspen = (currentP===10 && aspenOn);
    /* Plotly.react expects same number of traces; keep order [0]=EOS, [1]=Aspen */
    Plotly.react(plotDiv,
        [
            newEOSTrace,
            {
                x:aspenData.x,
                y:aspenData.y,
                type:'scatter',
                mode:'markers',
                marker:{color:'green',symbol:'square',size:8},
                name:'Aspen Plus',
                visible: showAspen ? true : 'legendonly'
            }
        ],
        plotDiv.layout,
        {responsive:true}
    );
}

/* ---------- EVENT LISTENERS ---------- */
btnSRK.addEventListener('click', ()=>{
    currentEOS = 'SRK';
    btnSRK.classList.add('selected');
    btnPR.classList.remove('selected');
    updatePlot();
});
btnPR.addEventListener('click', ()=>{
    currentEOS = 'PR';
    btnPR.classList.add('selected');
    btnSRK.classList.remove('selected');
    updatePlot();
});

sliderP.addEventListener('input', ()=>{
    currentP = Number(sliderP.value);
    displayP.textContent = currentP;
    updatePlot();
});

chkAspen.addEventListener('change', ()=>{
    aspenOn = chkAspen.checked;
    updatePlot();
});

/* ---------- INITIAL RUN ---------- */
initPlot();
</script>
</body>
</html>
