<!-- ============  SCRIPTS  ============ -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.6.0/lib/p5.min.js"></script>
<script>
/* ---------------------------------------------------
 * GLOBAL STATE OBJECT
 * --------------------------------------------------- */
const state = {
    mode             : 'da',          // 'da','regio','stereo-phile','stereo-diene','homo'
    inverse          : false,
    focus            : false,
    sliderReaction   : 0,
    sliderErgEwg     : 0
};

/* ---------------------------------------------------
 * HELPER FUNCTIONS
 * --------------------------------------------------- */
function updateControlVisibility(){
    const show = (id,flag)=>document.getElementById(id).style.display = flag? 'flex':'none';

    switch(state.mode){
        case 'da':
            show('inverse-container',           false);
            show('focus-container',             true);
            show('reaction-slider-container',   true);
            show('erg-ewg-slider-container',    false);
            break;
        case 'regio':
            show('inverse-container',           true);
            show('focus-container',             true);
            show('reaction-slider-container',   true);
            show('erg-ewg-slider-container',    true);
            break;
        case 'stereo-phile':
        case 'stereo-diene':
            show('inverse-container',           false);
            show('focus-container',             true);
            show('reaction-slider-container',   false);
            show('erg-ewg-slider-container',    false);
            break;
        case 'homo':
            show('inverse-container',           true);
            show('focus-container',             false);
            show('reaction-slider-container',   false);
            show('erg-ewg-slider-container',    true);
            break;
    }
}

function setActiveModeButton(){
    document.querySelectorAll('.mode-button').forEach(btn=>{
        btn.classList.toggle('active', btn.dataset.mode===state.mode);
    });
}

/* ---------------------------------------------------
 * DOM EVENT LISTENERS
 * --------------------------------------------------- */

// ---------- MODE BUTTONS ----------
document.querySelectorAll('.mode-button').forEach(btn=>{
    // store desired mode on each button for easier handling
    const id = btn.id;
    let mode = '';
    if(id==='btn-mode-da')            mode='da';
    if(id==='btn-mode-regio')         mode='regio';
    if(id==='btn-mode-stereo-phile')  mode='stereo-phile';
    if(id==='btn-mode-stereo-diene')  mode='stereo-diene';
    if(id==='btn-mode-homo')          mode='homo';
    btn.dataset.mode = mode;

    btn.addEventListener('click',()=>{
        if(state.mode!==mode){
            state.mode = mode;
            // visual update
            document.querySelectorAll('.mode-button').forEach(b=>b.classList.remove('active'));
            btn.classList.add('active');
            updateControlVisibility();
        }
    });
});

// ---------- CHECKBOXES ----------
document.getElementById('check-inverse').addEventListener('change',e=>{
    state.inverse = e.target.checked;
});
document.getElementById('check-focus').addEventListener('change',e=>{
    state.focus = e.target.checked;
});

// ---------- SLIDERS ----------
document.getElementById('slider-reaction').addEventListener('input',e=>{
    state.sliderReaction = Number(e.target.value);
});
document.getElementById('slider-erg-ewg').addEventListener('input',e=>{
    state.sliderErgEwg = Number(e.target.value);
});

// ---------- PLUS / MINUS BUTTONS ----------
function makeStepper(btnMinusId,btnPlusId,sliderId){
    const slider = document.getElementById(sliderId);
    document.getElementById(btnMinusId).addEventListener('click',()=>{
        slider.value = Math.max(parseInt(slider.value) - 1, slider.min);
        slider.dispatchEvent(new Event('input'));
    });
    document.getElementById(btnPlusId).addEventListener('click',()=>{
        slider.value = Math.min(parseInt(slider.value) + 1, slider.max);
        slider.dispatchEvent(new Event('input'));
    });
}
makeStepper('btn-reaction-minus','btn-reaction-plus','slider-reaction');
makeStepper('btn-erg-ewg-minus','btn-erg-ewg-plus','slider-erg-ewg');

// INITIAL VISIBILITY
updateControlVisibility();

/* ---------------------------------------------------
 * P5.js SKETCH
 * --------------------------------------------------- */

let sketch = (p)=>{

    /* ---------- initial setup ---------- */
    p.setup = function(){
        // remove placeholder canvas (supplied by spec)
        const placeholder = document.getElementById('viz-canvas');
        if(placeholder) placeholder.remove();

        const container = document.getElementById('canvas-container');
        const canvas = p.createCanvas(container.clientWidth, container.clientHeight);
        canvas.parent(container);
        canvas.id('viz-canvas');
        p.noLoop(); // we’ll manually loop using p.redraw()
    };

    /* ---------- responsive canvas ---------- */
    p.windowResized = function(){
        const container = document.getElementById('canvas-container');
        p.resizeCanvas(container.clientWidth, container.clientHeight);
        p.redraw();
    };

    /* ---------- master draw ---------- */
    p.draw = function(){
        p.clear();
        p.background(255);

        switch(state.mode){
            case 'da':           drawDielsAlder(); break;
            case 'regio':        drawRegio();       break;
            case 'stereo-phile':
            case 'stereo-diene': drawStereo();      break;
            case 'homo':         drawHomoLumo();    break;
        }
    };

    /* =====================================================
     * DRAWING FUNCTIONS  (simplified representations)
     * ===================================================== */

    /* ---- 1. Diels–Alder ---- */
    function drawDielsAlder(){
        const w = p.width, h = p.height;
        const t = state.sliderReaction/100;

        // molecule positions through interpolation (0-0.5 move together, 0.5-1 convert)
        const leftStart  = w*0.25;
        const rightStart = w*0.75;
        const leftEnd    = w*0.45;
        const rightEnd   = w*0.55;
        const interp     = Math.min(t,0.5)/0.5;

        const leftX  = p.lerp(leftStart,  leftEnd,  interp);
        const rightX = p.lerp(rightStart, rightEnd, interp);
        const molY   = h*0.5;

        // draw reactant circles
        p.fill(240);
        p.stroke(0);
        p.strokeWeight(2);

        // reacting bonds (just two representative lines)
        p.line(leftX-30, molY-15, leftX+30, molY-15); // pretend π bond
        p.line(rightX-30, molY+15, rightX+30, molY+15);

        p.circle(leftX,  molY, 120);
        p.circle(rightX, molY, 120);

        // product ring when t>0.5
        if(t>0.5){
            const prog = (t-0.5)/0.5; // 0→1

            // fade-in product hexagon
            const radius = 90;
            const centerX = w*0.5;
            const centerY = molY;
            p.push();
            p.noFill();
            p.strokeWeight(3);
            const alpha = p.map(prog,0,1,0,255);
            p.stroke(state.focus ? p.color(255,0,0,alpha) : p.color(0,0,0,alpha));

            p.beginShape();
            for(let i=0;i<6;i++){
                const angle = p.PI/6 + i*p.TWO_PI/6;
                p.vertex(centerX + radius*p.cos(angle),
                         centerY + radius*p.sin(angle));
            }
            p.endShape(p.CLOSE);
            p.pop();
        }

        // focus highlighting
        if(state.focus){
            p.push();
            p.stroke(255,0,0,120);
            p.strokeWeight(6);
            // highlight pre-reaction bonds
            if(t<0.5){
                p.line(leftX-30, molY-15, leftX+30, molY-15);
                p.line(rightX-30, molY+15, rightX+30, molY+15);
            }else{
                // highlight formed σ bonds (representative)
                const centerX=w*0.5, centerY=molY;
                p.line(centerX-90,centerY,centerX-30,centerY-60);
                p.line(centerX+90,centerY,centerX+30,centerY-60);
            }
            p.pop();
        }
        p.noLoop(); // only redraw on interaction
    }

    /* ---- 2. Regioselectivity ---- */
    function drawRegio(){
        const w=p.width,h=p.height;
        const t=state.sliderReaction/100;
        const bias=state.sliderErgEwg/100;  // 0 - 1
        const invert=state.inverse;

        // choose which product is major: top=ortho, bottom=meta for example
        let topOpacity = invert ? 1-bias : 1-(bias*0.8);
        let bottomOpacity = invert ? bias : bias*0.8;

        // background path rectangles
        p.noStroke();
        p.fill(230);
        p.rect(0,0,w,h/2);
        p.rect(0,h/2,w,h/2);

        // Draw reactants → products arrows
        p.stroke(0);
        p.strokeWeight(2);

        drawPath(h*0.25, topOpacity);
        drawPath(h*0.75, bottomOpacity);

        function drawPath(y,op){
            const startX = w*0.15;
            const prodX  = p.lerp(startX, w*0.75, t);
            p.push();
            p.stroke(0,0,0,255*op);
            p.fill(255,255,255,255*op);
            // reactant
            p.circle(startX, y, 60);
            // arrow
            p.line(startX+30,y,prodX-30,y);
            p.triangle(prodX-30,y-6, prodX-30,y+6, prodX-20,y);
            // product
            p.rect(prodX, y-30, 60, 60);
            p.pop();
        }

        // focus highlight: all bonds in products
        if(state.focus){
            p.push();
            p.stroke(255,0,0,120);
            p.strokeWeight(4);
            const y1=h*0.25,y2=h*0.75;
            p.rect(w*0.75, y1-30, 60, 60);
            p.rect(w*0.75, y2-30, 60, 60);
            p.pop();
        }
        p.noLoop();
    }

    /* ---- 3. Stereospecificity ---- */
    function drawStereo(){
        const w=p.width,h=p.height;
        p.noFill(); p.stroke(0); p.strokeWeight(2);

        const topY=h*0.25, bottomY=h*0.75;
        const reactX = w*0.2, prodX = w*0.75;

        // draw reactants circles
        p.circle(reactX,topY,60);
        p.circle(reactX,bottomY,60);

        // draw products (meso vs racemic)
        // top (meso)
        p.rect(prodX, topY-30, 60, 60);
        // symmetry line
        p.drawingContext.setLineDash([5,5]);
        p.stroke(255,0,0,140);
        p.line(prodX+30, topY-40, prodX+30, topY+40);

        // bottom (enantiomers)
        p.drawingContext.setLineDash([]);
        p.stroke(0);
        p.rect(prodX-40, bottomY-30, 60, 60);
        p.rect(prodX+40, bottomY-30, 60, 60);

        // focus highlight
        if(state.focus){
            p.push();
            p.stroke(255,0,0,120);
            p.strokeWeight(4);
            // highlight bonds (edges of rectangles)
            p.rect(prodX, topY-30, 60, 60);
            p.rect(prodX-40, bottomY-30, 60, 60);
            p.rect(prodX+40, bottomY-30, 60, 60);
            p.pop();
        }
        p.noLoop();
    }

    /* ---- 4. HOMO/LUMO ---- */
    function drawHomoLumo(){
        const w=p.width, h=p.height;
        const padding=80;
        const centerY=h*0.5;
        const shift= p.map(state.sliderErgEwg,0,100,0,100);
        const inv=state.inverse;

        // baseline energy
        const baseOffset = 50;

        // compute levels
        let dieneH, dieneL, phileH, phileL;
        if(!inv){
            dieneH = centerY - baseOffset - shift;
            dieneL = centerY + baseOffset - shift;
            phileH = centerY - baseOffset + shift;
            phileL = centerY + baseOffset + shift;
        }else{
            dieneH = centerY - baseOffset + shift;
            dieneL = centerY + baseOffset + shift;
            phileH = centerY - baseOffset - shift;
            phileL = centerY + baseOffset - shift;
        }

        // draw levels
        drawLevel(w*0.3 ,dieneH,"diene HOMO");
        drawLevel(w*0.3 ,dieneL,"diene LUMO");
        drawLevel(w*0.7 ,phileH,"dienophile HOMO");
        drawLevel(w*0.7 ,phileL,"dienophile LUMO");

        // interaction arrows
        const gap1 = Math.abs(dieneH - phileL);
        const gap2 = Math.abs(phileH - dieneL);

        p.strokeWeight(2);
        p.noFill();

        // choose dominance
        const dom1 = (!inv && !state.inverse) || (inv); // which arrow is dominant depends on inverse state
        const stronger = gap1<gap2;

        // arrow 1: diene HOMO -> dienophile LUMO
        drawArrow(w*0.3+60,dieneH, w*0.7-60, phileL, stronger);

        // arrow 2: dienophile HOMO -> diene LUMO
        drawArrow(w*0.7-60,phileH, w*0.3+60, dieneL, !stronger);

        // legend for inverse
        if(state.inverse){
            p.noStroke();
            p.fill(0);
            p.textAlign(p.CENTER);
            p.textSize(12);
            p.text("diene EWG", w*0.2, 30);
            p.text("dienophile ERG", w*0.8, 30);
        }

        function drawLevel(x,y,label){
            p.stroke(0); p.line(x-50,y,x+50,y);
            p.noStroke(); p.fill(0);
            p.textSize(12); p.textAlign(p.CENTER, p.BOTTOM);
            p.text(label,x,y-6);
        }

        function drawArrow(x1,y1,x2,y2,dominant){
            p.push();
            if(dominant){
                p.stroke(255,0,0);
                p.strokeWeight(3);
            }else{
                p.stroke(0,80);
                p.strokeWeight(1.5);
                p.drawingContext.setLineDash([6,4]);
            }
            p.line(x1,y1, x2,y2);
            // arrow head
            const ang = p.atan2(y2-y1,x2-x1);
            const len=dominant?10:7;
            p.line(x2,y2, x2-len*p.cos(ang-0.3), y2-len*p.sin(ang-0.3));
            p.line(x2,y2, x2-len*p.cos(ang+0.3), y2-len*p.sin(ang+0.3));
            p.pop();
        }
        p.noLoop();
    }
};

/* ---------------------------------------------------
 * START P5 INSTANCE
 * --------------------------------------------------- */
new p5(sketch);

/* ---------------------------------------------------
 * WHENEVER STATE CHANGES, REQUEST REDRAW
 * --------------------------------------------------- */
['inverse','focus','sliderReaction','sliderErgEwg'].forEach(id=>{
    // not actual DOM IDs here, but we'll hook events separately
});
function requestRedraw(){
    const p5Instance = window._p5js ? window._p5js[0] : p5.instance;
    if(p5Instance) p5Instance.loop();
}
['check-inverse','check-focus','slider-reaction','slider-erg-ewg'].forEach(id=>{
    document.getElementById(id).addEventListener('input', requestRedraw);
    document.getElementById(id).addEventListener('change', requestRedraw);
});
document.querySelectorAll('.mode-button').forEach(btn=>{
    btn.addEventListener('click', requestRedraw);
});
</script>
</body>
