<body>
<div id="demo-container">
    <!-- Section 1: Control Panel -->
    <div id="control-panel">
        <label>step</label>
        <input type="radio" id="step1" name="step-selector" value="1" checked>
        <label for="step1">1</label>
        <input type="radio" id="step2" name="step-selector" value="2">
        <label for="step2">2</label>
        <input type="radio" id="step3" name="step-selector" value="3">
        <label for="step3">3</label>
    </div>
    <!-- Section 2: Visualization Area -->
    <div id="canvas-container">
        <!-- p5.js canvas will be created here -->
    </div>
</div>

<script>
/*
    Viviani’s theorem – interactive visual proof
    ------------------------------------------------
    p5.js sketch
*/

let A, B, C;                 // vertices of main triangle
let P;                       // draggable point
let dragging = false;        // drag state
const CANVAS_W = 600;
const CANVAS_H = 520;
const rHandle = 10;          // radius for grabbing P

let currentStep = "1";       // default step

function setup() {
    const canvas = createCanvas(CANVAS_W, CANVAS_H);
    canvas.parent('canvas-container');
    // Define main triangle (equilateral)
    const sideLen = 500;
    const h = sideLen * Math.sqrt(3) / 2;           // altitude

    A = createVector(50, 450);                      // left-bottom
    B = createVector(550, 450);                     // right-bottom
    C = createVector(300, 450 - h);                 // top

    // Initial P (inside triangle)
    P = createVector(width * 0.4, height * 0.6);
    if (!isInsideTriangle(P, A, B, C)) {
        // minor safeguard, project to center
        P = p5.Vector.add(A, B).add(C).div(3);
    }

    // Radio buttons listener
    document.querySelectorAll('input[name="step-selector"]').forEach(radio=>{
        radio.addEventListener('change', e=>{
            currentStep = e.target.value;
        });
    });
}

function draw() {
    background(255);

    // ---- Common Elements ----
    // Main triangle
    stroke(0); strokeWeight(2); noFill();
    triangle(A.x, A.y, B.x, B.y, C.x, C.y);

    // Labels
    noStroke(); fill(0); textSize(14);
    text('A', A.x - 15, A.y + 15);
    text('B', B.x + 5, B.y + 15);
    text('C', C.x - 5, C.y - 10);

    // Main altitude (dashed, light-blue)
    push();
    stroke(100, 180, 255); strokeWeight(1.5);
    drawingContext.setLineDash([6, 6]);
    const midAB = p5.Vector.add(A, B).div(2);
    line(C.x, C.y, midAB.x, midAB.y);
    pop();

    // Distances and feet
    const footAB = footOfPerpendicular(P, A, B);
    const footBC = footOfPerpendicular(P, B, C);
    const footAC = footOfPerpendicular(P, A, C);

    const dAB = P.dist(footAB);
    const dBC = P.dist(footBC);
    const dAC = P.dist(footAC);

    // Draw according to current step
    if (currentStep === "1") drawStep1(footAB, footBC, footAC);
    else if (currentStep === "2") drawStep2(footAB, footBC, footAC, dAB, dBC, dAC);
    else drawStep3(dAB, dBC, dAC);

    // Draw point P (different appearance for step 3)
    if (currentStep !== "3") drawPoint(P, color(200), color(0));
    else drawPoint(P, color(180,180,180,120), color(120));  // grayed marker
}

/* -------------------------------------------------- */
/*                STEP-SPECIFIC DRAWS                 */
/* -------------------------------------------------- */
function drawStep1(fAB, fBC, fAC){
    stroke(0); strokeWeight(1.5);
    // Perpendiculars
    line(P.x, P.y, fAB.x, fAB.y);
    line(P.x, P.y, fBC.x, fBC.y);
    line(P.x, P.y, fAC.x, fAC.y);

    // Feet as blue dots
    fill(0, 120, 255); noStroke();
    [fAB, fBC, fAC].forEach(pt=>{
        circle(pt.x, pt.y, 8);
    });
}

function drawStep2(fAB, fBC, fAC, dAB, dBC, dAC){
    // helper to draw equilateral from vertex P
    const redCol = color(255,0,0,120);
    const greenCol = color(0,255,0,120);
    const blueCol = color(0,0,255,120);

    // RED – base parallel to AB (horizontal)
    drawEquilateralFromPeak(P, dAB, createVector(1,0), redCol);

    // GREEN – base parallel to AC
    const dirAC = p5.Vector.sub(C, A).normalize();
    const nAC = p5.Vector.sub(fAC, P).normalize();   // altitude direction
    drawEquilateralGeneric(P, dAC, dirAC, nAC, greenCol, 'x', fAC);

    // BLUE – base parallel to BC
    const dirBC = p5.Vector.sub(C, B).normalize();
    const nBC = p5.Vector.sub(fBC, P).normalize();
    drawEquilateralGeneric(P, dBC, dirBC, nBC, blueCol, 'y', fBC);
}

function drawStep3(dAB, dBC, dAC){
    const xMid = (A.x + B.x)/2;
    const redCol   = color(255,0,0,120);
    const blueCol  = color(0,0,255,120);
    const greenCol = color(0,255,0,120);

    // compute side lengths
    const sR = dAB * 2/Math.sqrt(3);
    const sB = dBC * 2/Math.sqrt(3);
    const sG = dAC * 2/Math.sqrt(3);

    // RED triangle
    const redBaseY = A.y;                     // on AB
    const redTop   = createVector(xMid, redBaseY - dAB);
    fill(redCol); stroke(0); strokeWeight(1);
    beginShape();
    vertex(xMid - sR/2, redBaseY);
    vertex(xMid + sR/2, redBaseY);
    vertex(redTop.x, redTop.y);
    endShape(CLOSE);

    // BLUE triangle (on top of red)
    const blueBaseY = redTop.y;
    const blueTop   = createVector(xMid, blueBaseY - dBC);
    fill(blueCol);
    beginShape();
    vertex(xMid - sB/2, blueBaseY);
    vertex(xMid + sB/2, blueBaseY);
    vertex(blueTop.x, blueTop.y);
    endShape(CLOSE);

    // GREEN triangle (on top of blue) – should align with C
    const greenBaseY = blueTop.y;
    const greenTop   = createVector(xMid, greenBaseY - dAC);  // ideally C
    fill(greenCol);
    beginShape();
    vertex(xMid - sG/2, greenBaseY);
    vertex(xMid + sG/2, greenBaseY);
    vertex(greenTop.x, greenTop.y);
    endShape(CLOSE);

    // Label y at blue top, only once
    noStroke(); fill(0); textSize(14);
    text('y', blueTop.x + 6, blueTop.y + 4);
}

/* -------------------------------------------------- */
/*                  GEOMETRY HELPERS                  */
/* -------------------------------------------------- */
// Draw P marker
function drawPoint(pt, fillCol, strokeCol){
    push();
    stroke(strokeCol); strokeWeight(1.5);
    fill(fillCol);
    circle(pt.x, pt.y, 12);
    // black cross
    line(pt.x -4, pt.y, pt.x +4, pt.y);
    line(pt.x, pt.y -4, pt.x, pt.y +4);
    pop();
    // label P
    noStroke(); fill(200,0,0); textSize(14);
    text('P', pt.x + 8, pt.y - 8);
}

// Generic equilateral drawing with arbitrary orientation
function drawEquilateralGeneric(vertex, alt, dirUnit, nUnit, fillCol, labelChar, footPt){
    const side = alt * 2/Math.sqrt(3);
    const baseCenter = p5.Vector.add(vertex, p5.Vector.mult(nUnit, alt));
    const halfBase = p5.Vector.mult(dirUnit, side/2);

    const b1 = p5.Vector.sub(baseCenter, halfBase);
    const b2 = p5.Vector.add(baseCenter, halfBase);

    fill(fillCol); stroke(0); strokeWeight(1);
    beginShape();
    vertex(b1.x, b1.y);
    vertex(b2.x, b2.y);
    vertex(vertex.x, vertex.y);
    endShape(CLOSE);

    // Altitude line
    stroke(80); strokeWeight(1);
    line(vertex.x, vertex.y, baseCenter.x, baseCenter.y);

    // label on side if requested
    if(labelChar && footPt){
        noStroke(); fill(0); textSize(14);
        text(labelChar, footPt.x + 6, footPt.y + 4);
    }
}

// Simplified for horizontal base (red)
function drawEquilateralFromPeak(vertex, alt, dirUnit, fillCol){
    const side = alt * 2/Math.sqrt(3);
    const baseCenter = createVector(vertex.x, vertex.y + alt);
    const half = side/2;
    fill(fillCol); stroke(0); strokeWeight(1);
    beginShape();
    vertex(baseCenter.x - half, baseCenter.y);
    vertex(baseCenter.x + half, baseCenter.y);
    vertex(vertex.x, vertex.y);
    endShape(CLOSE);

    // altitude line
    stroke(80); strokeWeight(1);
    line(vertex.x, vertex.y, baseCenter.x, baseCenter.y);
}

/* -- foot and distance helpers -- */
function footOfPerpendicular(p, a, b){
    // returns the projection of point p onto line ab
    const ap = p5.Vector.sub(p, a);
    const ab = p5.Vector.sub(b, a);
    const t = constrain(ap.dot(ab) / ab.magSq(), 0, 1); // clamp to segment
    return p5.Vector.add(a, ab.mult(t));
}

function isInsideTriangle(p, a, b, c){
    const area = triArea(a,b,c);
    const a1 = triArea(p,b,c);
    const a2 = triArea(a,p,c);
    const a3 = triArea(a,b,p);
    const diff = Math.abs(area - (a1+a2+a3));
    return diff < 0.1; // small tolerance
}
// area of triangle
function triArea(p1,p2,p3){
    return Math.abs((p1.x*(p2.y-p3.y)+p2.x*(p3.y-p1.y)+p3.x*(p1.y-p2.y))/2);
}

/* -------------------------------------------------- */
/*                  DRAG INTERACTION                  */
/* -------------------------------------------------- */
function mousePressed(){
    if(dist(mouseX, mouseY, P.x, P.y) < rHandle){
        dragging = true;
    }
}
function mouseDragged(){
    if(dragging){
        const candidate = createVector(mouseX, mouseY);
        if(isInsideTriangle(candidate, A, B, C)){
            P.set(candidate);
        }
    }
}
function mouseReleased(){
    dragging = false;
}
</script>
</body>
</html>
