<!-- ---------- THREE.JS & APP SCRIPT ---------- -->
<script type="module">
/* ---- Import Three.js & OrbitControls ---- */
import * as THREE from 'https://cdn.jsdelivr.net/npm/three@0.128.0/build/three.module.js';
import {OrbitControls} from 'https://cdn.jsdelivr.net/npm/three@0.128.0/examples/jsm/controls/OrbitControls.js';

/* ---- DOM ELEMENTS ---- */
const canvas      = document.getElementById('3d-canvas');
const dateDisplay = document.getElementById('date-display');
const timeSlider  = document.getElementById('time-slider');
const playBtn     = document.getElementById('play-pause-button');
const planetBtns  = document.querySelectorAll('#dwarf-planet-selector button');

/* ---- GLOBAL STATE ---- */
let selectedDwarfPlanet = 'Pluto';     // default
let isAnimating         = false;
let animationFrameId    = null;

/* ---- CONSTANTS ---- */
const MILLISECONDS_PER_DAY = 86400000;
const START_DATE_UTC       = Date.UTC(2000,0,1,0,0,0); // Jan 1 2000 00:00:00
const DEG2RAD = Math.PI/180;

/* ---- ORBITAL ELEMENTS ---- */
const orbitalElements = {
    Mercury : {a:0.3871 ,e:0.2056 ,i:7.005  ,omegaNode:48.331 ,argPeri:29.124 ,M:174.796},
    Venus   : {a:0.7233 ,e:0.0068 ,i:3.395  ,omegaNode:76.680 ,argPeri:54.884 ,M:50.416 },
    Earth   : {a:1.0000 ,e:0.0167 ,i:0.000  ,omegaNode:-11.260,argPeri:114.208,M:358.617},
    Mars    : {a:1.5237 ,e:0.0934 ,i:1.850  ,omegaNode:49.562 ,argPeri:286.502,M:19.412 },
    Ceres   : {a:2.7691 ,e:0.0760 ,i:10.593 ,omegaNode:80.393 ,argPeri:73.598 ,M:77.372 },
    Pluto   : {a:39.482 ,e:0.2488 ,i:17.160 ,omegaNode:110.299,argPeri:113.834,M:14.882 },
    Haumea  : {a:43.132 ,e:0.1912 ,i:28.220 ,omegaNode:122.155,argPeri:239.544,M:204.03 },
    Makemake: {a:45.791 ,e:0.1559 ,i:29.006 ,omegaNode:79.623 ,argPeri:294.821,M:95.895 },
    Eris    : {a:67.668 ,e:0.4418 ,i:44.187 ,omegaNode:35.951 ,argPeri:151.291,M:206.51 }
};

/* ---- THREE.JS INITIALISATION ---- */
const scene    = new THREE.Scene();
scene.background = new THREE.Color(0xf0f0f0);

const camera   = new THREE.PerspectiveCamera(45, window.innerWidth/window.innerHeight, 0.1, 1000);
camera.position.set(0, 15, 30);

const renderer = new THREE.WebGLRenderer({canvas:canvas,antialias:true});
renderer.setPixelRatio(window.devicePixelRatio);
resizeRenderer();

/* Orbit Controls */
const controls = new OrbitControls(camera, renderer.domElement);
controls.enableDamping = true;

/* Lighting */
scene.add(new THREE.AmbientLight(0xffffff,0.5));
const sunLight = new THREE.PointLight(0xffffff,1.2);
scene.add(sunLight);

/* Sun Mesh */
const sunGeometry = new THREE.SphereGeometry(0.5,32,32);
const sunMaterial = new THREE.MeshBasicMaterial({color:0xFDB813});
const sunMesh     = new THREE.Mesh(sunGeometry,sunMaterial);
scene.add(sunMesh);

/* Containers for planet meshes/orbits */
const planetMeshes = {};
const orbitLines   = {};
let dwarfMesh      = null;
let dwarfOrbitLine = null;

/* ---- CREATE INNER PLANETS ---- */
['Mercury','Venus','Earth','Mars'].forEach(name=>{
    const mesh = createPlanetMesh(name);
    scene.add(mesh);
    planetMeshes[name]=mesh;

    const orbit = createOrbitLine(name,0x2196f3);
    scene.add(orbit);
    orbitLines[name]=orbit;
});

/* ---- INITIAL DWARF PLANET ---- */
updateDwarfPlanet(selectedDwarfPlanet);

/* ---- EVENT LISTENERS ---- */
window.addEventListener('resize', resizeRenderer);

timeSlider.addEventListener('input', ()=>{
    stopAnimation();
    updateScene();
});

playBtn.addEventListener('click', ()=>{
    isAnimating ? stopAnimation() : startAnimation();
});

planetBtns.forEach(btn=>{
    btn.addEventListener('click', ()=>{
        if(btn.dataset.planet === selectedDwarfPlanet) return;
        document.querySelector('#dwarf-planet-selector button.active')?.classList.remove('active');
        btn.classList.add('active');
        selectedDwarfPlanet = btn.dataset.planet;
        updateDwarfPlanet(selectedDwarfPlanet);
        updateScene();
    });
});

/* ---- CORE  FUNCTIONS ---- */
function createPlanetMesh(name){
    const colorMap = {
        Mercury:0x9e9e9e,
        Venus  :0xd7ccc8,
        Earth  :0x2196f3,
        Mars   :0xf44336
    };
    const geometry = new THREE.SphereGeometry(0.12,16,16);
    const material = new THREE.MeshLambertMaterial({color:colorMap[name]||0x555555});
    return new THREE.Mesh(geometry,material);
}
function createDwarfPlanetMesh(name){
    return new THREE.Mesh(
        new THREE.SphereGeometry(0.18,16,16),
        new THREE.MeshLambertMaterial({color:0x4a148c})
    );
}
function createOrbitLine(name,colorHex){
    const el = orbitalElements[name];
    const points = [];
    for(let j=0;j<=200;j++){
        const theta = j/200 * 2*Math.PI;
        const r = (el.a*(1 - el.e*el.e))/(1 + el.e*Math.cos(theta));
        const x_orb = r * Math.cos(theta);
        const y_orb = r * Math.sin(theta);
        const pos = rotateOrbitalToEcliptic(x_orb,y_orb,0,el);
        points.push(new THREE.Vector3(pos.x,pos.y,pos.z));
    }
    const geometry = new THREE.BufferGeometry().setFromPoints(points);
    const material = new THREE.LineBasicMaterial({color:colorHex});
    return new THREE.Line(geometry,material);
}
function updateDwarfPlanet(name){
    /* remove previous */
    if(dwarfMesh){ scene.remove(dwarfMesh); }
    if(dwarfOrbitLine){ scene.remove(dwarfOrbitLine); }

    /* create new */
    dwarfMesh      = createDwarfPlanetMesh(name);
    dwarfOrbitLine = createOrbitLine(name,0xe53935);
    scene.add(dwarfMesh);
    scene.add(dwarfOrbitLine);
}
function calculatePosition(days, el){
    /* Orbital period in days */
    const P = 365.25 * Math.pow(el.a,1.5);

    /* Mean anomaly (deg) */
    let M = (el.M + (360/P)*days)%360;
    if(M<0) M += 360;
    const e = el.e;

    /* Solve Kepler's Equation for E (rad) */
    let E = M*DEG2RAD;
    for(let k=0;k<10;k++){
        E = E - (E - e*Math.sin(E) - M*DEG2RAD)/(1 - e*Math.cos(E));
    }

    /* True anomaly ν */
    const nu = 2*Math.atan2(Math.sqrt(1+e)*Math.sin(E/2),Math.sqrt(1-e)*Math.cos(E/2));

    /* Distance r */
    const r = el.a * (1 - e*Math.cos(E));

    /* Position in orbital plane */
    const x_orb = r*Math.cos(nu);
    const y_orb = r*Math.sin(nu);

    /* Convert to ecliptic coordinates */
    const pos = rotateOrbitalToEcliptic(x_orb,y_orb,0,el);
    return new THREE.Vector3(pos.x,pos.y,pos.z);
}
function rotateOrbitalToEcliptic(x,y,z,el){
    /* Rotate by argument of perihelion (ω), inclination (i), longitude of ascending node (Ω) */
    const i  = el.i*DEG2RAD;
    const Ω  = el.omegaNode*DEG2RAD;
    const ω  = el.argPeri*DEG2RAD;

    const cosΩ = Math.cos(Ω), sinΩ = Math.sin(Ω);
    const cosi = Math.cos(i), sini = Math.sin(i);
    const cosωpθ = Math.cos(ω); // we rotate (x,y) by ω first when only position in ellipse; handled below with argument
    const sinωpθ = Math.sin(ω);

    /* Rotate (x,y) in plane by ω -> (x1,y1) */
    const x1 = x*Math.cos(ω) - y*Math.sin(ω);
    const y1 = x*Math.sin(ω) + y*Math.cos(ω);
    const z1 = z;

    /* Inclination & node */
    const x2 = x1*cosΩ - y1*sinΩ*cosi + z1*sinΩ*sini;
    const y2 = x1*sinΩ + y1*cosΩ*cosi - z1*cosΩ*sini;
    const z2 = y1*sini + z1*cosi;

    return {x:x2,y:z2,z:y2}; // swap so that 'y' (vertical) is up
}
function daysSinceStart(){
    return parseInt(timeSlider.value,10);
}
function currentDate(){
    return new Date(START_DATE_UTC + daysSinceStart()*MILLISECONDS_PER_DAY);
}
function updateDateDisplay(){
    const date = currentDate();
    const days = ['Sun','Mon','Tue','Wed','Thu','Fri','Sat'];
    const months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    const txt = `${days[date.getUTCDay()]} ${date.getUTCDate()} ${months[date.getUTCMonth()]} ${date.getUTCFullYear()} 00:00:00`;
    dateDisplay.textContent = txt;
}
function updateScene(){
    updateDateDisplay();
    const d = daysSinceStart();

    /* Update inner planets */
    ['Mercury','Venus','Earth','Mars'].forEach(name=>{
        const pos = calculatePosition(d,orbitalElements[name]);
        planetMeshes[name].position.copy(pos);
    });

    /* Update dwarf planet */
    const posD = calculatePosition(d,orbitalElements[selectedDwarfPlanet]);
    dwarfMesh.position.copy(posD);

    renderer.render(scene,camera);
}
function animate(){
    controls.update();
    renderer.render(scene,camera);

    if(isAnimating){
        let val = parseInt(timeSlider.value,10)+2;
        if(val > parseInt(timeSlider.max,10)) val = parseInt(timeSlider.min,10);
        timeSlider.value = val;
        updateScene();
        animationFrameId = requestAnimationFrame(animate);
    }
}
function startAnimation(){
    isAnimating = true;
    playBtn.textContent = '⏸';
    animate();
}
function stopAnimation(){
    isAnimating = false;
    playBtn.textContent = '▶';
    if(animationFrameId) cancelAnimationFrame(animationFrameId);
}
function resizeRenderer(){
    const w = renderer.domElement.clientWidth;
    const h = renderer.domElement.clientHeight;
    renderer.setSize(w,h,false);
    camera.aspect = w/h;
    camera.updateProjectionMatrix();
}
/* ---- INITIAL DRAW ---- */
updateScene();
renderer.setAnimationLoop(()=>{controls.update();renderer.render(scene,camera);});
</script>
</body>
