<script>
/* -----------------------------------------------------------------
   1.  DATA
------------------------------------------------------------------*/
const cfcData = {
  'CFC-12': { color: 'purple', data: [[1940, 5], [1950, 20], [1960, 50], [1970, 150], [1980, 320], [1990, 480], [2000, 540], [2010, 520], [2020, 500]] },
  'CFC-11': { color: 'orange', data: [[1940, 2], [1950, 10], [1960, 30], [1970, 90], [1980, 180], [1990, 260], [2000, 250], [2010, 230], [2020, 220]] },
  'CFC-113': { color: 'black', data: [[1940, 0], [1950, 1], [1960, 5], [1970, 15], [1980, 40], [1990, 80], [2000, 85], [2010, 75], [2020, 70]] },
  'CCl4': { color: 'green', data: [[1940, 50], [1950, 60], [1960, 75], [1970, 90], [1980, 100], [1990, 105], [2000, 95], [2010, 85], [2020, 80]] }
};

/* -----------------------------------------------------------------
   2.  ELEMENT REFERENCES
------------------------------------------------------------------*/
const selectCFC   = document.getElementById('select-cfc');
const checkboxAll = document.getElementById('checkbox-show-all');
const sliderYear  = document.getElementById('slider-year');
const labelYear   = document.getElementById('label-year');
const sliderTime  = document.getElementById('slider-time');
const labelTime   = document.getElementById('label-time');

/* -----------------------------------------------------------------
   3.  HELPER FUNCTIONS
------------------------------------------------------------------*/
function interpolate(arr, x) {
    // Linear interpolation of y at given x assuming arr = [[x0,y0],...]
    for (let i = 0; i < arr.length - 1; i++) {
        const [x0,y0] = arr[i];
        const [x1,y1] = arr[i+1];
        if (x >= x0 && x <= x1) {
            const t = (x - x0)/(x1 - x0);
            return y0 + t*(y1 - y0);
        }
    }
    // Out of range, return boundary value
    return (x < arr[0][0]) ? arr[0][1] : arr[arr.length-1][1];
}

/* -----------------------------------------------------------------
   4.  PLOTS
------------------------------------------------------------------*/
const y0_Ozone = 10;          // 1/[O3](0)
const kfactor  = 0.05;        // arbitrary rate constant multiplier
let ozoneTrace = {};          // stored for quick update

function drawCfcPlot() {
    const selected = selectCFC.value;
    const showAll  = checkboxAll.checked;
    const selectedYear = +sliderYear.value;

    let traces = [];
    // Line traces
    for (const [name,obj] of Object.entries(cfcData)) {
        const xs = obj.data.map(d => d[0]);
        const ys = obj.data.map(d => d[1]);
        traces.push({
            x: xs,
            y: ys,
            mode:'lines',
            name: name,
            line:{
                color: obj.color,
                width: (name===selected)?4:2
            },
            visible: showAll ? true : (name===selected)
        });
    }
    // Marker trace (always on top)
    const conc = interpolate(cfcData[selected].data, selectedYear);
    traces.push({
        x: [selectedYear],
        y: [conc],
        mode:'markers',
        marker:{color:'red', size:10},
        name:'Selected year',
        showlegend:false
    });

    const layout = {
        title:'CFC concentration',
        margin:{l:50,r:20,t:40,b:40},
        xaxis:{title:'year', range:[1920,2020]},
        yaxis:{range:[0,600]},
        legend:{orientation:'h', y:-0.2}
    };

    Plotly.react('plot-cfc', traces, layout, {responsive:true});
    return conc;   // return concentration for further calculations
}

function drawOzonePlot(currentConc) {
    const tEnd = +sliderTime.value;
    const xs = [0, tEnd];
    const slope = kfactor * currentConc;
    const ys = xs.map(t => y0_Ozone + slope*t);

    ozoneTrace = {
        x: xs,
        y: ys,
        mode:'lines',
        line:{color:'blue', width:3},
        name:'1/[O3]'
    };
    const layout = {
        title:'concentration of ozone',
        margin:{l:60,r:20,t:40,b:40},
        xaxis:{title:'time (s)', range:[0,30]},
        yaxis:{title:'1/[O<sub>3</sub>]', range:[0,110]}
    };
    Plotly.react('plot-ozone', [ozoneTrace], layout, {responsive:true});
}

/* -----------------------------------------------------------------
   5.  THREE.JS SCENE
------------------------------------------------------------------*/
let renderer, scene, camera, ozoneLayerMaterial;

function init3D() {
    const container = document.getElementById('scene-container');
    const w = container.clientWidth;
    const h = container.clientHeight;

    scene = new THREE.Scene();

    camera = new THREE.PerspectiveCamera(40, w/h, 0.1, 1000);
    camera.position.set(0, 3, 10);

    renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setSize(w, h);
    container.appendChild(renderer.domElement);

    // Earth
    const earthGeom = new THREE.SphereGeometry(2,32,32);
    const earthMat  = new THREE.MeshBasicMaterial({color:0x0000ff});
    const earth = new THREE.Mesh(earthGeom, earthMat);
    scene.add(earth);

    // Ozone Layer
    const ozoneGeom = new THREE.SphereGeometry(2.2,32,32);
    ozoneLayerMaterial = new THREE.MeshBasicMaterial({
        color:0x00ff00,
        transparent:true,
        opacity:0.9
    });
    const ozoneLayer = new THREE.Mesh(ozoneGeom, ozoneLayerMaterial);
    scene.add(ozoneLayer);

    // Atmosphere shell
    const atmGeom = new THREE.SphereGeometry(2.4,32,32);
    const atmMat  = new THREE.MeshBasicMaterial({
        color:0xffaa88,
        transparent:true,
        opacity:0.1
    });
    const atm = new THREE.Mesh(atmGeom, atmMat);
    scene.add(atm);

    // Sun
    const sunGeom = new THREE.SphereGeometry(0.5,32,32);
    const sunMat  = new THREE.MeshBasicMaterial({color:0xffff00});
    const sun = new THREE.Mesh(sunGeom, sunMat);
    sun.position.set(-6,3,0);
    scene.add(sun);

    // Radiation arrows
    const arrowDir = new THREE.Vector3(1, -0.2, 0).normalize();
    for(let i=-2;i<=2;i++){
        const origin = new THREE.Vector3(-5,3+i*0.3,i*0.2);
        const arrow = new THREE.ArrowHelper(arrowDir, origin, 6, 0xff8800,0.3,0.2);
        scene.add(arrow);
    }

    // Handle resizing
    window.addEventListener('resize', ()=>{
        const w = container.clientWidth;
        const h = container.clientHeight;
        camera.aspect = w/h;
        camera.updateProjectionMatrix();
        renderer.setSize(w,h);
    });

    animate();
}

function animate(){
    requestAnimationFrame(animate);
    // gently rotate the scene for aesthetics
    scene.rotation.y += 0.0015;
    renderer.render(scene,camera);
}

/* -----------------------------------------------------------------
   6.  UPDATE FUNCTION (central hub)
------------------------------------------------------------------*/
function update(){
    // Update labels
    labelYear.textContent = sliderYear.value;
    labelTime.textContent = sliderTime.value;

    // (1) Update CFC plot, get current concentration
    const concentration = drawCfcPlot();

    // (2) Update Ozone plot
    drawOzonePlot(concentration);

    // (3) Update 3D ozone opacity
    const newOpacity = Math.max(0.1, 1 - concentration/600); // clamp at 0.1
    ozoneLayerMaterial.opacity = newOpacity;
}

/* -----------------------------------------------------------------
   7.  INITIALIZATION
------------------------------------------------------------------*/
init3D();
update();

/* Event listeners */
selectCFC.addEventListener('change', update);
checkboxAll.addEventListener('change', update);
sliderYear.addEventListener('input', update);
sliderTime.addEventListener('input', update);

</script>
</body>
</html>
