        text-align:center;
    }
    canvas{
        border:1px solid #ccc;
    }
</style>
</head>
<body>

<h1>Eratosthenes's Measure of the Earth's Circumference</h1>
<p>
Eratosthenes (276–194 BCE) famously estimated the circumference of the Earth by comparing the
sun’s angle at Alexandria with the no-shadow noon at Syene.  Use the slider to change the measured
angle and the checkbox to switch between a full–Earth view and a zoomed-in geometric view
highlighting the sector between the two cities.
</p>

<!-- ===== Control Panel ===== -->
<div id="control-panel">
    <label for="slider-post-angle">post angle</label>
    <input  type="range"
            id="slider-post-angle"
            min="1"
            max="60"
            step="0.1"
            value="7">
    <span id="display-post-angle">7.0</span>°
    &nbsp;&nbsp;&nbsp;
    <label for="checkbox-zoom">zoom</label>
    <input type="checkbox" id="checkbox-zoom">
</div>

<!-- ===== Visualization Area ===== -->
<div id="visualization-container">
    <canvas id="demo-canvas"></canvas>
</div>

<script>
/* ---------- Global State ---------- */
let angle = 7;          // degrees
let isZoomed = false;   // full view by default
let canvas;

/* ---------- DOM : Controls ---------- */
const slider = document.getElementById('slider-post-angle');
const angleDisplay = document.getElementById('display-post-angle');
const zoomCheckbox = document.getElementById('checkbox-zoom');

/* Update angle from slider */
slider.addEventListener('input', e=>{
    angle = parseFloat(e.target.value);
    angleDisplay.textContent = angle.toFixed(1);
    redraw();
});

/* Update zoom state from checkbox */
zoomCheckbox.addEventListener('change', e=>{
    isZoomed = e.target.checked;
    redraw();
});

/* ---------- p5.js Sketch ---------- */
function setup(){
    canvas = createCanvas(600, 450);
    canvas.parent('visualization-container');
    angleMode(DEGREES);
    noLoop();                     // draw only when redraw() is called
}

/* Main draw dispatcher */
function draw(){
    background('#F0F0F0');
    drawSunRays();                // common element

    if(isZoomed){
        drawZoomedView();
    }else{
        drawFullView();
    }
}

/* ---------- Shared Visual Elements ---------- */
function drawSunRays(){
    push();
    stroke('#DAA520');
    strokeWeight(3);
    const left = width - 120;
    const right = width - 10;
    for(let y=40; y<height; y+=60){
        line(left, y, right, y);
        // Arrow-head
        fill('#DAA520');
        noStroke();
        triangle(left, y, left+12, y-5, left+12, y+5);
        stroke('#DAA520');
    }
    pop();
}

/* ---------- View 1 : Full Earth ---------- */
function drawFullView(){
    const earthR = 180;
    const earthX = 200;
    const earthY = height/2;

    // Earth
    push();
    noStroke();
    fill('#468296');
    ellipse(earthX, earthY, earthR*2, earthR*2);
    fill(255);
    textAlign(CENTER,CENTER);
    textSize(18);
    text('Earth', earthX, earthY);
    pop();

    // Syene radius (horizontal right)
    const sx = earthX + earthR;
    const sy = earthY;
    drawDashedLine(earthX, earthY, sx, sy);

    // Syene label
    push();
    fill(0);
    noStroke();
    textAlign(LEFT, CENTER);
    text('Syene', sx + 5, sy);
    pop();

    // Alexandria point on circumference
    const ax = earthX + earthR * cos(-angle);
    const ay = earthY + earthR * sin(-angle);

    // Alexandria radial
    drawDashedLine(earthX, earthY, ax, ay);

    // Horizontal through Alexandria
    drawDashedLine(ax, ay, width-130, ay);

    // Alexandria label
    push();
    fill(0);
    noStroke();
    textAlign(LEFT, CENTER);
    text('Alexandria', ax + 5, ay - 5);
    pop();

    // Angle arc at Alexandria
    push();
    noFill();
    stroke(0);
    strokeWeight(1.5);
    const arcSize = 80;
    const startA = 0;            // 0° points right
    const endA   = -angle;       // negative for CCW up
    arc(ax, ay, arcSize, arcSize, endA, startA);
    pop();

    // Angle text
    push();
    fill(0);
    noStroke();
    textSize(14);
    text(angle.toFixed(1)+'°', ax + 35, ay - 20);
    pop();

    // Observation arrow/triangle at Alexandria
    push();
    fill(0);
    noStroke();
    const dirX = cos(-angle);
    const dirY = sin(-angle);
    const tipX = ax;
    const tipY = ay;
    const base1X = tipX + 10*dirX - 5*dirY;   // small perpendicular base
    const base1Y = tipY + 10*dirY + 5*dirX;
    const base2X = tipX + 10*dirX + 5*dirY;
    const base2Y = tipY + 10*dirY - 5*dirX;
    triangle(tipX, tipY, base1X, base1Y, base2X, base2Y);
    pop();
}

/* ---------- View 2 : Zoomed Sector ---------- */
function drawZoomedView(){
    const cx = 100;             // center x
    const cy = height + 50;     // center below canvas for visible sector
    const R  = 450;             // radius large enough to fill area

    // Earth sector
    push();
    fill('#468296');
    noStroke();
    arc(cx, cy, R*2, R*2, -angle, 0, PIE);
    pop();

    // Radii : Syene (0°) and Alexandria (-angle)
    const sx = cx + R * cos(0);
    const sy = cy + R * sin(0);
    const ax = cx + R * cos(-angle);
    const ay = cy + R * sin(-angle);

    drawDashedLine(cx, cy, sx, sy);      // Syene radius
    drawDashedLine(cx, cy, ax, ay);      // Alexandria radius

    // Labels
    push();
    fill(0);
    noStroke();
    textAlign(CENTER, CENTER);
    text('Syene', sx, sy - 15);
    text('Alexandria', ax, ay - 15);
    pop();

    // Angle arc at center
    push();
    noFill();
    stroke(0);
    strokeWeight(2);
    const arcRad = 120;
    arc(cx, cy, arcRad*2, arcRad*2, -angle, 0);
    pop();

    // Angle text (inside sector)
    push();
    fill(0);
    noStroke();
    textSize(16);
    const midAngle = -angle/2;
    const tx = cx + (arcRad+20)*cos(midAngle);
    const ty = cy + (arcRad+20)*sin(midAngle);
    text(angle.toFixed(1)+'°', tx, ty);
    pop();

    // Post / Shadow right-angle triangle at Alexandria
    drawPostAndShadow(ax, ay, -angle);
}

/* ---------- Utility Drawing Functions ---------- */
function drawDashedLine(x1,y1,x2,y2){
    push();
    stroke(0);
    strokeWeight(1.5);
    drawingContext.setLineDash([6,6]);
    line(x1,y1,x2,y2);
    drawingContext.setLineDash([]);
    pop();
}

function drawPostAndShadow(ax, ay, localAngle){
    push();
    const postLen = 40;
    const shadowLen = 40;

    // Post direction (towards center) is opposite of radial
    const postDX = -cos(localAngle);
    const postDY = -sin(localAngle);
    const postEndX = ax + postDX * postLen;
    const postEndY = ay + postDY * postLen;

    // Shadow direction horizontal to right (sunlight direction)
    const shadowEndX = ax + shadowLen;
    const shadowEndY = ay;

    fill('#D2B48C');
    stroke('#8B795E');
    strokeWeight(1);
    beginShape();
        vertex(ax, ay);                 // Alexandria point
        vertex(postEndX, postEndY);     // end of post
        vertex(shadowEndX, shadowEndY); // end of shadow
    endShape(CLOSE);

    // outline for clarity
    line(ax, ay, postEndX, postEndY);
    line(ax, ay, shadowEndX, shadowEndY);
    line(postEndX, postEndY, shadowEndX, shadowEndY);
    pop();
}
</script>
</body>
</html>
