<script>
/* ------------- Interaction Wiring ------------- */
document.addEventListener("DOMContentLoaded", () => {
  const sliders = [
    document.getElementById("slider-moon-pos"),
    document.getElementById("slider-moon-pull"),
    document.getElementById("slider-sun-pos"),
    document.getElementById("slider-sun-pull")
  ];

  sliders.forEach(slider => {
    slider.addEventListener("input", () => {
      // ask p5.js to render one frame
      if (typeof redraw === "function") redraw();
    });
  });

  document.getElementById("btn-reset").addEventListener("click", () => {
    // default values as specified
    document.getElementById("slider-moon-pos").value  = 144;
    document.getElementById("slider-moon-pull").value = 40;
    document.getElementById("slider-sun-pos").value   = 144;
    document.getElementById("slider-sun-pull").value  = 20;
    if (typeof redraw === "function") redraw();
  });
});

/* ------------- p5.js Sketch ------------- */
const baseEarthRadius  = 180;  // pixels
const baseWaterRadius  = 190;  // a little larger than earth
const scalingFactor    = 0.5;  // converts pull into pixel bulge

function setup(){
  const canvas = createCanvas(500, 500);
  canvas.parent("viz-container");
  angleMode(RADIANS);
  noLoop();   // draw only when explicitly requested
  redraw();   // initial render
}

function draw(){
  // ----- Read current control values -----
  const moonAngleDeg = Number(document.getElementById("slider-moon-pos").value);
  const moonPull     = Number(document.getElementById("slider-moon-pull").value);
  const sunAngleDeg  = Number(document.getElementById("slider-sun-pos").value);
  const sunPull      = Number(document.getElementById("slider-sun-pull").value);

  const moonAngleRad = radians(moonAngleDeg);
  const sunAngleRad  = radians(sunAngleDeg);

  // ----- Clear Canvas -----
  clear();                           // resets background to transparent
  background(255);                   // white background
  translate(width/2, height/2);      // move origin to center

  // ----- Draw Water Layer -----
  stroke(0);
  strokeWeight(1);
  fill("#aed6f1");

  beginShape();
  for(let deg = 0; deg <= 360; deg += 2){   // iterate every 2°
    const theta = radians(deg);

    const moonEffect = moonPull * sq(cos(theta - moonAngleRad));
    const sunEffect  = sunPull  * sq(cos(theta - sunAngleRad));
    const totalRadius = baseWaterRadius + scalingFactor * (moonEffect + sunEffect);

    const x = totalRadius * cos(theta);
    const y = totalRadius * sin(theta);
    vertex(x, y);
  }
  endShape(CLOSE);

  // ----- Draw Earth -----
  noStroke();
  fill("#966F33");
  ellipse(0, 0, baseEarthRadius * 2, baseEarthRadius * 2);  // diameter = 2*radius
}
</script>

</body>
