<script>
/********************************************************************
    Basic helpers
*********************************************************************/
function degToRad(d){return d*Math.PI/180;}
function mapRange(v, vmin, vmax, tmin, tmax){
    return tmin + (tmax-tmin)*( (v-vmin)/(vmax-vmin) );
}

/********************************************************************
    DOM references
*********************************************************************/
const sliders = {
    earthPos:   document.getElementById('slider-earth-position'),
    cometPos:   document.getElementById('slider-comet-position'),
    coma:       document.getElementById('slider-coma'),
    tail:       document.getElementById('slider-tail'),
    tailSpread: document.getElementById('slider-tail-spread')
};

const orbitCanvas      = document.getElementById('orbit-canvas');
const telescopicCanvas = document.getElementById('telescopic-canvas');

/********************************************************************
    THREE.JS – Orbit Diagram Scene
*********************************************************************/
const orbitScene   = new THREE.Scene();
const orbitCamera  = new THREE.PerspectiveCamera(45, 1, 0.1, 1000);
orbitCamera.position.set(0, 25, 25);
orbitCamera.lookAt(0,0,0);

const rendererOrbit = new THREE.WebGLRenderer({canvas: orbitCanvas, antialias:true});
rendererOrbit.setClearColor(0xffffff, 1);

/* Lights */
orbitScene.add(new THREE.AmbientLight(0xffffff, 0.3));
const sunLight = new THREE.PointLight(0xffee88, 1.2, 0);
orbitScene.add(sunLight);

/* Sun */
const sunGeom = new THREE.SphereGeometry(0.5, 32,32);
const sunMat  = new THREE.MeshBasicMaterial({color:0xffdd55});
const sunMesh = new THREE.Mesh(sunGeom, sunMat);
orbitScene.add(sunMesh);

/* Function that creates orbit lines */
function createOrbit(a,e,color){
    const curve = new THREE.EllipseCurve(
        0,0,             // ax, aY (center)
        a, a*Math.sqrt(1-e*e),  // xRadius, yRadius  (b = a*sqrt(1-e^2))
        0, 2*Math.PI,
        false, 0
    );
    const points = curve.getPoints(360);
    const geom   = new THREE.BufferGeometry().setFromPoints(points.map(p => new THREE.Vector3(p.x,0,p.y)));
    const mat    = new THREE.LineBasicMaterial({color:color});
    const ellipse = new THREE.LineLoop(geom, mat);
    return ellipse;
}

/* Add orbit lines */
orbitScene.add(createOrbit(4,   0.0167, 0x0000ff)); // Earth
orbitScene.add(createOrbit(6.1, 0.0934, 0xff0000)); // Mars
orbitScene.add(createOrbit(20.8,0.0489, 0xff8800)); // Jupiter
orbitScene.add(createOrbit(14.4,0.432,  0xffff00)); // Comet Holmes

/* Earth mesh */
const earthMesh = new THREE.Mesh(
    new THREE.SphereGeometry(0.25,24,24),
    new THREE.MeshPhongMaterial({color:0x0033ff})
);
orbitScene.add(earthMesh);

/* ---------------- Comet (group) ---------------- */
function buildCometGroup(){
    const group = new THREE.Group();

    // nucleus
    const nuc = new THREE.Mesh(
        new THREE.SphereGeometry(0.15,24,24),
        new THREE.MeshBasicMaterial({color:0xffffff})
    );
    group.add(nuc);

    // coma
    const comaGeom = new THREE.SphereGeometry(0.4, 32,32);
    const comaMat  = new THREE.MeshBasicMaterial({color:0x99ff99, transparent:true, opacity:0.5});
    const comaMesh = new THREE.Mesh(comaGeom, comaMat);
    group.add(comaMesh);

    // tail (along +Y by default)
    const tailHeight = 3;
    const tailGeom = new THREE.ConeGeometry(0.4, tailHeight, 32, 1, true);
    /* move so that base touches nucleus (at origin) and points +Y  */
    tailGeom.translate(0, tailHeight/2, 0);

    const tailMat  = new THREE.MeshBasicMaterial({color:0x99ff99, transparent:true, opacity:0.4});
    const tailMesh = new THREE.Mesh(tailGeom, tailMat);
    group.add(tailMesh);

    return {group, nucleus: nuc, coma: comaMesh, tail: tailMesh};
}

/* Create two comet groups (they share geometries & materials internally) */
const cometOrbit  = buildCometGroup();
const cometTelesc = {
    group   : cometOrbit.group.clone(true),
    nucleus : null,
    coma    : null,
    tail    : null
};

/* Locate analogous child references inside the cloned group */
cometTelesc.group.traverse((obj)=>{
    if(obj.name === cometOrbit.nucleus.name) cometTelesc.nucleus = obj;
});
cometTelesc.nucleus = cometTelesc.group.children[0];
cometTelesc.coma    = cometTelesc.group.children[1];
cometTelesc.tail    = cometTelesc.group.children[2];

/* Add groups to scenes */
orbitScene.add(cometOrbit.group);

/********************************************************************
    THREE.JS – Telescopic Scene
*********************************************************************/
const telescopicScene  = new THREE.Scene();
telescopicScene.background = new THREE.Color(0x000000);
const telescopicCamera = new THREE.PerspectiveCamera(15, 1, 0.01, 1000);

/* Basic ambient so we can see nucleus/coma */
telescopicScene.add(new THREE.AmbientLight(0xffffff, 1));
telescopicScene.add(cometTelesc.group);

const rendererTelescopic = new THREE.WebGLRenderer({canvas: telescopicCanvas, antialias:true});
rendererTelescopic.setClearColor(0x000000, 1);

/********************************************************************
    Compute position on ellipse
*********************************************************************/
function orbitalPosition(a,e,angleDeg){
    const theta = degToRad(angleDeg);
    const r = (a*(1-e*e)) / (1 + e*Math.cos(theta));
    return new THREE.Vector3( r*Math.cos(theta), 0, r*Math.sin(theta) );
}

/********************************************************************
    UPDATE ROUTINE  (called on any slider input)
*********************************************************************/
function update(){
    const earthAngle  = parseFloat(sliders.earthPos.value);
    const cometAngle  = parseFloat(sliders.cometPos.value);

    /* Positions */
    const earthPos = orbitalPosition(4, 0.0167, earthAngle);
    earthMesh.position.copy(earthPos);

    const cometPos = orbitalPosition(14.4, 0.432, cometAngle);
    cometOrbit.group.position.copy(cometPos);
    cometTelesc.group.position.copy(cometPos);

    /* Update comet tail orientation (point away from Sun) */
    const dir = cometPos.clone().normalize(); // pointing away
    const baseAxis = new THREE.Vector3(0,1,0); // tail default orientation +Y
    const quat = new THREE.Quaternion().setFromUnitVectors(baseAxis, dir);

    [cometOrbit.tail, cometTelesc.tail].forEach(tail=>{
        tail.quaternion.copy(quat);
    });

    /* Coma scale */
    const comaScale = mapRange(parseFloat(sliders.coma.value), 0, 100, 0.01, 2.5);
    [cometOrbit.coma, cometTelesc.coma].forEach(coma=>{
        coma.scale.setScalar(comaScale);
        coma.material.opacity = mapRange(sliders.coma.value, 0,100, 0.05,0.7);
    });

    /* Tail length */
    const tailLengthScale = mapRange(parseFloat(sliders.tail.value), 0,100, 0.01, 3);
    /* Tail width */
    const tailWidthScale  = mapRange(parseFloat(sliders.tailSpread.value), 0,100, 0.05, 3);

    [cometOrbit.tail, cometTelesc.tail].forEach(tail=>{
        tail.scale.set(tailWidthScale, tailLengthScale, tailWidthScale);
        tail.material.opacity = mapRange(sliders.tail.value, 0,100, 0.0, 0.6);
    });

    /* Telescopic camera is at Earth's position, looking at comet */
    telescopicCamera.position.copy(earthPos.clone().add(new THREE.Vector3(0, 0.3, 0)));
    telescopicCamera.lookAt(cometPos);

    /* keep aspect ratios fresh */
    onWindowResize();
}
Object.values(sliders).forEach(s=>s.addEventListener('input', update));

/********************************************************************
    RESIZE HANDLING
*********************************************************************/
function onWindowResize(){
    /* Orbit renderer */
    const orbitWidth  = orbitCanvas.clientWidth;
    const orbitHeight = orbitCanvas.clientHeight;
    if( orbitCanvas.width !== orbitWidth || orbitCanvas.height !== orbitHeight ){
        rendererOrbit.setSize(orbitWidth, orbitHeight, false);
        orbitCamera.aspect = orbitWidth / orbitHeight;
        orbitCamera.updateProjectionMatrix();
    }
    /* Telescopic renderer */
    const telWidth  = telescopicCanvas.clientWidth;
    const telHeight = telescopicCanvas.clientHeight;
    if( telescopicCanvas.width !== telWidth || telescopicCanvas.height !== telHeight ){
        rendererTelescopic.setSize(telWidth, telHeight, false);
        telescopicCamera.aspect = telWidth / telHeight;
        telescopicCamera.updateProjectionMatrix();
    }
}
window.addEventListener('resize', onWindowResize);

/********************************************************************
    MAIN ANIMATION LOOP
*********************************************************************/
function animate(){
    requestAnimationFrame(animate);
    rendererOrbit.render(orbitScene, orbitCamera);
    rendererTelescopic.render(telescopicScene, telescopicCamera);
}
update();       // initial state
animate();      // start loop
</script>
</body>
</html>
