<!-- p5.js CDN -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>

<style>
    *{box-sizing:border-box;font-family:Arial,Helvetica,sans-serif}
    body{margin:0;background:#fafafa;display:flex;flex-direction:column;align-items:center}
    #app-wrapper{width:100%;max-width:900px;display:flex;flex-direction:column;align-items:stretch;padding:10px}
    /* Control panel --------------------------------------------------------*/
    #control-panel{background:#eaeaea;border-radius:6px;padding:12px 18px;display:flex;flex-direction:column;gap:12px;margin-bottom:14px}
    .slider-group{display:flex;align-items:center;gap:10px}
