    canvas{background:#ffffff;border:1px solid #ccc;border-radius:4px}
</style>
</head>
<body>

<div id="app-wrapper">
    <!-- -------------------- Control Panel -------------------- -->
    <div id="control-panel">
        <!-- Mass slider -->
        <div class="slider-group">
            <label for="slider-m">m</label>
            <input id="slider-m" type="range" min="0" max="10" step="0.001" value="2.845">
            <span id="label-m">2.845</span>
        </div>
        <!-- Horizontal position slider -->
        <div class="slider-group">
            <label for="slider-x">x</label>
            <input id="slider-x" type="range" min="0" max="10" step="0.1" value="4.1">
            <span id="label-x">4.1</span>
        </div>
        <!-- Vertical position slider -->
        <div class="slider-group">
            <label for="slider-y">y</label>
            <input id="slider-y" type="range" min="0.1" max="5" step="0.001" value="1.498">
            <span id="label-y">1.498</span>
        </div>
    </div>

    <!-- ------------------ Visualization Area ------------------ -->
    <div id="visualization">
        <!-- p5.js canvas will be injected here -->
    </div>
</div>

<script>
// ---------- DOM Elements ----------
const sliderM = document.getElementById('slider-m');
const sliderX = document.getElementById('slider-x');
const sliderY = document.getElementById('slider-y');
const labelM  = document.getElementById('label-m');
const labelX  = document.getElementById('label-x');
const labelY  = document.getElementById('label-y');

// Update slider value displays ------------------------------------------------
function updateLabels(){
    labelM.textContent = Number(sliderM.value).toFixed(3);
    labelX.textContent = Number(sliderX.value).toFixed(1);
    labelY.textContent = Number(sliderY.value).toFixed(3);
}
[sliderM, sliderX, sliderY].forEach(slider=>{
    slider.addEventListener('input', updateLabels);
});
updateLabels(); // initial call

// -------------------- p5.js Sketch --------------------
let anchorLeftX = 100;
let anchorRightX = 700;
let anchorY = 150;
let scaleX = (anchorRightX - anchorLeftX) / 10; // logical 10 units horizontally
let scaleY = 50;                                 // logical 1 unit vertically -> 50px

function setup(){
    const cnv = createCanvas(800, 500);
    cnv.parent('visualization');
    textAlign(CENTER, CENTER);
}

function draw(){
    background(255);

    // ---------- Retrieve current values ----------
    const m = Number(sliderM.value);
    const xLogical = Number(sliderX.value);
    const yLogical = Number(sliderY.value);

    const g = 9.81;
    const L = 10;

    // Derived quantities ------------------------------------------------------
    const theta1 = Math.atan(yLogical / xLogical);      // radians
    const theta2 = Math.atan(yLogical / (L - xLogical)); // radians
    const Fg = m * g;
    const T1 = (Fg * Math.cos(theta2)) / Math.sin(theta1 + theta2);
    const T2 = (Fg * Math.cos(theta1)) / Math.sin(theta1 + theta2);

    // Pixel coordinates -------------------------------------------------------
    const massX = anchorLeftX + xLogical * scaleX;
    const massY = anchorY + yLogical * scaleY;

    // ------------------ Drawing ------------------
    // Posts
    stroke(0); strokeWeight(8);
    line(anchorLeftX, anchorY - 80, anchorLeftX, height);   // left post
    line(anchorRightX, anchorY - 80, anchorRightX, height); // right post

    // Horizontal reference dashed line
    strokeWeight(1);
    stroke(120); drawingContext.setLineDash([6, 6]);
    line(anchorLeftX, anchorY, anchorRightX, anchorY);
    drawingContext.setLineDash([]);

    // Ropes
    stroke(0); strokeWeight(3);
    line(anchorLeftX, anchorY, massX, massY);   // left rope
    line(anchorRightX, anchorY, massX, massY);  // right rope

    // Mass
    fill(0); noStroke();
    ellipse(massX, massY, 40, 40);

    // Angles arcs
    noFill(); stroke(70); strokeWeight(2);
    const arcRadius = 50;

    // θ₁ at left anchor: 0 to theta1
    arc(anchorLeftX, anchorY, arcRadius*2, arcRadius*2, 0, theta1);

    // θ₂ at right anchor: PI - theta2 to PI
    arc(anchorRightX, anchorY, arcRadius*2, arcRadius*2, Math.PI - theta2, Math.PI);

    // Labels and values -------------------------------------------------------
    fill(0); noStroke();
    textSize(14);

    // Tension labels near rope segments
    const midLeftX  = (anchorLeftX + massX)/2;
    const midLeftY  = (anchorY + massY)/2;
    const midRightX = (anchorRightX + massX)/2;
    const midRightY = (anchorY + massY)/2;
    text('T₁', midLeftX - 20, midLeftY - 10);
    text('T₂', midRightX + 20, midRightY - 10);

    // Angle labels
    text('θ₁', anchorLeftX + arcRadius + 15, anchorY - 10);
    text('θ₂', anchorRightX - arcRadius - 15, anchorY - 10);

    // Tension values at top
    textSize(18);
    const tensionText = `T₁ = ${T1.toFixed(4)} N   |   T₂ = ${T2.toFixed(4)} N`;
    text(tensionText, width / 2, 30);
}
</script>
</body>
</html>
