<script>
/* ---------------------------------------------------------------------
 * GLOBAL STATE VARIABLES
 * -------------------------------------------------------------------*/
let time = 0;                          // current simulation time [s]
let isRunning = false;                 // animation running?
let animationFrameId = null;
let lastTimestamp = null;              // for delta-time computation

/* Arrays for pre-computed analytical solution */
const T_MAX = 10;                      // seconds
const N_POINTS = 1001;                 // for plot curves
let timeArray = [];                    // t = 0 … 10
let xArray = [];                       // displacement solution
let vArray = [];                       // velocity solution

/* References to Plotly markers (trace indices 1) */
const DISP_DIV = 'plot-displacement';
const VEL_DIV  = 'plot-velocity';

/* ---------------------------------------------------------------------
 * HELPER FUNCTIONS
 * -------------------------------------------------------------------*/
/*  get current values from all sliders */
function readParameters(){
    return {
        x0 : parseFloat(document.getElementById('slider-displacement').value),
        v0 : parseFloat(document.getElementById('slider-velocity').value),
        k  : parseFloat(document.getElementById('slider-stiffness').value),
        m  : parseFloat(document.getElementById('slider-mass').value),
        c  : parseFloat(document.getElementById('slider-damping').value),
        Kp : parseFloat(document.getElementById('slider-proportional-gain').value),
        Kd : parseFloat(document.getElementById('slider-derivative-gain').value),
    };
}

/* analytical solution of second-order homogeneous ODE */
function computeSolution(){
    const p = readParameters();
    const k_eff = p.k + p.Kp;
    const c_eff = p.c + p.Kd;
    const omega_n = Math.sqrt(k_eff / p.m);
    const zeta = c_eff / (2*Math.sqrt(p.m * k_eff));

    timeArray = [];
    xArray = [];
    vArray = [];

    for(let i=0;i<N_POINTS;i++){
        const t = i*T_MAX/(N_POINTS-1);
        timeArray.push(t);

        let x,v;

        if(zeta < 1-1e-4){          /* UNDER-DAMPED */
            const omega_d = omega_n*Math.sqrt(1-zeta*zeta);
            const A = p.x0;
            const B = (p.v0 + zeta*omega_n*p.x0)/omega_d;
            const expTerm = Math.exp(-zeta*omega_n*t);
            x = expTerm*(A*Math.cos(omega_d*t)+B*Math.sin(omega_d*t));
            v = expTerm*(-A*omega_d*Math.sin(omega_d*t)+B*omega_d*Math.cos(omega_d*t))
                - zeta*omega_n*expTerm*(A*Math.cos(omega_d*t)+B*Math.sin(omega_d*t));
        }else if(Math.abs(zeta-1)<1e-4){     /* CRITICALLY DAMPED */
            const A = p.x0;
            const B = p.v0 + omega_n*p.x0;
            x = (A + B*t)*Math.exp(-omega_n*t);
            v = (B - omega_n*(A + B*t))*Math.exp(-omega_n*t);
        }else{                                /* OVER-DAMPED */
            const s1 = -omega_n*(zeta - Math.sqrt(zeta*zeta-1));
            const s2 = -omega_n*(zeta + Math.sqrt(zeta*zeta-1));
            const C1 = (p.v0 - s2*p.x0)/(s1 - s2);
            const C2 = p.x0 - C1;
            x = C1*Math.exp(s1*t) + C2*Math.exp(s2*t);
            v = C1*s1*Math.exp(s1*t) + C2*s2*Math.exp(s2*t);
        }

        xArray.push(x);
        vArray.push(v);
    }
}

/* redraw whole Plotly charts */
function drawPlots(){
    /* Displacement */
    const dispTrace = {
        x:timeArray,
        y:xArray,
        mode:'lines',
        line:{color:'black'}
    };
    const dispMarker = {
        x:[0],
        y:[xArray[0]],
        mode:'markers',
        marker:{color:'blue',size:8}
    };
    Plotly.react(DISP_DIV, [dispTrace,dispMarker], {
        margin:{l:40,r:10,b:30,t:10},
        xaxis:{range:[0,T_MAX],title:'Time (s)'},
        yaxis:{range:[-3,3],title:'Displacement (m)'},
        showlegend:false
    },{responsive:true});

    /* Velocity */
    const velTrace = {
        x:timeArray,
        y:vArray,
        mode:'lines',
        line:{color:'black'}
    };
    const velMarker = {
        x:[0],
        y:[vArray[0]],
        mode:'markers',
        marker:{color:'green',size:8}
    };
    Plotly.react(VEL_DIV, [velTrace,velMarker], {
        margin:{l:40,r:10,b:30,t:10},
        xaxis:{range:[0,T_MAX],title:'Time (s)'},
        yaxis:{range:[-3,3],title:'Velocity (m/s)'},
        showlegend:false
    },{responsive:true});
}

/* update only the moving markers */
function updateMarkers(t,x,v){
    Plotly.restyle(DISP_DIV,{x:[[t]],y:[[x]]},[1]);
    Plotly.restyle(VEL_DIV,{x:[[t]],y:[[v]]},[1]);
}

/* ---------------------------------------------------------------------
 * THREE.JS SETUP
 * -------------------------------------------------------------------*/
let scene, camera, renderer;
let massMesh, springMesh, baseMesh, enclosureMesh, damperHousing, damperPiston;
const SPRING_LENGTH = 4;       // nominal length

function initThree(){
    /* Scene & Camera */
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(45, window.innerWidth/window.innerHeight,0.1,100);
    camera.position.set(8,5,8);
    camera.lookAt(0,0,0);

    /* Renderer */
    renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setSize(document.getElementById('scene-container').clientWidth,
                     document.getElementById('scene-container').clientHeight);
    renderer.setPixelRatio(devicePixelRatio);
    document.getElementById('scene-container').appendChild(renderer.domElement);

    /* Lights */
    scene.add(new THREE.AmbientLight(0x999999));
    const dirLight = new THREE.DirectionalLight(0xffffff,0.8);
    dirLight.position.set(5,10,7);
    scene.add(dirLight);

    /* Base */
    const baseGeo = new THREE.BoxGeometry(8,0.2,4);
    const baseMat = new THREE.MeshStandardMaterial({color:0x2e7d32});
    baseMesh = new THREE.Mesh(baseGeo,baseMat);
    baseMesh.position.set(2, -1, 0);
    scene.add(baseMesh);

    /* Mass block */
    const massGeo = new THREE.BoxGeometry(1,1,1);
    const massMat = new THREE.MeshStandardMaterial({color:0xffeb3b});
    massMesh = new THREE.Mesh(massGeo,massMat);
    massMesh.position.set(0,0,0);
    scene.add(massMesh);

    /* Simple spring (cylinder) */
    const springGeo = new THREE.CylinderGeometry(0.1,0.1,SPRING_LENGTH,12,1,true);
    const springMat = new THREE.MeshStandardMaterial({color:0xd32f2f});
    springMesh = new THREE.Mesh(springGeo,springMat);
    springMesh.rotation.z = Math.PI/2;
    springMesh.position.set(SPRING_LENGTH/2,0,0);
    scene.add(springMesh);

    /* Damper (housing + piston) */
    const housingGeo = new THREE.CylinderGeometry(0.25,0.25,SPRING_LENGTH/2,12);
    const housingMat = new THREE.MeshStandardMaterial({color:0x1976d2});
    damperHousing = new THREE.Mesh(housingGeo,housingMat);
    damperHousing.rotation.z = Math.PI/2;
    damperHousing.position.set(SPRING_LENGTH+1,0,0);
    scene.add(damperHousing);

    const pistonGeo = new THREE.CylinderGeometry(0.15,0.15,SPRING_LENGTH/3,12);
    const pistonMat = new THREE.MeshStandardMaterial({color:0x64b5f6});
    damperPiston = new THREE.Mesh(pistonGeo,pistonMat);
    damperPiston.rotation.z = Math.PI/2;
    damperPiston.position.set(SPRING_LENGTH/2,0,0);
    scene.add(damperPiston);

    /* Wireframe enclosure */
    const boxGeo = new THREE.BoxGeometry(10,4,4);
    const wireMat = new THREE.MeshBasicMaterial({color:0xffffff,wireframe:true,transparent:true,opacity:0.2});
    enclosureMesh = new THREE.Mesh(boxGeo,wireMat);
    enclosureMesh.position.set(3,0,0);
    scene.add(enclosureMesh);

    window.addEventListener('resize', onWindowResize);
    renderer.render(scene,camera);
}
function onWindowResize(){
    const cDiv = document.getElementById('scene-container');
    camera.aspect = cDiv.clientWidth / cDiv.clientHeight;
    camera.updateProjectionMatrix();
    renderer.setSize(cDiv.clientWidth, cDiv.clientHeight);
}

/* move 3D elements according to displacement x */
function updateScene(x){
    massMesh.position.x = x;
    springMesh.scale.set(1, (SPRING_LENGTH + x)/SPRING_LENGTH, 1);
    springMesh.position.x = (SPRING_LENGTH + x)/2;

    damperPiston.scale.set(1,(SPRING_LENGTH/3 + x/2)/(SPRING_LENGTH/3),1);
    damperPiston.position.x = SPRING_LENGTH + x/2;
}

/* ---------------------------------------------------------------------
 * ANIMATION LOOP
 * -------------------------------------------------------------------*/
function animate(timestamp){
    if(!isRunning){return;}
    if(!lastTimestamp){lastTimestamp = timestamp;}
    const dt = (timestamp - lastTimestamp)/1000;   // seconds
    lastTimestamp = timestamp;
    time += dt;

    if(time > T_MAX){
        pauseAnimation();
        time = T_MAX;
    }

    /* interpolation for x and v at current time */
    const idx = Math.min(Math.floor(time/T_MAX*(N_POINTS-1)),N_POINTS-2);
    const t0 = timeArray[idx], t1 = timeArray[idx+1];
    const alpha = (time - t0)/(t1 - t0);
    const x = xArray[idx] + alpha*(xArray[idx+1]-xArray[idx]);
    const v = vArray[idx] + alpha*(vArray[idx+1]-vArray[idx]);

    updateMarkers(time,x,v);
    updateScene(x);
    renderer.render(scene,camera);

    animationFrameId = requestAnimationFrame(animate);
}

/* control helpers */
function startAnimation(){
    if(isRunning) return;
    isRunning = true;
    lastTimestamp = null;
    animationFrameId = requestAnimationFrame(animate);
}
function pauseAnimation(){
    isRunning = false;
    if(animationFrameId) cancelAnimationFrame(animationFrameId);
}
function resetAnimation(){
    pauseAnimation();
    time = 0;
    updateMarkers(0,xArray[0],vArray[0]);
    updateScene(xArray[0]);
    renderer.render(scene,camera);
}

/* ---------------------------------------------------------------------
 * UI EVENT LISTENERS
 * -------------------------------------------------------------------*/
function setupUI(){
    /* update span text and add listener to every range input */
    document.querySelectorAll('#controls-container input[type="range"]').forEach(slider=>{
        const span = document.getElementById('value-'+slider.id.split('slider-')[1]);
        /* initial display */
        span.textContent = parseFloat(slider.value).toFixed( slider.step < 0.1 ? 2 : 1 );

        slider.addEventListener('input', ()=>{
            span.textContent = parseFloat(slider.value).toFixed( slider.step < 0.1 ? 2 : 1 );
            pauseAnimation();
            time = 0;
            computeSolution();
            drawPlots();
            updateScene(xArray[0]);
        });
    });

    /* buttons */
    document.getElementById('btn-play').addEventListener('click', startAnimation);
    document.getElementById('btn-pause').addEventListener('click', pauseAnimation);
    document.getElementById('btn-reset').addEventListener('click', resetAnimation);
}

/* ---------------------------------------------------------------------
 * INITIALISE EVERYTHING
 * -------------------------------------------------------------------*/
initThree();
computeSolution();
drawPlots();
setupUI();
updateScene(xArray[0]);
</script>
</body>
</html>
