        height:100%;
        overflow:hidden;
    }
    #main-animation-container{
        flex:0 0 33%;
        min-height:200px;
    }
    #displacement-plot-container,
    #velocity-plot-container{
        flex:0 0 33%;
        min-height:200px;
    }
</style>
</head>
<body>

<div id="main-container">

    <!-- ============  CONTROL PANEL  ============ -->
    <div id="control-panel">
        <h4>tension T</h4>
        <input type="range" id="slider-tension" min="1" max="100" step="1" value="50">

        <h4>displacement</h4>
        <label for="slider-x1">x₁</label>
        <input type="range" id="slider-x1" min="-0.5" max="0.5" step="0.01" value="0.1">

        <label for="slider-x2">x₂</label>
        <input type="range" id="slider-x2" min="-0.5" max="0.5" step="0.01" value="0.1">

        <label for="slider-x3">x₃</label>
        <input type="range" id="slider-x3" min="-0.5" max="0.5" step="0.01" value="0.0">

        <h4>mass</h4>
        <label for="slider-m1">m₁</label>
        <input type="range" id="slider-m1" min="0.1" max="2.0" step="0.05" value="1.0">

        <label for="slider-m2">m₂</label>
        <input type="range" id="slider-m2" min="0.1" max="2.0" step="0.05" value="1.0">

        <label for="slider-m3">m₃</label>
        <input type="range" id="slider-m3" min="0.1" max="2.0" step="0.05" value="1.0">

        <h4>release system</h4>
        <div id="animation-controls">
            <button id="btn-play-pause">▶</button>
            <button id="btn-reset">⏮</button>
        </div>
    </div>

    <!-- ============  VISUALIZATION AREA  ============ -->
    <div id="visualization-area">
        <div id="main-animation-container"></div>
        <div id="displacement-plot-container"></div>
        <div id="velocity-plot-container"></div>
    </div>
</div>

<!-- =============  JavaScript  ============= -->
<script>
/* ---------- Global state ---------- */
let params = {};
let eigen = {};
let precomputed = {t:[], y:[[],[],[]], v:[[],[],[]]};
let isAnimating = false;
let tSim = 0;
const tMax = 5;
const dtPlot = 0.01;        // time resolution for plots
const dtAnim = 1/60;        // p5 draw step
let sketchInstance;

/* ---------- Utility helpers ---------- */
function getSliderVal(id){ return parseFloat(document.getElementById(id).value); }

function buildMatrices(){
    const m1=getSliderVal('slider-m1'), m2=getSliderVal('slider-m2'), m3=getSliderVal('slider-m3');
    const T=getSliderVal('slider-tension');
    params = {T,m1,m2,m3,
              x1:getSliderVal('slider-x1'),
              x2:getSliderVal('slider-x2'),
              x3:getSliderVal('slider-x3')};

    const M=[[m1,0,0],[0,m2,0],[0,0,m3]];
    const K=numeric.mul(params.T, [[2,-1,0],[-1,2,-1],[0,-1,2]]);

    // M^{-1/2}
    const MinvS=[[1/Math.sqrt(m1),0,0],[0,1/Math.sqrt(m2),0],[0,0,1/Math.sqrt(m3)]];
    // C = MinvS * K * MinvS
    const C = numeric.dot(numeric.dot(MinvS, K), MinvS);

    // eigenvalue problem C*z = λ*z
    const eig = numeric.eig(C);
    const lambda = eig.lambda.x;               // real parts
    const Z = eig.E.x;                         // eigenvectors columns

    // Modes in original coordinates: A = MinvS * Z
    const A = numeric.dot(MinvS, Z);           // 3x3

    // angular frequencies
    const omega = lambda.map(l => Math.sqrt(Math.abs(l)));

    eigen = {omega, modes:A};
}

function computeModalCoefficients(){
    const y0 = [params.x1, params.x2, params.x3];
    // Solve A * c = y0
    const c = numeric.solve(eigen.modes, y0);
    eigen.c = c;
}

/* displacement vector at time t */
function displacementAt(t){
    const y=[0,0,0], v=[0,0,0];
    for(let i=0;i<3;i++){
        const w = eigen.omega[i];
        const ci = eigen.c[i];
        const coswt = Math.cos(w*t);
        const sinwt = Math.sin(w*t);
        for(let j=0;j<3;j++){
            y[j] += ci*eigen.modes[j][i]*coswt;
            v[j] += -ci*eigen.modes[j][i]*w*sinwt;
        }
    }
    return {y,v};
}

function precomputeCurves(){
    const N = Math.round(tMax/dtPlot)+1;
    precomputed.t = new Array(N);
    precomputed.y = [new Array(N), new Array(N), new Array(N)];
    precomputed.v = [new Array(N), new Array(N), new Array(N)];

    for(let k=0;k<N;k++){
        const t = k*dtPlot;
        const res = displacementAt(t);
        precomputed.t[k]=t;
        for(let j=0;j<3;j++){
            precomputed.y[j][k]=res.y[j];
            precomputed.v[j][k]=res.v[j];
        }
    }
}

/* ---------- Plotly helpers ---------- */
function makePlots(){
    const colors=['blue','orange','green'];
    const yTraces = [0,1,2].map(j=>({
        x:precomputed.t, y:precomputed.y[j],
        mode:'lines', name:`y${j+1}`, line:{color:colors[j]}
    }));
    const vTraces = [0,1,2].map(j=>({
        x:precomputed.t, y:precomputed.v[j],
        mode:'lines', name:`v${j+1}`, line:{color:colors[j]}
    }));

    const shapeLine = (t)=>({
        type:'line', x0:t, x1:t, y0: -1e9, y1:1e9,
        line:{color:'red', width:2}
    });

    Plotly.newPlot('displacement-plot-container', yTraces, {
        title:'displacement', xaxis:{title:'time t',range:[0,tMax]},
        shapes:[shapeLine(0)]
    },{responsive:true});

    Plotly.newPlot('velocity-plot-container', vTraces, {
        title:'velocity', xaxis:{title:'time t',range:[0,tMax]},
        shapes:[shapeLine(0)]
    },{responsive:true});
}

function updateTimeMarker(t){
    ['displacement-plot-container','velocity-plot-container'].forEach(id=>{
        Plotly.relayout(id, {'shapes[0].x0':t,'shapes[0].x1':t});
    });
}

/* ---------- p5.js sketch ---------- */
function createSketch(){
    return function(p){
        const margin=50;
        let w,h,scaleX,scaleY,centerY;
        p.setup=()=> {
            const parent = document.getElementById('main-animation-container');
            w = parent.clientWidth;
            h = parent.clientHeight;
            p.createCanvas(w,h).parent(parent);
            p.noLoop();   // start paused
            computeTransform();
        };
        function computeTransform(){
            scaleX = (w-2*margin)/4;
            scaleY = (h-2*margin)/2;
            centerY = h/2;
        }
        p.windowResized=()=>{
            const parent = document.getElementById('main-animation-container');
            w = parent.clientWidth;
            h = parent.clientHeight;
            p.resizeCanvas(w,h);
            computeTransform();
            p.redraw();
        };
        function toScreenX(x){ return margin + x*scaleX; }
        function toScreenY(y){ return centerY - y*scaleY; }

        p.draw=()=> {
            p.background(255);
            // axes
            p.stroke(200);
            for(let i=0;i<=4;i++){
                p.line(toScreenX(i),margin,toScreenX(i),h-margin); // vertical grid
            }
            p.line(margin,centerY,w-margin,centerY); // horizontal axis

            // current displacement
            const res = displacementAt(tSim);
            const yy = res.y;
            const masses=[params.m1,params.m2,params.m3];

            p.stroke(0); p.strokeWeight(2);
            p.noFill();
            p.beginShape();
            p.vertex(toScreenX(0), toScreenY(0));
            p.vertex(toScreenX(1), toScreenY(yy[0]));
            p.vertex(toScreenX(2), toScreenY(yy[1]));
            p.vertex(toScreenX(3), toScreenY(yy[2]));
            p.vertex(toScreenX(4), toScreenY(0));
            p.endShape();

            // masses
            p.fill('red'); p.noStroke();
            const k=20;
            for(let i=0;i<3;i++){
                const r = k*Math.sqrt(masses[i]);
                p.circle(toScreenX(i+1), toScreenY(yy[i]), r);
            }

            // advance time & update plots
            if(isAnimating){
                tSim += dtAnim;
                if(tSim>tMax){ pauseAnimation(); }
                updateTimeMarker(tSim);
            }
        };
    };
}

/* ---------- Controls & Events ---------- */
function pauseAnimation(){
    isAnimating=false;
    document.getElementById('btn-play-pause').textContent='▶';
    if(sketchInstance) sketchInstance.noLoop();
}

function playAnimation(){
    if(tSim>=tMax) tSim=0;   // restart when at end
    isAnimating=true;
    document.getElementById('btn-play-pause').textContent='❚❚';
    if(sketchInstance) sketchInstance.loop();
}

function togglePlayPause(){
    (isAnimating)? pauseAnimation() : playAnimation();
}

function resetSimulation(){
    pauseAnimation();
    buildMatrices();
    computeModalCoefficients();
    precomputeCurves();
    makePlots();

    tSim=0;
    updateTimeMarker(0);
    if(sketchInstance){
        sketchInstance.redraw();
    }
}

/* ---------- Set up everything ---------- */
window.addEventListener('DOMContentLoaded',()=>{
    // p5 sketch
    sketchInstance = new p5(createSketch());

    // slider events
    ['slider-tension','slider-x1','slider-x2','slider-x3',
     'slider-m1','slider-m2','slider-m3'].forEach(id=>{
        document.getElementById(id).addEventListener('input', resetSimulation);
    });

    // buttons
    document.getElementById('btn-play-pause').addEventListener('click',togglePlayPause);
    document.getElementById('btn-reset').addEventListener('click',resetSimulation);

    // initial
    resetSimulation();
});
</script>

</body>
</html>
