</style>
</head>
<body>
<div id="app-container">

    <!-- Header controls -->
    <div id="header-controls">
        <button id="btn-move" class="active">move the points</button>
        <button id="btn-start-algo">start the algorithm</button>
    </div>

    <!-- Algorithm step controls -->
    <div id="step-controls">
        <!-- Step 1 -->
        <div class="step-group">
            <button disabled>step 1</button>
            <button id="btn-step1-explain" disabled>explain</button>
            <button id="btn-step1-do"      disabled>do it</button>
        </div>

        <!-- Step 2 -->
        <div class="step-group">
            <button disabled>step 2</button>
            <button id="btn-step2-explain" disabled>explain</button>
            <button id="btn-step2-do"      disabled>do it</button>
        </div>

        <!-- Step 3 -->
        <div class="step-group" id="step3-controls-container">
            <button disabled>step 3</button>
            <button id="btn-step3-explain" disabled>explain</button>
            <div id="step3-actions" style="display:inline-block;"></div>
        </div>
    </div>

    <!-- p5.js canvas goes here -->
    <div id="canvas-container"></div>

    <!-- Explanation panel -->
    <div id="explanation-panel">
        <p id="explanation-text"></p>
    </div>
</div>

<script>
/* -----------------------------------------------------------
   GLOBAL STATE
----------------------------------------------------------- */
let mode = 'move';  // 'move' | 'algorithm'
let points = [
    {x:150, y:350},
    {x:250, y:150},
    {x:350, y:250},
    {x:550, y:180},
    {x:480, y:320},
    {x:420, y:400}
];
let draggedPoint = null;

let algorithmState = {};

function defaultAlgorithmState(){
    return {
        step:0,
        hullPointsIndices:[],
        fullHull:[],
        leftmostPointIndex:null,
        currentPointIndex:null,
        candidateLines:[],
        explanationSteps:[]
    };
}
algorithmState = defaultAlgorithmState();

/* -----------------------------------------------------------
   HELPER FUNCTIONS
----------------------------------------------------------- */

function enableBtn(id, enabled){
    const el = document.getElementById(id);
    if(el) el.disabled = !enabled;
}

function appendExplanation(str){
    algorithmState.explanationSteps.push(str);
    document.getElementById('explanation-text').innerHTML = algorithmState.explanationSteps.join('<br>');
}

/* ---------- Geometry helpers ---------- */
function orientation(p, q, r){
    // cross product (q-p) x (r-p)
    return (q.x-p.x)*(r.y-p.y) - (q.y-p.y)*(r.x-p.x);
}
function distSq(p,q){
    let dx=p.x-q.x, dy=p.y-q.y;
    return dx*dx+dy*dy;
}

function jarvisMarch(pointsArr){
    const n = pointsArr.length;
    if(n<3) return [...Array(n).keys()];
    // find leftmost (min x, then min y)
    let left = 0;
    for(let i=1;i<n;i++){
        if(pointsArr[i].x < pointsArr[left].x ||
          (pointsArr[i].x === pointsArr[left].x && pointsArr[i].y < pointsArr[left].y)){
              left = i;
        }
    }
    const hull = [];
    let p = left;
    do{
        hull.push(p);
        let q = (p+1)%n;
        for(let r=0;r<n;r++){
            if(r===p) continue;
            const o = orientation(pointsArr[p], pointsArr[q], pointsArr[r]);
            if(o<0 || (o===0 && distSq(pointsArr[p], pointsArr[r]) > distSq(pointsArr[p], pointsArr[q]))){
                // r is more clockwise
                q = r;
            }
        }
        p = q;
    }while(p !== left);
    return hull; // clockwise order
}

/* -----------------------------------------------------------
   p5.js  SKETCH
----------------------------------------------------------- */
const sketch = (p)=>{
    p.setup = function(){
        const canvas = p.createCanvas(700,500);
        canvas.parent('canvas-container');
    };

    p.draw = function(){
        p.background(255);

        // board outline for clarity
        p.noFill();
        p.stroke(220);
        p.rect(0,0,p.width-1,p.height-1);

        // Candidate lines
        p.stroke(60,150,140);
        p.strokeWeight(2);
        algorithmState.candidateLines.forEach(pair=>{
            const a = points[pair[0]];
            const b = points[pair[1]];
            p.line(a.x,a.y,b.x,b.y);
        });

        // Hull edges so far
        p.stroke(45,60,110);
        p.strokeWeight(3);
        for(let i=0;i<algorithmState.hullPointsIndices.length-1;i++){
            const a = points[algorithmState.hullPointsIndices[i]];
            const b = points[algorithmState.hullPointsIndices[i+1]];
            p.line(a.x,a.y,b.x,b.y);
        }

        // If full hull completed, draw closing edge
        if(mode==='algorithm' && algorithmState.hullPointsIndices.length>2){
            const first = points[algorithmState.hullPointsIndices[0]];
            const last  = points[algorithmState.hullPointsIndices[algorithmState.hullPointsIndices.length-1]];
            if(last!==first && algorithmState.hullCompleted){
                p.line(first.x,first.y,last.x,last.y);
            }
        }

        // Draw points
        for(let i=0;i<points.length;i++){
            const pt = points[i];
            // check if hull point
            if(algorithmState.hullPointsIndices.includes(i)){
                // hull style
                p.fill(45,60,110);
            }else{
                // default style
                p.fill(200,220,100);
            }
            p.stroke(0);
            p.strokeWeight(1);
            p.ellipse(pt.x, pt.y, 12, 12);

            // crosshair
            p.line(pt.x-3, pt.y, pt.x+3, pt.y);
            p.line(pt.x, pt.y-3, pt.x, pt.y+3);
        }
    };

    /* ------------- Mouse interaction for MOVE mode ------------- */

    p.mousePressed = function(){
        if(mode!=='move') return;
        // find nearest point within radius
        for(let i=0;i<points.length;i++){
            const d = p.dist(p.mouseX,p.mouseY, points[i].x, points[i].y);
            if(d<8){
                draggedPoint = i;
                break;
            }
        }
    };

    p.mouseDragged = function(){
        if(mode!=='move' || draggedPoint===null) return;
        points[draggedPoint].x = p.constrain(p.mouseX,0,p.width);
        points[draggedPoint].y = p.constrain(p.mouseY,0,p.height);
    };

    p.mouseReleased = function(){
        draggedPoint = null;
    };
};
new p5(sketch);

/* -----------------------------------------------------------
   CONTROL HANDLERS
----------------------------------------------------------- */

// Header buttons
document.getElementById('btn-move').addEventListener('click', ()=>{
    if(mode==='move') return;
    mode = 'move';
    document.getElementById('btn-move').classList.add('active');
    document.getElementById('btn-start-algo').classList.remove('active');

    // Hide step controls
    document.getElementById('step-controls').style.display='none';

    // reset algorithm state
    algorithmState = defaultAlgorithmState();
    document.getElementById('explanation-text').innerHTML='';
});

document.getElementById('btn-start-algo').addEventListener('click', ()=>{
    if(mode==='algorithm') return;
    mode='algorithm';
    document.getElementById('btn-start-algo').classList.add('active');
    document.getElementById('btn-move').classList.remove('active');

    // reset and pre-compute hull
    algorithmState = defaultAlgorithmState();
    algorithmState.fullHull = jarvisMarch(points);

    // show step controls
    document.getElementById('step-controls').style.display='flex';

    // Step1 buttons enabled
    enableBtn('btn-step1-explain', true);
    enableBtn('btn-step1-do', true);

    // Reset others
    ['btn-step2-explain','btn-step2-do','btn-step3-explain'].forEach(id=>enableBtn(id,false));

    // build dynamic step3 buttons
    const ctn = document.getElementById('step3-actions');
    ctn.innerHTML=''; // clear

    const first = document.createElement('button');
    first.id='btn-step3-do';
    first.textContent='do it';
    first.disabled=true;
    ctn.appendChild(first);

    // repeats
    for(let i=1;i<=algorithmState.fullHull.length-2;i++){
        const rep = document.createElement('button');
        rep.id='btn-step3-repeat-'+i;
        rep.textContent='repeat';
        rep.disabled=true;
        ctn.appendChild(rep);
    }
});

// Step 1
document.getElementById('btn-step1-explain').addEventListener('click', ()=>{
    appendExplanation('Step&nbsp;1. Find the leftmost point.');
});
document.getElementById('btn-step1-do').addEventListener('click', ()=>{
    // find leftmost
    let left=0;
    for(let i=1;i<points.length;i++){
        if(points[i].x<points[left].x || (points[i].x===points[left].x && points[i].y<points[left].y)){
            left=i;
        }
    }
    algorithmState.leftmostPointIndex = left;
    algorithmState.currentPointIndex = left;
    algorithmState.hullPointsIndices.push(left);

    // disable step1 buttons
    enableBtn('btn-step1-explain',false);
    enableBtn('btn-step1-do',false);

    // enable step2 buttons
    enableBtn('btn-step2-explain',true);
    enableBtn('btn-step2-do',true);
});

// Step 2
document.getElementById('btn-step2-explain').addEventListener('click', ()=>{
    appendExplanation('Step&nbsp;2. From that point, find the point clockwise furthest to the right.');
});

document.getElementById('btn-step2-do').addEventListener('click', ()=>{
    const cur = algorithmState.currentPointIndex;

    // candidate lines
    algorithmState.candidateLines = [];
    for(let i=0;i<points.length;i++){
        if(i===cur) continue;
        algorithmState.candidateLines.push([cur,i]);
    }

    // After short delay, commit the second hull point
    setTimeout(()=>{
        const secondIdx = algorithmState.fullHull[1];
        algorithmState.hullPointsIndices.push(secondIdx);
        algorithmState.currentPointIndex = secondIdx;
        algorithmState.candidateLines = [];

        // disable step2 buttons
        enableBtn('btn-step2-explain',false);
        enableBtn('btn-step2-do',false);

        // enable step3 controls
        enableBtn('btn-step3-explain',true);
        enableBtn('btn-step3-do',true);
    }, 500);
});

// Step 3 explanation
document.getElementById('btn-step3-explain').addEventListener('click', ()=>{
    appendExplanation('Step&nbsp;3. Repeat the search for the next hull point until we return to the start.');
});

/* Delegate clicks for dynamic step3 buttons */
document.getElementById('step3-actions').addEventListener('click', (e)=>{
    if(e.target.tagName!=='BUTTON' || e.target.disabled) return;
    handleStep3Action(e.target);
});

function handleStep3Action(btn){
    const nextHullIdx = algorithmState.hullPointsIndices.length; // which index in fullHull to consume
    if(nextHullIdx >= algorithmState.fullHull.length){
        return;
    }
    const cur = algorithmState.currentPointIndex;
    const next = algorithmState.fullHull[nextHullIdx];

    // build candidate lines list (skip those already on hull)
    algorithmState.candidateLines = [];
    const already = new Set(algorithmState.hullPointsIndices);
    for(let i=0;i<points.length;i++){
        if(already.has(i) || i===cur) continue;
        algorithmState.candidateLines.push([cur,i]);
    }

    // small delay then commit
    setTimeout(()=>{
        algorithmState.candidateLines = [];
        algorithmState.hullPointsIndices.push(next);
        algorithmState.currentPointIndex = next;

        // close hull when finished
        if(algorithmState.hullPointsIndices.length === algorithmState.fullHull.length){
            algorithmState.hullCompleted = true;
        }

        // disable this button, enable next (if any)
        btn.disabled = true;
        const parts = btn.id.split('-');
        const index = parts[parts.length-1]; // could be 'do' or number
        if(btn.id==='btn-step3-do'){
            // enable repeat-1 if exists
            const nbtn = document.getElementById('btn-step3-repeat-1');
            if(nbtn) nbtn.disabled = false;
        }else{
            const nextIdx = parseInt(index)+1;
            const nbtn = document.getElementById('btn-step3-repeat-'+nextIdx);
            if(nbtn){
                nbtn.disabled = false;
            }
        }
    }, 500);
}
</script>
</body>
</html>
