<body>
<div id="main-container">

    <!-- ============ CONTROL PANEL ============ -->
    <div id="controls">
        <h2>Parameters</h2>

        <details>
            <summary>initial conditions</summary>
            <!-- Contents intentionally left blank -->
        </details>

        <!-- Slider: alpha max -->
        <div class="control-group">
            <label for="slider-alpha-max">
                maximum fraction of respiring biomass
                <span id="val-alpha-max" class="value-span"></span>
            </label>
            <input id="slider-alpha-max" type="range" min="0.1" max="1.0" step="0.01" value="0.3">
        </div>

        <!-- Slider: t-min (disabled) -->
        <div class="control-group">
            <label for="slider-t-min">
                run time minimum (h)
                <span id="val-t-min" class="value-span"></span>
            </label>
            <input id="slider-t-min" type="range" min="0" max="0" step="1" value="0" disabled>
        </div>

        <!-- Slider: t-max -->
        <div class="control-group">
            <label for="slider-t-max">
                run time maximum (h)
                <span id="val-t-max" class="value-span"></span>
            </label>
            <input id="slider-t-max" type="range" min="10" max="100" step="1" value="60">
        </div>

        <!-- Slider: enzyme control -->
        <div class="control-group">
            <label for="slider-enzyme-control">
                intermediate enzyme control [mol/m³]
                <span id="val-enzyme-control" class="value-span"></span>
            </label>
            <input id="slider-enzyme-control" type="range" min="0.1" max="5.0" step="0.01" value="1">
        </div>

        <!-- Slider: feed rate -->
        <div class="control-group">
            <label for="slider-feed-rate">
                feed flow rate (m³/h)
                <span id="val-feed-rate" class="value-span"></span>
            </label>
            <input id="slider-feed-rate" type="range" min="0.0" max="2.0" step="0.01" value="0.2">
        </div>

        <!-- Slider: substrate feed concentration -->
        <div class="control-group">
            <label for="slider-substrate-feed-conc">
                substrate feed concentration [mol/m³]
                <span id="val-substrate-feed-conc" class="value-span"></span>
            </label>
            <input id="slider-substrate-feed-conc" type="range" min="10" max="200" step="1" value="100">
        </div>

        <!-- Slider: feed time -->
        <div class="control-group">
            <label for="slider-feed-time">
                feed time (h)
                <span id="val-feed-time" class="value-span"></span>
            </label>
            <input id="slider-feed-time" type="range" min="0" max="60" step="1" value="35">
        </div>

        <!-- Plot selector -->
        <div class="control-group">
            <label for="select-plot">plot</label>
            <select id="select-plot">
                <option value="fed batch" selected>fed batch</option>
                <option value="α">α</option>
            </select>
        </div>
    </div>

    <!-- ============ VISUALIZATION AREA ============ -->
    <div id="visualization">
        <div id="plot-top"></div>
        <div id="plot-bottom"></div>
    </div>
</div>

<script>
/* ========= GLOBAL STATE ========= */
const sliders = [
    "slider-alpha-max",
    "slider-t-min",
    "slider-t-max",
    "slider-enzyme-control",
    "slider-feed-rate",
    "slider-substrate-feed-conc",
    "slider-feed-time"
];
let simData = null; // will hold latest simulation results

/* ========= UTILITY FUNCTIONS ========= */
function $(id){ return document.getElementById(id); }

function updateValueDisplays(){
    $("val-alpha-max").textContent      = +$("slider-alpha-max").value;
    $("val-t-min").textContent          = +$("slider-t-min").value;
    $("val-t-max").textContent          = +$("slider-t-max").value;
    $("val-enzyme-control").textContent = +$("slider-enzyme-control").value;
    $("val-feed-rate").textContent      = +$("slider-feed-rate").value;
    $("val-substrate-feed-conc").textContent = +$("slider-substrate-feed-conc").value;
    $("val-feed-time").textContent      = +$("slider-feed-time").value;
}

/* ========= ODE SYSTEM ========= */
function alphaCalc(glu, alpha_max, K_I, n){
    let num = alpha_max * Math.pow(K_I, n);
    let den = Math.pow(K_I, n) + Math.pow(Math.max(glu,0), n);
    return den===0 ? 0 : num/den;
}

function derivs(t, state, p){
    // state: [X, Glu, Eta, E0, E1, E2, V]
    const [
        X, Glu, Eta, E0, E1, E2, V
    ] = state;

    const {
        alpha_max, K_I, n, feedRate, G_in, t_feed,
        mu_max, Ks, Yxg, k_E1_syn, k_E1_deg,
        k_E2_syn, k_E2_deg, Yeta, mu_eta
    } = p;

    // compute dynamic quantities
    const F = (t <= t_feed) ? feedRate : 0;      // m³/h
    const D = (V>0) ? (F / V) : 0;               // dilution rate
    const alpha = alphaCalc(Glu, alpha_max, K_I, n);

    // Specific growth rate (simple Monod)
    const mu = mu_max * Glu / (Ks + Glu);

    // Biomass derivative
    const dXdt = (mu * X) - D * X;

    // Glucose consumption rate (positive = consumption)
    const r_G = mu * X / Yxg;

    // Glucose derivative
    const dGludt = -r_G - D * Glu + D * G_in;

    // Ethanol production mostly from fermentation (1-alpha)
    const r_Eta_prod = Yeta * mu * X * (1 - alpha);
    const dEtadt = r_Eta_prod - D * Eta;

    // Enzyme E1 (fermentative) induced by glucose
    const dE1dt = k_E1_syn * (Glu / (Ks + Glu)) - k_E1_deg * E1;

    // Enzyme E2 (respirative) repressed by glucose
    const dE2dt = k_E2_syn * (K_I / (K_I + Glu)) - k_E2_deg * E2;

    // E0 constitutive (no change)
    const dE0dt = 0;

    // Volume change
    const dVdt = F;

    return [dXdt, dGludt, dEtadt, dE0dt, dE1dt, dE2dt, dVdt];
}

/* RK4 single step */
function rk4(state, t, dt, p){
    const f = (tt, yy)=>derivs(tt, yy, p);

    const k1 = f(t, state);
    const k2 = f(t+dt/2, state.map((y,i)=>y+dt*k1[i]/2));
    const k3 = f(t+dt/2, state.map((y,i)=>y+dt*k2[i]/2));
    const k4 = f(t+dt,   state.map((y,i)=>y+dt*k3[i]));

    return state.map((y,i)=> y + dt/6*(k1[i]+2*k2[i]+2*k3[i]+k4[i]));
}

/* ========= SIMULATION ========= */
function runSimulation(){
    // Read UI values
    const alpha_max = +$("slider-alpha-max").value;
    const t_min     = +$("slider-t-min").value; // always zero
    const t_max     = +$("slider-t-max").value;
    const K_I       = +$("slider-enzyme-control").value;
    const feedRate  = +$("slider-feed-rate").value;
    const G_in      = +$("slider-substrate-feed-conc").value;
    const t_feed    = +$("slider-feed-time").value;

    // ODE parameters
    const p = {
        alpha_max,
        K_I,
        n:4,
        feedRate,
        G_in,
        t_feed,
        mu_max:0.4,
        Ks:0.5,
        Yxg:0.5,
        Yeta:0.2,
        mu_eta:0.2,
        k_E1_syn:0.1,
        k_E1_deg:0.01,
        k_E2_syn:0.1,
        k_E2_deg:0.01
    };

    // Initial state [X, Glu, Eta, E0, E1, E2, V]
    let state = [0.1, 60, 0, 1.0, 0.0, 0.0, 1.0];

    const dt = 0.1; // h
    const steps = Math.ceil((t_max - t_min)/dt);

    // Arrays to store results
    const t_arr   = new Array(steps+1).fill(0);
    const X_arr   = new Array(steps+1).fill(0);
    const Glu_arr = new Array(steps+1).fill(0);
    const Eta_arr = new Array(steps+1).fill(0);
    const E0_arr  = new Array(steps+1).fill(0);
    const E1_arr  = new Array(steps+1).fill(0);
    const E2_arr  = new Array(steps+1).fill(0);
    const a_arr   = new Array(steps+1).fill(0);

    // store initial values
    t_arr[0]   = t_min;
    X_arr[0]   = state[0];
    Glu_arr[0] = state[1];
    Eta_arr[0] = state[2];
    E0_arr[0]  = state[3];
    E1_arr[0]  = state[4];
    E2_arr[0]  = state[5];
    a_arr[0]   = alphaCalc(state[1], alpha_max, K_I, 4);

    // integrate
    let t = t_min;
    for(let i=1;i<=steps;i++){
        state = rk4(state, t, dt, p);
        t += dt;

        t_arr[i]   = t;
        X_arr[i]   = state[0];
        Glu_arr[i] = state[1];
        Eta_arr[i] = state[2];
        E0_arr[i]  = state[3];
        E1_arr[i]  = state[4];
        E2_arr[i]  = state[5];
        a_arr[i]   = alphaCalc(state[1], alpha_max, K_I, 4);
    }

    // Save to global
    simData = {
        t: t_arr,
        X: X_arr,
        Glu: Glu_arr,
        Eta: Eta_arr,
        E0: E0_arr,
        E1: E1_arr,
        E2: E2_arr,
        a:  a_arr
    };
}

/* ========= PLOTTING ========= */
function drawPlots(){
    if(!simData) return;

    const plotChoice = $("select-plot").value;

    if(plotChoice === "fed batch"){
        // ----------- TOP PLOT -----------
        const topData = [
            {x:simData.t, y:simData.X,  name:'X(t)',  line:{color:'blue'}},
            {x:simData.t, y:simData.Glu,name:'Glu(t)',line:{color:'red'}},
            {x:simData.t, y:simData.Eta,name:'Eta(t)',line:{color:'green'}},
        ];
        const topLayout = {
            title:'diauxic behavior',
            xaxis:{title:'time (h)'},
            yaxis:{title:'concentration [mol/m³]'},
            legend:{orientation:'h'}
        };
        Plotly.react("plot-top", topData, topLayout, {displayModeBar:false});

        // ----------- BOTTOM PLOT -----------
        const bottomData = [
            {x:simData.t, y:simData.E2, name:'E₂(t)', line:{color:'black'}},
            {x:simData.t, y:simData.E1, name:'E₁(t)', line:{color:'magenta'}},
            {x:simData.t, y:simData.E0, name:'E₀(t)', line:{color:'orange'}},
        ];
        const bottomLayout = {
            title:'enzyme concentration',
            xaxis:{title:'time (h)'},
            yaxis:{title:'enzyme concentration [mol/m³]'},
            legend:{orientation:'h'}
        };
        Plotly.react("plot-bottom", bottomData, bottomLayout, {displayModeBar:false});
    } else { // plotChoice === "α"
        // ----------- TOP PLOT -----------
        const topData = [
            {x:simData.t, y:simData.Glu, name:'Glu(t)', line:{color:'red'}}
        ];
        const topLayout = {
            title:'glucose concentration',
            xaxis:{title:'time (h)'},
            yaxis:{title:'glucose concentration [mol/m³]'},
            legend:{orientation:'h'}
        };
        Plotly.react("plot-top", topData, topLayout, {displayModeBar:false});

        // ----------- BOTTOM PLOT -----------
        const bottomData = [
            {x:simData.t, y:simData.a, name:'α', line:{color:'blue'}}
        ];
        const bottomLayout = {
            title:'fraction of respiring biomass',
            xaxis:{title:'time (h)'},
            yaxis:{title:'α'},
            legend:{orientation:'h'}
        };
        Plotly.react("plot-bottom", bottomData, bottomLayout, {displayModeBar:false});
    }
}

/* ========= EVENT HANDLERS ========= */
function handleSliderInput(){
    // Update number displays
    updateValueDisplays();

    // sync feed-time max with t-max
    const t_max = +$("slider-t-max").value;
    const feedTimeSlider = $("slider-feed-time");
    feedTimeSlider.max = t_max;

    if(+feedTimeSlider.value > t_max){
        feedTimeSlider.value = t_max;
        $("val-feed-time").textContent = t_max;
    }

    // Re-run sim & redraw
    runSimulation();
    drawPlots();
}

function handlePlotChange(){
    drawPlots(); // just redraw with existing data
}

/* ========= INIT ========= */
document.addEventListener("DOMContentLoaded",()=>{
    // attach listeners
    sliders.forEach(id=>{
        $(id).addEventListener("input",handleSliderInput);
    });
    $("select-plot").addEventListener("change",handlePlotChange);

    // initial display
    updateValueDisplays();
    runSimulation();
    drawPlots();
});
</script>
</body>
</html>
