    selectView: document.getElementById('select-view'),
    labelDate : document.getElementById('label-date'),
    labelLat  : document.getElementById('label-latitude'),
    labelTime : document.getElementById('label-time'),
    optionsGround: document.getElementById('options-ground'),
    optionsSpace : document.getElementById('options-space'),
    readDay  : document.getElementById('readout-day-length'),
    readNight: document.getElementById('readout-night-length'),
    readRiseT: document.getElementById('readout-sunrise-time'),
    readRiseD: document.getElementById('readout-sunrise-direction'),
    readSetT : document.getElementById('readout-sunset-time'),
    readSetD : document.getElementById('readout-sunset-direction'),
    // Checkboxes
    chkSunPath : document.getElementById('check-sun-path'),
    chkConstDay: document.getElementById('check-constellations-day'),
    chkStatEarth:document.getElementById('check-stationary-earth'),
    chkConst   : document.getElementById('check-constellations'),
    chkTerm    : document.getElementById('check-day-night-division'),
    chkParallel: document.getElementById('check-day-night-parallel')
};

// --------------- INITIALIZATION ------------------
initThree();
initObjects();
attachEvents();
updateLabels();
recalculate();  // also marks dirty
animate();

// ----------------- FUNCTIONS ---------------------
function initThree(){
    scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);

    camera = new THREE.PerspectiveCamera(40, innerWidth/innerHeight, 0.1, 1000);
    camera.position.set(0,1,5); // ground default

    renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setPixelRatio(devicePixelRatio);
    renderer.setSize(innerWidth - 280, innerHeight); // leave space for panel
    document.getElementById('visualization-container').appendChild(renderer.domElement);

    controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enablePan = false;
}

function initObjects(){
    // LIGHT
    const hemi = new THREE.HemisphereLight(0xffffff,0x333366,1);
    scene.add(hemi);

    // ---------- SHARED: SUN ----------
    const sunGeo = new THREE.SphereGeometry(0.3,16,16);
    const sunMat = new THREE.MeshBasicMaterial({color:0xffff00});
    sunMesh = new THREE.Mesh(sunGeo, sunMat);
    const sunlight = new THREE.DirectionalLight(0xffffff,1);
    sunlight.position.set(0,0,0);
    sunMesh.add(sunlight);
    scene.add(sunMesh);

    // ---------- GROUND GROUP ----------
    groundGroup = new THREE.Group();
    scene.add(groundGroup);

    const groundGeo = new THREE.CylinderGeometry(10,10,0.2,32,1,true);
    const matTop = new THREE.MeshPhongMaterial({color:0x226622});
    const matSide = new THREE.MeshPhongMaterial({color:0x111133});
    // top disc
    const disc = new THREE.Mesh(new THREE.CircleGeometry(10,32), matTop);
    disc.rotation.x = -Math.PI/2;
    disc.position.y = 0.1;
    groundGroup.add(disc);
    // side
    const cylSide = new THREE.Mesh(groundGeo, matSide);
    cylSide.position.y = 0;
    groundGroup.add(cylSide);

    // Celestial dome
    const domeGeo = new THREE.SphereGeometry(15,32,32,0,Math.PI*2,0,Math.PI/2);
    const domeMat = new THREE.MeshPhongMaterial({color:0x3366ff,opacity:0.2,transparent:true,side:THREE.BackSide});
    const dome = new THREE.Mesh(domeGeo,domeMat);
    groundGroup.add(dome);

    // Sun path placeholder line
    sunPathLine = new THREE.Line(new THREE.BufferGeometry(), new THREE.LineBasicMaterial({color:0xffff00}));
    groundGroup.add(sunPathLine);

    // ---------- SPACE GROUP ----------
    spaceGroup = new THREE.Group();
    scene.add(spaceGroup);

    // Earth
    const earthGeo = new THREE.SphereGeometry(5,32,32);
    const earthMat = new THREE.MeshPhongMaterial({color:0x2244ff,flatShading:true});
    earthMesh = new THREE.Mesh(earthGeo, earthMat);
    spaceGroup.add(earthMesh);

    // Earth axis
    const axisMat = new THREE.LineBasicMaterial({color:0xffffff});
    const axisGeo = new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(0,5.5,0), new THREE.Vector3(0,-5.5,0)]);
    const axisLine = new THREE.Line(axisGeo, axisMat);
    earthMesh.add(axisLine);

    // Day/Night plane
    const planeGeo = new THREE.PlaneGeometry(12,12);
    const planeMat = new THREE.MeshBasicMaterial({color:0x000000,opacity:0.4,transparent:true,side:THREE.DoubleSide});
    dayNightPlane = new THREE.Mesh(planeGeo, planeMat);
    spaceGroup.add(dayNightPlane);

    // Latitude parallel
    latitudeParallel = new THREE.Line(new THREE.BufferGeometry(), new THREE.LineBasicMaterial({color:0xffaa00}));
    spaceGroup.add(latitudeParallel);

    // Daylight parallel segment
    daylightParallel = new THREE.Line(new THREE.BufferGeometry(), new THREE.LineBasicMaterial({color:0xffff00,linewidth:4}));
    spaceGroup.add(daylightParallel);

    // Constellations (both views – single placeholder)
    const constellationsGeo = new THREE.BufferGeometry().setFromPoints([
        new THREE.Vector3(0,8,0), new THREE.Vector3(2,9,1),
        new THREE.Vector3(2,9,1), new THREE.Vector3(-1,11,-1),
        new THREE.Vector3(-1,11,-1), new THREE.Vector3(0,8,0)
    ]);
    const constellationsMat = new THREE.LineBasicMaterial({color:0xaaaaaa});
    const constellations = new THREE.LineSegments(constellationsGeo,constellationsMat);
    constellations.name = "constellations";
    scene.add(constellations);

    // Initial visibilities
    switchView(dom.selectView.value);
}

function attachEvents(){
    window.addEventListener('resize', onWindowResize);

    dom.sliderDate.addEventListener('input', ()=>{updateLabels(); recalculate();});
    dom.sliderLat .addEventListener('input', ()=>{updateLabels(); recalculate();});
    dom.sliderTime.addEventListener('input', ()=>{updateLabels(); updateTimeDriven();});

    dom.selectView.addEventListener('change', ()=>{switchView(dom.selectView.value); dirty=true;});

    [dom.chkSunPath, dom.chkConstDay, dom.chkStatEarth, dom.chkConst,
     dom.chkTerm, dom.chkParallel].forEach(chk=>{
        chk.addEventListener('change', ()=>{applyVisibility(); dirty=true;});
    });
}

function updateLabels(){
    dom.labelDate.textContent = dayOfYearToDateText(+dom.sliderDate.value);
    dom.labelLat.textContent  = `${dom.sliderLat.value}°`;
    dom.labelTime.textContent = hoursToHM(+dom.sliderTime.value);
    dirty=true;
}

function recalculate(){
    const day = +dom.sliderDate.value;
    const lat = +dom.sliderLat.value * DEG2RAD;

    const decl = solarDeclination(day); // radians
    const hourAngle = Math.acos(-Math.tan(lat) * Math.tan(decl)); // sunrise angle
    const dayLength = (2*hourAngle/RAD2DEG)/15*3600; // seconds

    // Readouts
    const dayLenH = dayLength/3600;
    const nightLenH = 24 - dayLenH;
    dom.readDay.textContent   = dayLenH.toFixed(2)+" h";
    dom.readNight.textContent = nightLenH.toFixed(2)+" h";

    const sunrise = 12 - dayLenH/2;
    const sunset  = 12 + dayLenH/2;
    dom.readRiseT.textContent = hoursToHM(sunrise);
    dom.readSetT .textContent = hoursToHM(sunset);

    const azimuth = Math.acos( Math.sin(decl)/Math.cos(lat) ); // sunrise azimuth from N
    dom.readRiseD.textContent = `${(azimuth/RAD2DEG).toFixed(0)}°`;
    dom.readSetD .textContent = `${(360 - azimuth/RAD2DEG).toFixed(0)}°`;

    // Update visuals
    rebuildSunPath(lat, decl);
    rebuildLatitudeParallel(lat);
    positionSunAndEarth(); // also updates day/night plane
    applyVisibility();
    dirty=true;
}

function updateTimeDriven(){
    positionSunAndEarth();
    dirty=true;
}

// ------- Astronomy helper functions ----------
function solarDeclination(day){ // approximate (Spencer formula)
    return 23.44*DEG2RAD * Math.sin(DEG2RAD * (360/365*(day-80)));
}

function hoursToHM(h){
    h = (h+24)%24; // clamp
    const hours = Math.floor(h);
    const mins  = Math.floor((h - hours)*60);
    return `${hours.toString().padStart(2,'0')}:${mins.toString().padStart(2,'0')}`;
}

function dayOfYearToDateText(d){
    const monthDays=[31,28,31,30,31,30,31,31,30,31,30,31];
    let m=0;
    while(d>monthDays[m]){d-=monthDays[m];m++;}
    const monthNames=["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];
    return `${monthNames[m]} ${d}`;
}

// ---------- Geometry builders -----------------
function rebuildSunPath(lat, decl){
    const points=[];
    const steps=48;
    for(let i=0;i<=steps;i++){
        const t = -Math.PI + i*(2*Math.PI/steps); // hour angle -180..180 deg
        const alt = Math.asin(Math.sin(lat)*Math.sin(decl)+Math.cos(lat)*Math.cos(decl)*Math.cos(t));
        const az  = Math.atan2(Math.sin(t), Math.cos(t)*Math.sin(lat)-Math.tan(decl)*Math.cos(lat));
        const r=10; // arbitrary radius
        const x=r*Math.cos(alt)*Math.sin(az);
        const y=r*Math.sin(alt);
        const z=r*Math.cos(alt)*Math.cos(az);
        points.push(new THREE.Vector3(x,y,z));
    }
    const geo = new THREE.BufferGeometry().setFromPoints(points);
    sunPathLine.geometry.dispose();
    sunPathLine.geometry = geo;
}

function rebuildLatitudeParallel(lat){
    const points=[];
    const steps=64;
    const R=5.01;
    for(let i=0;i<=steps;i++){
        const lon = i/steps*2*Math.PI;
        points.push(new THREE.Vector3(
            R*Math.cos(lat)*Math.cos(lon),
            R*Math.sin(lat),
            R*Math.cos(lat)*Math.sin(lon)
        ));
    }
    const geo = new THREE.BufferGeometry().setFromPoints(points);
    latitudeParallel.geometry.dispose();
    latitudeParallel.geometry = geo;
}

function positionSunAndEarth(){
    const solarTime = +dom.sliderTime.value;
    // --- SUN in ground view path (simple) ---
    const dayFrac = solarTime/24;
    const idx = Math.floor(dayFrac * (sunPathLine.geometry.attributes.position.count-1));
    const pos = new THREE.Vector3().fromBufferAttribute(sunPathLine.geometry.attributes.position, idx);
    sunMesh.position.copy(pos);

    // --- Earth rotation in space view ---
    if(!dom.chkStatEarth.checked){
        const rot = (solarTime/24)*2*Math.PI;
        earthMesh.rotation.y = rot;
    }

    // Day/night plane orientation: face sun
    dayNightPlane.lookAt(sunMesh.position);
}

function switchView(view){
    if(view==="ground"){
        dom.optionsGround.style.display="";
        dom.optionsSpace .style.display="none";
        camera.position.set(0,1,5);
        camera.lookAt(0,0,0);
        groundGroup.visible = true;
        spaceGroup .visible = false;
    }else{
        dom.optionsGround.style.display="none";
        dom.optionsSpace .style.display="";
        camera.position.set(0,0,30);
        camera.lookAt(0,0,0);
        groundGroup.visible = false;
        spaceGroup .visible = true;
    }
    applyVisibility();
    dirty=true;
}

function applyVisibility(){
    const constellationsObj = scene.getObjectByName("constellations");
    constellationsObj.visible = dom.selectView.value==="ground" ? dom.chkConstDay.checked : dom.chkConst.checked;

    sunPathLine.visible    = dom.chkSunPath.checked && dom.selectView.value==="ground";
    dayNightPlane.visible  = dom.chkTerm.checked && dom.selectView.value==="space";
    latitudeParallel.visible= dom.chkParallel.checked && dom.selectView.value==="space";
    daylightParallel.visible= dom.chkParallel.checked && dom.selectView.value==="space";
}

function onWindowResize(){
    camera.aspect = (innerWidth-280)/innerHeight;
    camera.updateProjectionMatrix();
    renderer.setSize(innerWidth-280, innerHeight);
    dirty=true;
}

// --------------- RENDER LOOP -------------------
function animate(){
    requestAnimationFrame(animate);
    controls.update();
    if(dirty){
        renderer.render(scene,camera);
        dirty=false;
    }
}
</script>
</body>
</html>
