<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Unsteady-State Diffusion in a Slab</title>

<!-- Plotly CDN -->
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

<style>
    body{
        font-family: Arial, sans-serif;
        margin:0;
        padding:20px;
    }

    #main-container{
        max-width:960px;
        margin:0 auto;
    }

    #control-panel{
        display:flex;
        flex-direction:column;
        gap:12px;
        padding:15px 20px;
        background:#f0f0f0;
        border:1px solid #ccc;
        border-radius:6px;
        margin:24px 0;
    }

    .control-row{
        display:flex;
        align-items:center;
        gap:10px;
    }

    .control-row label{
        flex:0 0 250px;
    }

    .control-row input[type="range"]{
        flex:1;
    }

    .control-row span{
        width:130px;
        text-align:right;
        font-weight:bold;
        font-variant-numeric: tabular-nums;
    }
</style>
</head>
<body>
<div id="main-container">
    <!-- Header -->
    <h1>Unsteady-State Diffusion in a Slab</h1>
    <p>
        This interactive demo illustrates the transient diffusion of a solute in a flat slab with one face
        suddenly brought to a constant surface concentration. Adjust the elapsed time and the diffusion
        coefficient to see how the concentration profile evolves inside the material.
        (Replace this paragraph with any description you like.)
    </p>

    <!-- Control Panel -->
    <div id="control-panel">
        <div class="control-row">
            <label for="time-slider">time (s)</label>
            <input type="range" id="time-slider" min="0" max="2500" step="1" value="100">
            <span id="time-value">100.0</span>
        </div>
        <div class="control-row">
            <label for="diffusivity-slider">diffusivity in m<sup>2</sup>/s</label>
            <input type="range" id="diffusivity-slider" min="1e-10" max="1e-8" step="1e-10" value="4e-9">
            <span id="diffusivity-value">4.0 × 10⁻⁹</span>
        </div>
    </div>

    <!-- Plot Area -->
    <div id="plot-div" style="width:100%;height:500px;"></div>
</div>

<script>
/* ------------------------  CONSTANTS & PRECOMPUTED VALUES  ------------------------ */
const L        = 0.004;     // slab thickness (m)
const c_s      = 4.0e-3;    // surface concentration (kg/m^3)
const N_TERMS  = 100;       // number of series terms
const NUM_XPTS = 200;       // resolution of x grid

// Build x-coordinate array once
const xArray = [];
for (let i = 0; i <= NUM_XPTS; i++){
    xArray.push(i * L / NUM_XPTS);
}

// Pre–compute k_n and B_n needed in series solution
const kArray = [];
const BArray = [];
for (let n = 0; n < N_TERMS; n++){
    const k_n = ( (n + 0.5) * Math.PI ) / L;
    const B_n = (2 / L) * 1e-3 * ( (250 * Math.pow(-1,n)) / (k_n*k_n) - 3 / k_n );
    kArray.push(k_n);
    BArray.push(B_n);
}

/* -----------------------------  UI ELEMENTS  -------------------------------------- */
const timeSlider = document.getElementById('time-slider');
const timeValue  = document.getElementById('time-value');
const DSlider    = document.getElementById('diffusivity-slider');
const DValue     = document.getElementById('diffusivity-value');

/* ---------- helper: format number in scientific notation with superscripts -------- */
const sup = {'0':'⁰','1':'¹','2':'²','3':'³','4':'⁴','5':'⁵','6':'⁶','7':'⁷','8':'⁸','9':'⁹','-':'⁻'};
function toSuperscript(numStr){
    return String(numStr).split('').map(ch=>sup[ch]||ch).join('');
}
function formatScientific(val){
    const exponent = Math.floor(Math.log10(val));
    const mantissa = val / Math.pow(10, exponent);
    return mantissa.toFixed(1) + ' × 10' + toSuperscript(exponent);
}

/* ---------------------------  MAIN PLOT FUNCTION  --------------------------------- */
let revision = 0;   // for Plotly.react efficiency

function updatePlot(){
    const t = parseFloat(timeSlider.value);
    const D = parseFloat(DSlider.value);

    const cArray = [];

    if (t === 0){
        // Initial linear profile
        for (const x of xArray){
            cArray.push( (1.0 + 250*x) * 1e-3 );
        }
    } else {
        // Series solution
        for (const x of xArray){
            let u = 0;
            for (let n = 0; n < N_TERMS; n++){
                u += BArray[n] * Math.sin(kArray[n]*x) * Math.exp( -kArray[n]*kArray[n]*D*t );
            }
            cArray.push( u + c_s );
        }
    }

    const trace = {
        x: xArray,
        y: cArray,
        mode: 'lines',
        line: { color:'#1f77b4', width:3 }
    };

    const layout = {
        xaxis:{
            title:'position (m)',
            range:[0, L],
            tickformat:'.3f',
            dtick:0.001,
            showgrid:true,
            minor:{showgrid:true}
        },
        yaxis:{
            title:'concentration (kg/m³)',
            range:[0.001, 0.0042],
            tickformat:'.4f',
            dtick:0.0005,
            showgrid:true,
            minor:{showgrid:true}
        },
        margin:{l:80,r:20,t:20,b:50},
        datarevision: revision
    };

    Plotly.react('plot-div', [trace], layout, {responsive:true});
    revision++;
}

/* -----------------------------  EVENT HANDLERS  ----------------------------------- */
timeSlider.addEventListener('input', ()=>{
    timeValue.textContent = parseFloat(timeSlider.value).toFixed(1);
    updatePlot();
});

DSlider.addEventListener('input', ()=>{
    DValue.textContent = formatScientific(parseFloat(DSlider.value));
    updatePlot();
});

/* --------------------------------  INIT  ------------------------------------------ */
timeValue.textContent = parseFloat(timeSlider.value).toFixed(1);
DValue.textContent    = formatScientific(parseFloat(DSlider.value));
updatePlot();
</script>
</body>
</html>
