    #canvas-container{margin-top:1rem;border:1px solid #ddd;border-radius:4px;overflow:hidden}
</style>
</head>
<body>
<div id="app-container">
    <!-- ================= CONTROLS ================= -->
    <div id="controls-container">
        <!-- Function selector -->
        <div id="function-controls" class="control-group">
            <span>function:</span>
            <button class="function-btn active" data-function="x">x</button>
            <button class="function-btn" data-function="parabola">(x-3)²</button>
            <button class="function-btn" data-function="cubic">(x-3)³+27</button>
            <button class="function-btn" data-function="sqrt">√x</button>
            <button class="function-btn" data-function="sin">sin(x)+1</button>
            <button class="function-btn" data-function="exp">eˣ/³</button>
        </div>
        <!-- Type selector -->
        <div id="type-controls" class="control-group">
            <span>type:</span>
            <button class="type-btn" data-type="left">left</button>
            <button class="type-btn active" data-type="right">right</button>
            <button class="type-btn" data-type="midpoint">midpoint</button>
            <button class="type-btn" data-type="overestimate">overestimate</button>
            <button class="type-btn" data-type="underestimate">underestimate</button>
            <button class="type-btn" data-type="trapezoidal">trapezoidal</button>
        </div>
        <!-- Slider a -->
        <div id="slider-a-controls" class="control-group">
            <label for="slider-a">upper limit a:</label>
            <input id="slider-a" type="range" min="0.1" max="8" step="0.01" value="5">
            <span id="value-a">5.00</span>
        </div>
        <!-- Slider n -->
        <div id="slider-n-controls" class="control-group">
            <label for="slider-n">number of quadrilaterals:</label>
            <input id="slider-n" type="range" min="1" max="50" step="1" value="3">
            <span id="value-n">3</span>
        </div>
    </div>
    <!-- ================= CANVAS ================= -->
    <div id="canvas-container">
        <!-- p5.js will append the canvas here -->
    </div>
</div>

<script>
/* ---------- STATE & FUNCTION DEFINITIONS ---------- */
const state = { funcKey:'x', type:'right', a:5, n:3 };

const funcDefs = {
    x:{
        label:'x',
        f:x=>x,
        F:x=>0.5*x*x
    },
    parabola:{
        label:'(x-3)²',
        f:x=>(x-3)**2,
        F:x=>((x-3)**3)/3
    },
    cubic:{
        label:'(x-3)³+27',
        f:x=>(x-3)**3 + 27,
        F:x=>((x-3)**4)/4 + 27*x
    },
    sqrt:{
        label:'√x',
        f:x=> x>=0 ? Math.sqrt(x): NaN,
        F:x=> (2/3)*Math.pow(Math.max(0,x),1.5)
    },
    sin:{
        label:'sin(x)+1',
        f:x=>Math.sin(x)+1,
        F:x=>-Math.cos(x)+x
    },
    exp:{
        label:'e^{x/3}',
        f:x=>Math.exp(x/3),
        F:x=>3*Math.exp(x/3)
    }
};

/* ---------- DOM HOOKS ---------- */
document.querySelectorAll('.function-btn').forEach(btn=>{
    btn.addEventListener('click',()=>{
        state.funcKey = btn.dataset.function;
        document.querySelectorAll('.function-btn').forEach(b=>b.classList.remove('active'));
        btn.classList.add('active');
        redraw();
    });
});

document.querySelectorAll('.type-btn').forEach(btn=>{
    btn.addEventListener('click',()=>{
        state.type = btn.dataset.type;
        document.querySelectorAll('.type-btn').forEach(b=>b.classList.remove('active'));
        btn.classList.add('active');
        redraw();
    });
});

const sliderA = document.getElementById('slider-a');
const valueA  = document.getElementById('value-a');
sliderA.addEventListener('input', ()=>{
    state.a = parseFloat(sliderA.value);
    valueA.textContent = state.a.toFixed(2);
    redraw();
});

const sliderN = document.getElementById('slider-n');
const valueN  = document.getElementById('value-n');
sliderN.addEventListener('input', ()=>{
    state.n = parseInt(sliderN.value);
    valueN.textContent = state.n;
    redraw();
});

/* ---------- P5 SKETCH ---------- */
let canvas;
const GRAPH_PADDING = {left:55, right:15, top:25, bottom:45};

function setup(){
    canvas = createCanvas(900,550);
    canvas.parent('canvas-container');
    textFont('Courier New, monospace');
    textSize(14);
    noLoop();           // draw() will be called manually on changes
    redraw();           // initial draw
}

function draw(){
    background(248,248,248);
    const fObj = funcDefs[state.funcKey];

    // --------- Determine logical x & y ranges ----------
    const xMin = -1;
    const xMax = Math.max(state.a + 1, 8.5);
    const samples = 400;
    let yMin = Infinity, yMax = -Infinity;

    for(let i=0;i<=samples;i++){
        const x = xMin + (xMax-xMin)*i/samples;
        const y = fObj.f(x);
        if(isFinite(y)){
            if(y<yMin) yMin = y;
            if(y>yMax) yMax = y;
        }
    }
    yMin = Math.min(yMin,0);
    yMax = Math.max(yMax,0);
    const yPad = (yMax-yMin)*0.1 || 1;
    yMin -= yPad;
    yMax += yPad;

    // --------- Coordinate helpers ----------
    const xToPx = x => map(x,xMin,xMax,GRAPH_PADDING.left,width-GRAPH_PADDING.right);
    const yToPx = y => map(y,yMin,yMax,height-GRAPH_PADDING.bottom,GRAPH_PADDING.top);

    // --------- Draw axes ----------
    stroke('#cccccc'); strokeWeight(1);
    // x-axis
    if(0>=yMin && 0<=yMax){
        line(xToPx(xMin), yToPx(0), xToPx(xMax), yToPx(0));
    }
    // y-axis
    if(0>=xMin && 0<=xMax){
        line(xToPx(0), yToPx(yMin), xToPx(0), yToPx(yMax));
    }

    // ticks (simple integer ticks)
    stroke('#cccccc'); fill('#666'); noStroke(); textAlign(CENTER,TOP);
    for(let xTick=Math.ceil(xMin); xTick<=xMax; xTick++){
        const px = xToPx(xTick);
        stroke('#cccccc');
        line(px, yToPx(0)-4, px, yToPx(0)+4);
        noStroke();
        text(xTick, px, yToPx(0)+6);
    }
    textAlign(RIGHT,CENTER);
    for(let yTick=Math.ceil(yMin); yTick<=yMax; yTick++){
        const py = yToPx(yTick);
        stroke('#cccccc');
        line(xToPx(0)-4, py, xToPx(0)+4, py);
        noStroke();
        if(yTick!==0) text(yTick, xToPx(0)-6, py);
    }

    // --------- Draw function curve ----------
    stroke(0); strokeWeight(2); noFill();
    beginShape();
    for(let i=0;i<=samples;i++){
        const x = xMin + (xMax-xMin)*i/samples;
        const y = fObj.f(x);
        if(isFinite(y)){
            vertex(xToPx(x), yToPx(y));
        }
    }
    endShape();

    // --------- Draw Riemann / Trapezoidal shapes ----------
    const delta = state.a / state.n;
    let estimatedArea = 0;
    stroke('#555'); strokeWeight(1);
    for(let i=0;i<state.n;i++){
        const x0 = i*delta;
        const x1 = x0 + delta;
        const xm = (x0 + x1)/2;
        const h0 = fObj.f(x0);
        const h1 = fObj.f(x1);
        const hm = fObj.f(xm);

        let areaShape = 0, trueSubInt = fObj.F(x1) - fObj.F(x0);
        let colorFill;

        if(state.type==='trapezoidal'){
            areaShape = 0.5*(h0+h1)*delta;
            colorFill = areaShape > trueSubInt ? 'rgba(173,216,230,0.7)' : 'rgba(144,238,144,0.7)';
            fill(colorFill); beginShape();
            vertex(xToPx(x0), yToPx(0));
            vertex(xToPx(x0), yToPx(h0));
            vertex(xToPx(x1), yToPx(h1));
            vertex(xToPx(x1), yToPx(0));
            endShape(CLOSE);
        }else{
            let hRect;
            switch(state.type){
                case 'left':         hRect = h0; break;
                case 'right':        hRect = h1; break;
                case 'midpoint':     hRect = hm; break;
                case 'overestimate': hRect = Math.max(h0,h1,hm); break;
                case 'underestimate':hRect = Math.min(h0,h1,hm); break;
            }
            areaShape = hRect*delta;
            colorFill = areaShape > trueSubInt ? 'rgba(173,216,230,0.7)' : 'rgba(144,238,144,0.7)';
            fill(colorFill);
            const px0 = xToPx(x0);
            const px1 = xToPx(x1);
            const py0 = yToPx(0);
            const pyH = yToPx(hRect);
            rectMode(CORNERS);
            rect(px0, py0, px1, pyH);
        }
        estimatedArea += areaShape;
    }

    // --------- Upper limit marker ----------
    stroke('red'); strokeWeight(2);
    const pxA = xToPx(state.a);
    line(pxA, yToPx(yMin), pxA, yToPx(yMax));
    noStroke(); fill('red'); textAlign(CENTER,TOP);
    text('a', pxA, yToPx(0)+5);

    // --------- Display calculations ----------
    const trueIntegral = (fObj.F(state.a) - fObj.F(0));
    fill(0); noStroke(); textAlign(CENTER,TOP);
    text(`estimated area: ${estimatedArea.toFixed(5)}    integral: ${trueIntegral.toFixed(5)}`, width/2, 5);
}
</script>
</body>
</html>
