<script>
/* ------------------------- GLOBAL STATE ------------------------- */
const initialParams = {
    current: 0.431,
    earthField: 0.0000194,
    radius: 0.5,
    turns: 25
};
let params = {...initialParams};    // working copy

/* --------------------- SLIDER / UI HANDLING --------------------- */
const sliders = {
    current: document.getElementById('slider-current'),
    earthField: document.getElementById('slider-earth-field'),
    radius: document.getElementById('slider-radius'),
    turns: document.getElementById('slider-turns')
};
const spans  = {
    current: document.getElementById('value-current'),
    earthField: document.getElementById('value-earth-field'),
    radius: document.getElementById('value-radius'),
    turns: document.getElementById('value-turns')
};

function fmt(val, digits=3){
    // helper for display; switch to scientific if small
    if(Math.abs(val)<0.0001 && val!==0){
        return Number(val).toExponential(2);
    }
    return Number(val).toFixed(digits);
}
function updateDisplay(){
    spans.current.textContent    = fmt(params.current,3);
    spans.earthField.textContent = fmt(params.earthField,2);
    spans.radius.textContent     = fmt(params.radius,2);
    spans.turns.textContent      = params.turns;
}
Object.keys(sliders).forEach(key=>{
    sliders[key].addEventListener('input',()=>{
        params[key] = (key==='turns')? parseInt(sliders[key].value,10) : parseFloat(sliders[key].value);
        updateDisplay();
        redraw();     // p5.js redraw
    });
});
document.getElementById('btn-reset').addEventListener('click',()=>{
    Object.keys(sliders).forEach(k=>{
        sliders[k].value = initialParams[k];
        params[k] = initialParams[k];
    });
    updateDisplay();
    redraw();
});
updateDisplay();

/* --------------------------- p5.js --------------------------- */
let canvasSize = 600;   // square
let halfRange = 0.5;    // world coordinate limit
let stepSize = 0.005;   // world units per streamline step
function worldToScreenY(y){  return map(y,-halfRange,halfRange,0,canvasSize); }
function worldToScreenZ(z){  return map(z,-halfRange,halfRange,canvasSize,0); } // invert Z for canvas

function setup(){
    let c = createCanvas(canvasSize,canvasSize);
    c.parent('viz-container');
    c.id('p5-canvas');
    colorMode(HSB,360,100,100);
    strokeWeight(1);
    noLoop();
}
function redraw(){   // override built-in to ensure available globally
    if(typeof window.draw === 'function') window.draw();
}
function draw(){
    clear();
    background(255);

    drawAxes();
    // -------------- pre-compute min / max magnitude --------------
    let grid = [];
    let minMag=Infinity, maxMag=0;
    const gridCount = 25;
    for(let i=0;i<gridCount;i++){
        let y = map(i,0,gridCount-1,-halfRange,halfRange);
        for(let j=0;j<gridCount;j++){
            let z = map(j,0,gridCount-1,-halfRange,halfRange);
            let B = totalField(y,z);
            let mag = Math.hypot(B.y,B.z);
            minMag = Math.min(minMag,mag);
            maxMag = Math.max(maxMag,mag);
            grid.push({y,z});
        }
    }
    // -------------- draw streamlines --------------
    for(let pt of grid){
        drawStreamline(pt.y,pt.z,minMag,maxMag);
    }
}

/* ----------------- Streamline drawing helpers ----------------- */
function drawStreamline(y0,z0,minMag,maxMag){
    const maxSteps = 100;
    stream(y0,z0,1);   // forward direction
    stream(y0,z0,-1);  // backward direction

    function stream(y,z,dirSign){
        let steps=0;
        let lastArrow=0;
        while(steps<maxSteps){
            let B = totalField(y,z);
            let mag = Math.hypot(B.y,B.z);
            if(mag===0) break;

            // color mapping
            let hue = map(mag,minMag,maxMag,60,270);   // low→yellow, high→purple/blue
            stroke(hue,100,100);

            // direction
            let dy = (B.y/mag)*stepSize*dirSign;
            let dz = (B.z/mag)*stepSize*dirSign;
            let y2 = y + dy;
            let z2 = z + dz;

            // stop if leaves world bounds
            if(Math.abs(y2)>halfRange || Math.abs(z2)>halfRange) break;

            // draw segment
            line(worldToScreenY(y),worldToScreenZ(z), worldToScreenY(y2),worldToScreenZ(z2));

            // arrow every 10 segments
            lastArrow++;
            if(lastArrow===10){
                drawArrow(worldToScreenY(y2),worldToScreenZ(z2),dy,dz);
                lastArrow=0;
            }

            y=y2; z=z2; steps++;
        }
    }
}
function drawArrow(x,y,dy,dz){
    const len = 6;   // pixels
    let angle = Math.atan2(-dz,dy); // canvas: y inverted
    push();
    translate(x,y);
    rotate(angle);
    line(0,0,-len/2,-len/3);
    line(0,0,-len/2,len/3);
    pop();
}

/* ------------------- Magnetic Field Functions ------------------- */
const mu0 = 4*Math.PI*1e-7;

function totalField(y,z){
    // Earth field (points along +Z)
    let BzEarth = params.earthField;
    let coil1 = coilField(y,z,-params.radius/2);
    let coil2 = coilField(y,z, params.radius/2);
    return {
        y: coil1.y + coil2.y,
        z: coil1.z + coil2.z + BzEarth
    };
}
function coilField(y,z,z_offset){
    let R = params.radius;
    let I = params.current;
    let N = params.turns;
    let zp = z - z_offset;
    let r_plus = R + y;
    let m = (4*R*y) / (r_plus*r_plus + zp*zp);
    if(m<0) m=0;
    if(m>1) m=1;

    // elliptic integrals
    let K = ellipticK(m);
    let E = ellipticE(m);

    let common1 = (mu0 * I * N) / (2 * Math.PI);
    let common2 = 1 / Math.sqrt(r_plus*r_plus + zp*zp);

    let By=0;
    if(Math.abs(y)>1e-9){
        By = common1*(zp/y)*common2 *
            ( ((R*R + y*y + zp*zp)/((R - y)*(R - y) + zp*zp))*E - K );
    }
    let Bz = common1*common2 *
            ( K - ((R*R - y*y - zp*zp)/((R - y)*(R - y) + zp*zp))*E );

    return {y:By, z:Bz};
}

/* ---------------- Elliptic Integrals (AGM method) ---------------- */
function ellipticK(m){
    if(m<0 || m>1) return NaN;
    let a=1,b=Math.sqrt(1-m), c = m, sum=0, pow2=1;
    while(Math.abs(c)>1e-10){
        let nextA = (a+b)/2;
        let nextB = Math.sqrt(a*b);
        c = (nextA-nextB)/2;
        sum += pow2*c*c;
        a=nextA; b=nextB; pow2*=2;
    }
    return Math.PI/(2*a);
}
function ellipticE(m){
    if(m<0 || m>1) return NaN;
    let a=1,b=Math.sqrt(1-m), c = m, sum=0, pow2=1;
    while(Math.abs(c)>1e-10){
        let nextA = (a+b)/2;
        let nextB = Math.sqrt(a*b);
        c = (nextA-nextB)/2;
        sum += pow2*c*c;
        a=nextA; b=nextB; pow2*=2;
    }
    return Math.PI/2 * (1 - sum)/(a);
}

/* --------------------------- Axes --------------------------- */
function drawAxes(){
    strokeWeight(1);
    stroke(var(--axis-red));
    fill(var(--axis-red));
    // Y axis (horizontal in world coords -> horizontal line through Z=0)
    let z0 = worldToScreenZ(0);
    line(0,z0,canvasSize,z0);
    // ticks
    for(let y=-0.4;y<=0.4;y+=0.2){
        let x = worldToScreenY(y);
        line(x,z0-4,x,z0+4);
        noStroke(); textSize(10); fill('red');
        textAlign(CENTER,TOP);
        text(y.toFixed(1),x,z0+6);
        stroke('red');
    }
    // Z axis (vertical line through Y=0)
    let x0 = worldToScreenY(0);
    line(x0,0,x0,canvasSize);
    for(let z=-0.4;z<=0.4;z+=0.2){
        let ypix = worldToScreenZ(z);
        line(x0-4,ypix,x0+4,ypix);
        noStroke(); textSize(10); fill('red');
        textAlign(LEFT,CENTER);
        text(z.toFixed(1),x0+6,ypix+2);
        stroke('red');
    }
    // labels
    noStroke(); fill('red'); textSize(14);
    textAlign(RIGHT,CENTER); text('Y',canvasSize-6,z0-12);
    textAlign(CENTER,TOP);   text('Z',x0+12,6);
}
</script>
</body>
</html>
