/* ------------ CONSTANTS & UTILITIES -------- */
const WORD_LIST = ['BRUTUS','MESSAGE','MARCH','SECRET','ATTACK','CAESAR',
                   'HELLO','WORLD','CIPHER','EVEN','IDES','COME','DAWN',
                   'THE','YOU','ARE','OF','I','A'].sort((a,b)=>b.length-a.length);

const ALPHABET = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');

/* -------------- INITIALIZATION ------------- */
function init(){
    createKeyboard('keyboard-encoding',true);
    createKeyboard('keyboard-decoding',false);
    attachEventHandlers();
    updateEncoding();   // sets initial alphabet map
    updateDecoding();   // sets initial alphabet map
}

function createKeyboard(containerId, includeSpace){
    const container = document.getElementById(containerId);
    ALPHABET.forEach(letter=>{
        const btn=document.createElement('button');
        btn.textContent=letter;
        btn.dataset.key=letter;
        container.appendChild(btn);
