    const back=document.createElement('button');
    back.style.flex="0 0 100px";
    back.textContent="BACKSPACE";
    back.dataset.key="BACKSPACE";
    container.appendChild(back);
}

/* ------------- EVENT HANDLERS -------------- */
function attachEventHandlers(){
    // Tab buttons
    document.getElementById('btn-toggle-encoding').addEventListener('click',()=>{
        document.getElementById('panel-encoding').style.display='';
        document.getElementById('panel-decoding').style.display='none';
        document.getElementById('btn-toggle-encoding').classList.add('active');
        document.getElementById('btn-toggle-decoding').classList.remove('active');
    });
    document.getElementById('btn-toggle-decoding').addEventListener('click',()=>{
        document.getElementById('panel-encoding').style.display='none';
        document.getElementById('panel-decoding').style.display='';
        document.getElementById('btn-toggle-decoding').classList.add('active');
        document.getElementById('btn-toggle-encoding').classList.remove('active');
    });

    // Encoding panel controls
    document.getElementById('select-message').addEventListener('change',e=>{
        currentMessage = e.target.value;
        document.getElementById('display-message').innerText=currentMessage;
        updateEncoding();
    });
    document.getElementById('slider-encoding-offset').addEventListener('input',e=>{
        document.getElementById('span-encoding-offset-value').innerText=e.target.value;
        updateEncoding();
    });
    document.getElementById('keyboard-encoding').addEventListener('click',e=>{
        const key = e.target.dataset.key;
        if(!key) return;
        if(key==="BACKSPACE"){
            currentMessage=currentMessage.slice(0,-1);
        }else if(key==="SPACE"){
            if(currentMessage.length<32) currentMessage+=" ";
        }else{  // Letter
            if(currentMessage.length<32) currentMessage+=key;
        }
        document.getElementById('display-message').innerText=currentMessage;
        updateEncoding();
    });
    document.getElementById('btn-clear-encoding').addEventListener('click',()=>{
        currentMessage="";
        currentCiphertext="";
        document.getElementById('select-message').value="";
        document.getElementById('slider-encoding-offset').value=0;
        document.getElementById('span-encoding-offset-value').innerText='0';
        document.getElementById('display-message').innerText="";
        document.getElementById('display-plaintext').innerText="";
        document.getElementById('display-ciphertext').innerText="";
        updateEncoding();
    });

    // Decoding panel controls
    document.getElementById('btn-intercept').addEventListener('click',()=>{
        document.getElementById('display-intercept').innerText=currentCiphertext;
        updateDecoding();
    });
    document.getElementById('slider-decoding-offset').addEventListener('input',e=>{
        document.getElementById('span-decoding-offset-value').innerText=e.target.value;
        updateDecoding();
    });
    document.getElementById('keyboard-decoding').addEventListener('click',e=>{
        const key=e.target.dataset.key;
        if(!key) return;
        const spanIntercept=document.getElementById('display-intercept');
        let text=spanIntercept.innerText;
        if(key==="BACKSPACE"){
            text=text.slice(0,-1);
        }else{
            if(text.length<32) text+=key;
        }
        spanIntercept.innerText=text;
        updateDecoding();
    });
    document.getElementById('btn-clear-decoding').addEventListener('click',()=>{
        document.getElementById('display-intercept').innerText="";
        document.getElementById('slider-decoding-offset').value=0;
        document.getElementById('span-decoding-offset-value').innerText='0';
        const tbody=document.querySelector('#table-decoding-output tbody');
        tbody.innerHTML="";
        updateDecoding();
    });
}

/* --------------- ENCODING LOGIC ------------ */
function updateEncoding(){
    const offset=parseInt(document.getElementById('slider-encoding-offset').value,10);
    // Plaintext (uppercase, spaces->X)
    const plaintext = currentMessage.toUpperCase().replace(/[^A-Z ]/g,'').replace(/ /g,'X');
    document.getElementById('display-plaintext').innerText=plaintext;

    // Update alphabet map
    const shifted = ALPHABET.map((l,i)=>ALPHABET[(i+offset)%26]);
    document.getElementById('display-encoding-alphabet-to').innerText=shifted.join(' ');

    // Generate ciphertext
    let ciphertext="";
    for(let char of plaintext){
        if(/[A-Z]/.test(char)){
            const idx=char.charCodeAt(0)-65;
            ciphertext+=shifted[idx];
        }else{
            ciphertext+=char;
        }
    }
    currentCiphertext=ciphertext;
    document.getElementById('display-ciphertext').innerText=ciphertext;
}

/* --------------- DECODING LOGIC ------------ */
function updateDecoding(){
    const offset=parseInt(document.getElementById('slider-decoding-offset').value,10);
    const intercept=document.getElementById('display-intercept').innerText.toUpperCase().replace(/[^A-Z]/g,'');

    // Update alphabet map (from shifted to normal)
    const fromAlphabet = ALPHABET.map((l,i)=>ALPHABET[(i - offset + 26*100)%26]); // big multiply for positive
    document.getElementById('display-decoding-alphabet-from').innerText=fromAlphabet.join(' ');
    document.getElementById('display-decoding-alphabet-to').innerText=ALPHABET.join(' ');

    // Build decoding table
    const tbody=document.querySelector('#table-decoding-output tbody');
    tbody.innerHTML="";
    const offsets=[offset-2,offset-1,offset,offset+1,offset+2];
    offsets.forEach(d=>{
        const tr=document.createElement('tr');
        const tdOffset=document.createElement('td');
        tdOffset.textContent=d;
        tr.appendChild(tdOffset);

        const decoded=decodeWithOffset(intercept,d);
        const highlighted=highlightWords(decoded);
        const tdDec=document.createElement('td');
        tdDec.innerHTML=highlighted;
        tr.appendChild(tdDec);

        tbody.appendChild(tr);
    });
}

function decodeWithOffset(text,off){
    let result="";
    for(let char of text){
        if(/[A-Z]/.test(char)){
            const idx=(char.charCodeAt(0)-65 - off + 26*100)%26;
            result+=ALPHABET[idx];
        }else result+=char;
    }
    return result;
}

function highlightWords(text){
    let i=0;
    let output='';
    while(i<text.length){
        let matched=false;
        for(let w of WORD_LIST){
            if(text.startsWith(w,i)){
                output+=`<span class="highlighted">${w}</span>`;
                i+=w.length;
                matched=true;
                break;
            }
        }
        if(!matched){
            output+=text[i];
            i++;
        }
    }
    return output;
}

/* -------------- START APP ------------------ */
init();
</script>
</body>
</html>
