            <div class="after-container">
                <p>after</p>
                <div id="canvas-after-container"></div>
            </div>
        </div>
    </div>

<script>
/* ======= GLOBAL STATE ======= */
const CANVAS_SIZE = 200;        // size (in px) for each preview canvas
let beforeGrid = [];            // binary grid (0/1) for original letter
let afterGrid  = [];            // grayscale grid (0..1) after convolution

/* ======= P5 SKETCHES (one for each canvas) ======= */
const beforeSketch = (p)=>{
    p.setup = function(){
        p.createCanvas(CANVAS_SIZE, CANVAS_SIZE).parent('canvas-before-container');
        p.noLoop();
    };
    p.draw = function(){
        p.background(255);
        if(!beforeGrid.length) return;
        const N = beforeGrid.length;
        const cell = CANVAS_SIZE/N;
        p.noStroke();
        for(let i=0;i<N;i++){
            for(let j=0;j<N;j++){
                p.fill(beforeGrid[i][j] ? 0 : 255);
                p.rect(j*cell, i*cell, cell, cell);
            }
        }
    };
};

const afterSketch = (p)=>{
    p.setup = function(){
        p.createCanvas(CANVAS_SIZE, CANVAS_SIZE).parent('canvas-after-container');
        p.noLoop();
    };
    p.draw = function(){
        p.background(255);
        if(!afterGrid.length) return;
        const N = afterGrid.length;
        const cell = CANVAS_SIZE/N;
        p.noStroke();
        for(let i=0;i<N;i++){
            for(let j=0;j<N;j++){
                const v = afterGrid[i][j]; // 0..1
                const shade = 255*(1-v);
                p.fill(shade);
                p.rect(j*cell, i*cell, cell, cell);
            }
        }
    };
};

// instantiate sketches
const p5Before = new p5(beforeSketch);
const p5After  = new p5(afterSketch);

/* ======= UPDATE VISUALIZATION ======= */
function updateVisualization(){
    /* read controls */
    const k = +document.getElementById('slider-convolver').value;   // kernel radius
    const N = +document.getElementById('slider-image-size').value;  // grid size
    let letter = document.getElementById('input-letter').value || ' ';
    letter = letter[0];                                             // first char
    const filter = document.querySelector('input[name="filter"]:checked').value;

    /* build BEFORE grid using off-screen canvas */
    const off = document.createElement('canvas');
    off.width = off.height = N;
    const ctx = off.getContext('2d');
    ctx.fillStyle = '#ffffff';
    ctx.fillRect(0,0,N,N);
    ctx.fillStyle = '#000000';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.font = `${N*0.8}px Arial`;
    ctx.fillText(letter, N/2, N/2);

    const imgData = ctx.getImageData(0,0,N,N).data;
    beforeGrid = new Array(N).fill().map(()=>new Array(N).fill(0));
    for(let i=0;i<N;i++){
        for(let j=0;j<N;j++){
            const idx = (i*N + j)*4;
            const val = imgData[idx];  // red channel enough
            beforeGrid[i][j] = val < 128 ? 1 : 0; // black => 1
        }
    }

    /* build convolution kernel */
    const D = 2*k + 1;
    let kernel = new Array(D).fill().map(()=>new Array(D).fill(0));

    if(filter === 'blur'){
        const v = 1/(D*D);
        for(let i=0;i<D;i++) for(let j=0;j<D;j++) kernel[i][j]=v;
    }else{ // shadow
        for(let i=0;i<k;i++){
            for(let j=0;j<k;j++){
                const row = k+1+i;
                const col = k+1+j;
                if(row<D && col<D) kernel[row][col]=1;
            }
        }
        const denom = k*k || 1; // avoid /0 when k==0 but slider min is 1
        for(let i=0;i<D;i++) for(let j=0;j<D;j++) kernel[i][j]/=denom;
    }

    /* apply convolution */
    afterGrid = new Array(N).fill().map(()=>new Array(N).fill(0));
    for(let i=0;i<N;i++){
        for(let j=0;j<N;j++){
            let acc = 0;
            for(let di=0;di<D;di++){
                for(let dj=0;dj<D;dj++){
                    const ii = i + di - k;
                    const jj = j + dj - k;
                    if(ii<0 || jj<0 || ii>=N || jj>=N) continue;
                    acc += kernel[di][dj] * beforeGrid[ii][jj];
                }
            }
            afterGrid[i][j] = acc;
        }
    }

    /* redraw both canvases */
    p5Before.redraw();
    p5After.redraw();
}

/* ======= EVENT LISTENERS ======= */
['slider-convolver','slider-image-size','input-letter'].forEach(id=>{
    document.getElementById(id).addEventListener('input', updateVisualization);
});
document.getElementById('radio-shadow').addEventListener('input', updateVisualization);
document.getElementById('radio-blur').addEventListener('input', updateVisualization);

/* plus/minus buttons */
function makeStepper(btnId, sliderId, delta){
    document.getElementById(btnId).addEventListener('click', ()=>{
        const slider = document.getElementById(sliderId);
        const step = +slider.step || 1;
        let val = +slider.value + delta*step;
        val = Math.max(+slider.min, Math.min(+slider.max, val));
        slider.value = val;
        slider.dispatchEvent(new Event('input'));
    });
}
makeStepper('convolver-plus-btn','slider-convolver',+1);
makeStepper('convolver-minus-btn','slider-convolver',-1);
makeStepper('image-size-plus-btn','slider-image-size',+1);
makeStepper('image-size-minus-btn','slider-image-size',-1);

/* arrow key cycling in letter input */
document.getElementById('input-letter').addEventListener('keydown',function(e){
    const alphabet='abcdefghijklmnopqrstuvwxyz';
    let chr = this.value.toLowerCase() || 'a';
    let idx = alphabet.indexOf(chr);
    if(idx===-1) idx = 0;

    if(e.key === 'ArrowUp'){
        idx = (idx+1)%26;
        this.value = alphabet[idx];
        this.dispatchEvent(new Event('input'));
        e.preventDefault();
    }else if(e.key === 'ArrowDown'){
        idx = (idx-1+26)%26;
        this.value = alphabet[idx];
        this.dispatchEvent(new Event('input'));
        e.preventDefault();
    }
});

/* ======= INITIAL RENDER ======= */
window.addEventListener('load', updateVisualization);
</script>
</body>
</html>
