<!-- ──  JavaScript  ──────────────────────────────────── -->
<script>
/* ───────────────────────────────────────────────────── */
/* ------------------  Global Variables ---------------- */
const aminoAcids = ['A','C','D','E','F','G','H','I','K','L','M','N','P','Q','R','S','T','V','W','Y'];
const NUM_RESIDUES = 400;

let proteinData = [];          // generated residues
let distanceMatrix = [];       // CA-CA distances
let currentView = '3D';        // '3D' or '2D'

/* 3D related */
let scene, camera, renderer, controls, atomGroup, boundingBox;
let animationId;

/* 2D related */
let p5Instance = null;

/* current selections */
let Residue_A = 1;
let Residue_X = 50;
let Residue_Y = 52;

/* ───────────────────────────────────────────────────── */
/* ------------------  Utility Functions --------------- */
function lerpColor(c1, c2, t){ // c1, c2 are THREE.Color
    return new THREE.Color(
        c1.r + (c2.r - c1.r)*t,
        c1.g + (c2.g - c1.g)*t,
        c1.b + (c2.b - c1.b)*t
    );
}
function randFromArray(arr){return arr[Math.floor(Math.random()*arr.length)];}

/* ───────────────────────────────────────────────────── */
/* ---------  Protein Generation & Pre-processing ------ */
function generateProteinData(n){
    let residues = [];
    let prevCA = new THREE.Vector3(0,0,0);
    for(let i=1;i<=n;i++){
        // Random unit vector for next step
        let dir = new THREE.Vector3(Math.random()*2-1,Math.random()*2-1,Math.random()*2-1).normalize();
        let caPos = prevCA.clone().add(dir.multiplyScalar(3.8)); // 3.8 Å between Cα
        prevCA = caPos;

        // Simple offsets for other atoms
        const offsets = {
            N : new THREE.Vector3(1, 0.5,-0.5),
            C : new THREE.Vector3(-1,-0.5, 0.5),
            O : new THREE.Vector3(0.5,1,-1),
            CB: new THREE.Vector3(-0.5,1, 1)
        };

        let atoms = [];
        atoms.push({atomName:'CA',pos:caPos.clone()});
        for(let name in offsets){
            atoms.push({atomName:name,pos:caPos.clone().add(offsets[name])});
        }

        residues.push({
            resName : randFromArray(aminoAcids),
            resSeq  : i,
            atoms   : atoms
        });
    }
    return residues;
}

function computeDistanceMatrix(){
    const n = proteinData.length;
    distanceMatrix = Array.from({length:n}, ()=>Array(n).fill(0));
    for(let i=0;i<n;i++){
        for(let j=i+1;j<n;j++){
            const d = proteinData[i].atoms[0].pos.distanceTo(proteinData[j].atoms[0].pos); // CA is first
            distanceMatrix[i][j]=distanceMatrix[j][i]=d;
        }
    }
}

/* ───────────────────────────────────────────────────── */
/* ---------------------  3D VIEW  --------------------- */
function create3DScene(){
    // Clean container first
    const container = document.getElementById('canvas-container');
    container.innerHTML = '';

    const plotSize = Number(document.getElementById('slider-plot-size').value);

    scene = new THREE.Scene();
    scene.background = new THREE.Color(0xffffff);

    camera = new THREE.PerspectiveCamera(45, 1, 0.1, 1000);
    camera.position.set(0,0,120);

    renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setSize(plotSize, plotSize);
    container.appendChild(renderer.domElement);

    controls = new THREE.OrbitControls(camera, renderer.domElement);

    // Lights
    scene.add(new THREE.AmbientLight(0x888888));
    const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
    dirLight.position.set(50,50,50);
    scene.add(dirLight);

    // Atom spheres
    atomGroup = new THREE.Group();
    const sphereGeo = new THREE.SphereGeometry(0.8, 16, 16);

    proteinData.forEach(res=>{
        res.atoms.forEach(atom=>{
            const mat = new THREE.MeshLambertMaterial();
            const mesh = new THREE.Mesh(sphereGeo, mat);
            mesh.position.copy(atom.pos);
            atom.mesh = mesh;                    // link back for updates
            atomGroup.add(mesh);
        });
    });
    scene.add(atomGroup);

    // Bounding box
    boundingBox = new THREE.Box3().setFromObject(atomGroup);
    const boxHelper = new THREE.BoxHelper(atomGroup, 0x888888);
    scene.add(boxHelper);

    animate3D();
    update3DColorsAndVisibility();
}

function animate3D(){
    animationId = requestAnimationFrame(animate3D);
    renderer.render(scene, camera);
}

function dispose3D(){
    if(animationId) cancelAnimationFrame(animationId);
    controls?.dispose();
    renderer?.dispose();
    scene = camera = renderer = controls = atomGroup = null;
    document.getElementById('canvas-container').innerHTML='';
}

function update3DColorsAndVisibility(){
    if(!atomGroup) return;
    const grayLevel = Number(document.getElementById('slider-gray-level').value);
    const mainColor = lerpColor(new THREE.Color(0x000000), new THREE.Color(0x0000ff), grayLevel);

    const showLateral = document.getElementById('checkbox-lateral-chains').checked;

    proteinData.forEach(res=>{
        res.atoms.forEach(atom=>{
            let color = mainColor;
            // visibility
            atom.mesh.visible = showLateral || atom.atomName==='CA';

            // Highlights
            if(res.resSeq === Residue_A){
                color = new THREE.Color(0xffff00); // yellow
            }
            if((res.resSeq === Residue_X || res.resSeq === Residue_Y) && atom.atomName==='CA'){
                color = new THREE.Color(0xff0000); // red
            }
            atom.mesh.material.color = color;
        });
    });
}

/* ───────────────────────────────────────────────────── */
/* --------------------  2D  VIEW  --------------------- */
function createContactMap(){
    const container = document.getElementById('canvas-container');
    container.innerHTML = '';
    const plotSize = Number(document.getElementById('slider-plot-size').value);

    p5Instance = new p5((p)=>{
        p.setup = function(){
            const cnv = p.createCanvas(plotSize, plotSize);
            cnv.parent(container);
            p.noLoop();
        };

        p.draw = function(){
            const rmsd = Number(document.getElementById('slider-rmsd').value);
            const size = p.width;
            const step = size / NUM_RESIDUES;

            // background & grid
            p.background(255);
            p.stroke(200);
            p.strokeWeight(1);
            for(let i=0;i<=NUM_RESIDUES;i+=100){
                let pos = i*step;
                p.line(pos,0,pos,size);
                p.line(0,pos,size,pos);
                p.noStroke();
                p.fill(0);
                p.textSize(8);
                p.textAlign(p.LEFT,p.TOP);
                p.text(i, pos+2,2);
                p.textAlign(p.RIGHT,p.BOTTOM);
                p.text(i, size-2,pos-2);
                p.stroke(200);
            }

            // points
            p.strokeWeight(2);
            p.stroke(0);
            for(let i=0;i<NUM_RESIDUES;i++){
                for(let j=0;j<=i;j++){
                    if(distanceMatrix[i][j]<=rmsd){
                        const x = i*step;
                        const y = j*step;
                        p.point(x,y);
                        if(i!==j) p.point(y,x); // mirror
                    }
                }
            }

            // Highlight lines
            p.strokeWeight(1);
            p.stroke(255,0,0);
            p.line((Residue_X-1)*step, 0, (Residue_X-1)*step, size); // vertical
            p.line(0,(Residue_Y-1)*step, size,(Residue_Y-1)*step);   // horizontal
        };
    });
}

function disposeContactMap(){
    if(p5Instance){
        p5Instance.remove();
        p5Instance = null;
    }
    document.getElementById('canvas-container').innerHTML='';
}

/* ───────────────────────────────────────────────────── */
/* --------------------   INFO UI   -------------------- */
function updateInfoDisplay(){
    const resA = proteinData[Residue_A-1];
    const resX = proteinData[Residue_X-1];
    const resY = proteinData[Residue_Y-1];
    const text = `Res A: ${resA.resName}${resA.resSeq} | Res X: ${resX.resName}${resX.resSeq} | Res Y: ${resY.resName}${resY.resSeq}`;
    document.getElementById('info-display').innerText = text;
}

/* ───────────────────────────────────────────────────── */
/* ---------------  Event Handlers & UI  --------------- */
function initUI(){
    // Populate residue select
    const select = document.getElementById('select-residue');
    aminoAcids.forEach(code=>{
        const opt = document.createElement('option');
        opt.value = code;
        opt.textContent = code;
        select.appendChild(opt);
    });

    // Button event listeners
    document.getElementById('btn-crystallography').addEventListener('click', ()=>{
        if(currentView==='3D') return;
        switchTo3D();
    });
    document.getElementById('btn-contact-map').addEventListener('click', ()=>{
        if(currentView==='2D') return;
        switchTo2D();
    });

    // Shared slider span updaters helper
    const sliderIds = [
        'slider-gray-level','slider-browse-sequence',
        'slider-residue-x','slider-residue-y',
        'slider-rmsd','slider-plot-size'
    ];
    sliderIds.forEach(id=>{
        const slider = document.getElementById(id);
        const span = document.getElementById(id.replace('slider-','')+'-value');
        slider.addEventListener('input', ()=>{
            span.textContent = slider.value;
            handleSliderChange(id);
        });
    });

    // Checkbox
    document.getElementById('checkbox-lateral-chains').addEventListener('change', ()=>{
        update3DColorsAndVisibility();
    });

    // Select residue
    select.addEventListener('change', ()=>{
        jumpToNextResidueOfType(select.value);
    });

    // Reset button
    document.getElementById('btn-reset').addEventListener('click', ()=>{
        if(currentView==='3D' && controls){
            controls.reset();
            camera.position.set(0,0,120);
        }
    });
}

function handleSliderChange(id){
    switch(id){
        case 'slider-gray-level':
            if(currentView==='3D') update3DColorsAndVisibility();
            break;
        case 'slider-browse-sequence':
            Residue_A = Number(document.getElementById('slider-browse-sequence').value);
            update3DColorsAndVisibility();
            updateInfoDisplay();
            break;
        case 'slider-residue-x':
            Residue_X = Number(document.getElementById('slider-residue-x').value);
            update3DColorsAndVisibility();
            if(currentView==='2D' && p5Instance) p5Instance.redraw();
            updateInfoDisplay();
            break;
        case 'slider-residue-y':
            Residue_Y = Number(document.getElementById('slider-residue-y').value);
            update3DColorsAndVisibility();
            if(currentView==='2D' && p5Instance) p5Instance.redraw();
            updateInfoDisplay();
            break;
        case 'slider-rmsd':
            if(currentView==='2D' && p5Instance) p5Instance.redraw();
            break;
        case 'slider-plot-size':
            resizePlot();
            break;
    }
}

function resizePlot(){
    const size = Number(document.getElementById('slider-plot-size').value);
    const container = document.getElementById('canvas-container');
    if(currentView==='3D' && renderer){
        renderer.setSize(size,size);
    }
    if(currentView==='2D' && p5Instance){
        p5Instance.resizeCanvas(size,size);
        p5Instance.redraw();
    }
}

/* ---------- jump to next residue -------------------- */
function jumpToNextResidueOfType(code){
    let currentIndex = Residue_A-1;
    for(let i=currentIndex+1;i<proteinData.length;i++){
        if(proteinData[i].resName===code){
            document.getElementById('slider-browse-sequence').value = i+1;
            document.getElementById('browse-sequence-value').textContent = i+1;
            handleSliderChange('slider-browse-sequence');
            return;
        }
    }
    // wrap around
    for(let i=0;i<=currentIndex;i++){
        if(proteinData[i].resName===code){
            document.getElementById('slider-browse-sequence').value = i+1;
            document.getElementById('browse-sequence-value').textContent = i+1;
            handleSliderChange('slider-browse-sequence');
            return;
        }
    }
}

/* ---------------  View Switchers -------------------- */
function switchTo3D(){
    currentView='3D';
    document.getElementById('btn-crystallography').classList.add('active');
    document.getElementById('btn-contact-map').classList.remove('active');
    disposeContactMap();
    create3DScene();
}

function switchTo2D(){
    currentView='2D';
    document.getElementById('btn-contact-map').classList.add('active');
    document.getElementById('btn-crystallography').classList.remove('active');
    dispose3D();
    createContactMap();
}

/* ───────────────────────────────────────────────────── */
/* --------------------   Start-up   ------------------- */
window.addEventListener('load',()=>{
    proteinData = generateProteinData(NUM_RESIDUES);
    computeDistanceMatrix();
    initUI();
    updateInfoDisplay();
    create3DScene(); // default view
});
</script>
</body>
</html>
