        flex-wrap:wrap;
        align-items:center;
        gap:10px 18px;
    }
    #controls-panel label{
        margin-right:4px;
    }
    #inputs-container{
        display:flex;
        flex-wrap:wrap;
        align-items:center;
        gap:10px;
    }
    #inputs-container input[type="number"]{
        width:60px;
    }
    #inputs-container input[type="range"]{
        width:160px;
    }
    /* Disable look */
    input:disabled{
        opacity:0.5;
    }

    /* ---------- Visualization ---------- */
    #visualization-area{
        margin-top:25px;
        text-align:center;
    }
    #equation-text{
        font-weight:bold;
        min-height:24px;
    }
    canvas{
        border:1px solid #aaa;
        background:white;
    }
</style>
</head>
<body>

<!-- ================== HEADER ================== -->
<div id="header-bar">
    <button id="info-toggle">+</button>
    <div id="info-content">
        <h2>Sylvester's Postage Stamp Problem</h2>
        <p>
            What is the greatest positive integer c such that the Diophantine equation
            a&nbsp;x&nbsp;+&nbsp;b&nbsp;y&nbsp;=&nbsp;c has no solution in non-negative integers?
            If a and b are relatively prime numbers, the equation has a solution in integers for
            any integer c and has a solution in non-negative integers for any integer c greater
            than a&nbsp;b&nbsp;−&nbsp;2.
        </p>
    </div>
</div>

<!-- ================== CONTROLS ================== -->
<div id="controls-panel">
    <input type="checkbox" id="checkbox-mode" checked>
    <label for="checkbox-mode">dots / numbers</label>

    <input type="checkbox" id="checkbox-display">
    <label for="checkbox-display">highest nonsolvable / current equation</label>

    <div id="inputs-container">
        <label for="input-a">a</label>
        <input type="number" id="input-a" min="1" max="20" step="1" value="7">

        <label for="input-b">b</label>
        <input type="number" id="input-b" min="1" max="20" step="1" value="8">

        <label for="slider-c">c</label>
        <input type="range" id="slider-c" min="1" max="112" step="1" value="24">
        <span id="display-c">24</span>
    </div>
</div>

<!-- ================== VISUALIZATION ================== -->
<div id="visualization-area">
    <p id="equation-text"></p>
    <div id="canvas-container"></div>
</div>



<script>
// ---------- Utility ----------
function gcd(a,b){
    while(b!==0){
        const t=b;
        b=a%b;
        a=t;
    }
    return a;
}

// ---------- DOM refs ----------
const infoToggle     = document.getElementById('info-toggle');
const infoContent    = document.getElementById('info-content');

const checkboxMode   = document.getElementById('checkbox-mode');
const checkboxDisplay= document.getElementById('checkbox-display');

const inputA         = document.getElementById('input-a');
const inputB         = document.getElementById('input-b');

const sliderC        = document.getElementById('slider-c');
const displayC       = document.getElementById('display-c');

const equationText   = document.getElementById('equation-text');

// ---------- State ----------
let aVal = parseInt(inputA.value,10);
let bVal = parseInt(inputB.value,10);
let cVal = parseInt(sliderC.value,10);
let p5Instance = null;

// ---------- Header toggle ----------
infoToggle.addEventListener('click', ()=>{
    const isHidden = infoContent.style.display==='none' || infoContent.style.display==='';

    infoContent.style.display = isHidden ? 'block' : 'none';
    infoToggle.textContent = isHidden ? '-' : '+';
});

// ---------- Control Handlers ----------
function updateSliderMax(){
    const newMax = aVal * bVal * 2;
    sliderC.max  = newMax;
    if(parseInt(sliderC.value,10) > newMax){
        sliderC.value = newMax;
        cVal = newMax;
        displayC.textContent = cVal;
    }
}

function syncDisableForFrobenius(isFMode){
    sliderC.disabled   = isFMode;
    displayC.style.opacity = isFMode?0.5:1;
}

function refreshEquationText(){
    if(gcd(aVal,bVal)!==1){
        equationText.textContent = 'Numbers a and b should be coprime!';
        return;
    }

    if(checkboxDisplay.checked){ // Frobenius number mode
        const F = aVal*bVal - aVal - bVal;
        equationText.textContent = `Frobenius number: F = ${aVal}×${bVal} - ${aVal} - ${bVal} = ${F}`;
    }else{
        equationText.textContent = `Current equation: ${aVal}·x + ${bVal}·y = ${cVal}`;
    }
}

// Handlers
[inputA, inputB].forEach(el=>{
    el.addEventListener('input', ()=>{
        aVal = Math.max(1, Math.min(20, parseInt(inputA.value,10) || 1));
        bVal = Math.max(1, Math.min(20, parseInt(inputB.value,10) || 1));
        inputA.value = aVal;
        inputB.value = bVal;

        updateSliderMax();
        refreshEquationText();
        p5Instance.redraw();
    });
});

sliderC.addEventListener('input', ()=>{
    cVal = parseInt(sliderC.value,10);
    displayC.textContent = cVal;
    refreshEquationText();
    p5Instance.redraw();
});

checkboxMode.addEventListener('change', ()=>{
    p5Instance.redraw();
});

checkboxDisplay.addEventListener('change', ()=>{
    syncDisableForFrobenius(checkboxDisplay.checked);
    refreshEquationText();
    p5Instance.redraw();
});

// ---------- p5.js sketch ----------
const sketch = (p)=>{
    const CANVAS_W = 550;
    const CANVAS_H = 550;
    const margin = 40;

    p.setup = function(){
        const cnv = p.createCanvas(CANVAS_W, CANVAS_H);
        cnv.parent('canvas-container');
        p.textFont('Arial',12);
        p.noLoop();
        refreshEquationText();
    };

    p.windowResized = ()=>{ p.redraw(); };

    p.redraw = function(){
        p.clear();
        p.background(255);

        // If not coprime, show nothing
        if(gcd(aVal,bVal)!==1){
            return;
        }

        // Determine grid scale
        const cellW = (CANVAS_W - margin*2)/bVal;
        const cellH = (CANVAS_H - margin*2)/aVal;
        const cell = Math.min(cellW, cellH);

        const axisXEnd = margin + cell*bVal;
        const axisYEnd = CANVAS_H - margin - cell*aVal;

        // Draw axes
        p.push();
        p.stroke(0);
        p.line(margin, CANVAS_H-margin, axisXEnd, CANVAS_H-margin); // x-axis
        p.line(margin, CANVAS_H-margin, margin, CANVAS_H-margin - cell*aVal); // y-axis

        // Axis labels
        p.fill(0,0,180);
        p.noStroke();
        p.textAlign(p.CENTER, p.TOP);
        for(let x=0; x<=bVal; x++){
            const sx = margin + x*cell;
            p.text(x, sx, CANVAS_H - margin + 4);
        }
        p.textAlign(p.RIGHT, p.CENTER);
        for(let y=0; y<=aVal; y++){
            const sy = CANVAS_H - margin - y*cell;
            p.text(y, margin-4, sy);
        }
        p.pop();

        // Main drawing
        const isDotsMode = checkboxMode.checked;
        const isFMode    = checkboxDisplay.checked;

        // Determine which c to use
        const currentC = isFMode ? (aVal*bVal - aVal - bVal) : cVal;

        if(isDotsMode){
            drawDotsMode(cell,currentC);
        }else{
            drawNumbersMode(cell,currentC,isFMode);
        }
    };

    // ----- Drawing helpers -----
    function gridToScreen(x,y,cell){
        const sx = margin + x*cell;
        const sy = CANVAS_H - margin - y*cell;
        return [sx, sy];
    }

    function drawDotsMode(cell,currentC){
        p.fill(0);
        p.stroke(0);
        for(let x=0; x<=bVal; x++){
            for(let y=0; y<=aVal; y++){
                const [sx,sy] = gridToScreen(x,y,cell);
                p.circle(sx,sy,5);
            }
        }

        // Line ax + by = c  => y = (c - a*x)/b
        p.push();
        p.stroke(200,0,0);
        p.strokeWeight(2);
        let x1 = currentC/aVal; // intercept x when y=0
        let y1 = currentC/bVal; // intercept y when x=0
        const p1 = gridToScreen(Math.min(x1,bVal), Math.max(0, y1), cell);
        const p2 = gridToScreen(Math.max(0, x1), Math.min(y1,aVal), cell);
        p.line(p1[0],p1[1],p2[0],p2[1]);
        p.pop();

        // Highlight solutions
        p.fill(0,180,0);
        p.noStroke();
        for(let x=0; x<=bVal; x++){
            for(let y=0; y<=aVal; y++){
                if(aVal*x + bVal*y === currentC){
                    const [sx,sy] = gridToScreen(x,y,cell);
                    p.circle(sx,sy,10);
                }
            }
        }
    }

    function computeUnsolvableUpToF(F){
        const reachable = new Array(F+1).fill(false);
        for(let x=0; x<=F/aVal; x++){
            for(let y=0; y<=F/bVal; y++){
                const v = aVal*x + bVal*y;
                if(v<=F) reachable[v]=true;
            }
        }
        const unsolved=[];
        for(let i=1;i<=F;i++){
            if(!reachable[i]) unsolved.push(i);
        }
        return unsolved;
    }

    function drawNumbersMode(cell,currentC,isFMode){
        p.textAlign(p.CENTER, p.CENTER);
        for(let x=0; x<=bVal; x++){
            for(let y=0; y<=aVal; y++){
                const v = aVal*x + bVal*y;
                const [sx,sy] = gridToScreen(x,y,cell);
                if(v === currentC && !isFMode){
                    p.fill(0,180,0);
                }else{
                    p.fill(0);
                }
                p.text(v, sx, sy);
            }
        }

        if(isFMode){
            const F = aVal*bVal - aVal - bVal;
            const unsolved = computeUnsolvableUpToF(F);
            // Display unsolved numbers below canvas
            p.noStroke();
            p.fill(180,0,0);
            p.textAlign(p.LEFT, p.TOP);
            p.textSize(12);
            const txt = 'Unsolvable (≤F): '+unsolved.join(', ');
            p.text(txt, margin, CANVAS_H - margin + 20);
        }
    }
};

p5Instance = new p5(sketch);

// ---------- Init ----------
syncDisableForFrobenius(false); // slider enabled
infoContent.style.display = 'none'; // ensure hidden

</script>
</body>
</html>
