    {name:'Aral Sea',lon:60.8,lat:45.0,found:false},
    {name:'Geneva',lon:6.6,lat:46.45,found:false},
    {name:'Tahoe',lon:-120.0,lat:39.1,found:false},
    {name:'Dead Sea',lon:35.5,lat:31.5,found:false}
];

/* ------------------------------------------------------------------------- */
/*                         DOM ELEMENT REFERENCES                            */
/* ------------------------------------------------------------------------- */
const tbl = document.getElementById('table-quiz');
const radioRivers = document.getElementById('radio-rivers');
const radioLakes  = document.getElementById('radio-lakes');
const btnReset    = document.getElementById('btn-reset');
const chkShowLoc  = document.getElementById('check-show-locations');
const chkShowSome = document.getElementById('check-show-some');
const pPosition   = document.getElementById('p-position');

/* ------------------------------------------------------------------------- */
/*                       APPLICATION STATE & HELPERS                         */
/* ------------------------------------------------------------------------- */
let currentData = riversData; // start with rivers
let currentType = 'rivers';
let hintSubset  = [];         // indices for hint labels

function populateTable(){
    tbl.innerHTML='';
    // Header Row
    const trHead = document.createElement('tr');
    currentData.forEach(item=>{
        const th = document.createElement('th');
        th.textContent=item.name;
        trHead.appendChild(th);
    });
    tbl.appendChild(trHead);
    // Status Row
    const trStat = document.createElement('tr');
    currentData.forEach(item=>{
        const td = document.createElement('td');
        td.textContent='False';
        trStat.appendChild(td);
    });
    tbl.appendChild(trStat);
}

function resetCurrent(){
    currentData.forEach(item=>item.found=false);
    // Update table cells
    const statusCells = tbl.rows[1].cells;
    [...statusCells].forEach(td=>{
        td.textContent='False';
        td.classList.remove('found');
    });
}

function chooseHintSubset(){
    hintSubset = [];
    if(!chkShowSome.checked) return;
    const unfound = currentData
                    .map((item,idx)=>({item,idx}))
                    .filter(obj=>!obj.item.found);
    if(unfound.length===0) return;
    // randomly choose up to 3 distinct indices
    const count = Math.min(3, unfound.length);
    while(hintSubset.length<count){
        const pick = unfound[Math.floor(Math.random()*unfound.length)].idx;
        if(!hintSubset.includes(pick)) hintSubset.push(pick);
    }
}

/* ------------------------------------------------------------------------- */
/*                               EVENT HANDLERS                              */
/* ------------------------------------------------------------------------- */
// Radio buttons
radioRivers.addEventListener('change',()=>{
    if(radioRivers.checked){
        currentData = riversData;
        currentType='rivers';
        populateTable();
    }
});
radioLakes.addEventListener('change',()=>{
    if(radioLakes.checked){
        currentData = lakesData;
        currentType='lakes';
        populateTable();
    }
});

// Reset button
btnReset.addEventListener('click',()=>{
    resetCurrent();
});

// Checkboxes
chkShowSome.addEventListener('change',()=>chooseHintSubset());

chkShowLoc.addEventListener('change',()=>{/* nothing extra, p5 draw uses flag */});

/* ------------------------------------------------------------------------- */
/*                              INITIALIZATION                               */
/* ------------------------------------------------------------------------- */
populateTable();

/* ------------------------------------------------------------------------- */
/*                               P5.JS SKETCH                                */
/* ------------------------------------------------------------------------- */
new p5(function(p){

    let worldImg;
    const tolerance = 15;

    p.preload = function(){
        worldImg = p.loadImage('https://upload.wikimedia.org/wikipedia/commons/c/c5/Blue_Marble_Next_Generation_%2B_topography_%2B_bathymetry.jpg');
    };

    p.setup = function(){
        const container = document.getElementById('canvas-container');
        const cnvWidth = container.clientWidth || 1000;
        const cnvHeight = Math.floor(cnvWidth/2); // keep 2:1 ratio
        const cnv = p.createCanvas(cnvWidth, cnvHeight);
        cnv.parent(container);
        p.noLoop();
        chooseHintSubset();
    };

    // Mapping helpers
    function mapToPixels(lon,lat){
        const x = p.map(lon,-180,180,0,p.width);
        const y = p.map(lat,90,-90,0,p.height);
        return {x,y};
    }
    function mapToGeo(x,y){
        const lon = p.map(x,0,p.width,-180,180);
        const lat = p.map(y,0,p.height,90,-90);
        return {lon,lat};
    }

    p.draw = function(){
        p.clear();
        p.image(worldImg,0,0,p.width,p.height);

        // Draw unfound location markers
        if(chkShowLoc.checked){
            currentData.forEach(obj=>{
                if(!obj.found){
                    const {x,y} = mapToPixels(obj.lon,obj.lat);
                    p.fill('red');
                    p.noStroke();
                    p.circle(x,y,10);
                }
            });
        }

        // Draw hints
        if(chkShowSome.checked){
            p.textSize(12);
            p.fill(255);
            hintSubset.forEach(idx=>{
                const obj = currentData[idx];
                if(!obj.found){
                    const {x,y} = mapToPixels(obj.lon,obj.lat);
                    p.text(obj.name,x+6,y-6);
                }
            });
        }

        // Cursor marker
        p.fill(173,216,230,150);
        p.noStroke();
        p.circle(p.mouseX,p.mouseY,30);
    };

    /* ------------------ p5 Event: Mouse Moved ------------------ */
    p.mouseMoved = function(){
        const withinCanvas = (p.mouseX>=0 && p.mouseX<=p.width &&
                              p.mouseY>=0 && p.mouseY<=p.height);
        if(withinCanvas){
            const {lon,lat} = mapToGeo(p.mouseX,p.mouseY);
            pPosition.textContent = `Current position {${Math.round(lon)}, ${Math.round(lat)}}`;
            p.redraw(); // update cursor position
        }
    };

    /* ------------------ p5 Event: Mouse Click ------------------ */
    p.mouseClicked = function(){
        const withinCanvas = (p.mouseX>=0 && p.mouseX<=p.width &&
                              p.mouseY>=0 && p.mouseY<=p.height);
        if(!withinCanvas) return;

        for(let i=0;i<currentData.length;i++){
            const obj = currentData[i];
            if(obj.found) continue;
            const {x,y} = mapToPixels(obj.lon,obj.lat);
            const d = p.dist(p.mouseX,p.mouseY,x,y);
            if(d<=tolerance){
                obj.found=true;
                // Update table cell
                const cell = tbl.rows[1].cells[i];
                cell.textContent='True';
                cell.classList.add('found');
                chooseHintSubset(); // refresh hints
                p.redraw(); // update marker rendering
                break;
            }
        }
    };

    /* ------------------ React to external UI changes ------------------ */
    // When user changes show-location checkbox or resets etc., redraw
    [chkShowLoc, chkShowSome, btnReset, radioRivers, radioLakes].forEach(elem=>{
        elem.addEventListener('change',()=>{chooseHintSubset(); p.redraw();});
        elem.addEventListener('click',()=>{chooseHintSubset(); p.redraw();});
    });

}, /* end p5 instance */ );
</script>
</body>
</html>
