<script>
/* ---------------- STATE ---------------- */
let currentTier = 5;        // 1–5
let showBinary   = false;   // checkbox
let showNodeChars= true;    // radio
/* ---------------- DATA ---------------- */
const nodes = [
    { id:'I',  label:'I', freq:5, tier:1, pos:{x:100,y:450}, children:[] },
    { id:'S',  label:'S', freq:4, tier:1, pos:{x:180,y:450}, children:[] },
