    { id:'E',  label:'E', freq:1, tier:1, pos:{x:580,y:450}, children:[] },
    { id:'_',  label:'_', freq:1, tier:1, pos:{x:660,y:450}, children:[] },
    { id:'IS', label:'IS', freq:9, tier:2, pos:{x:140,y:350}, children:['I','S'] },
    { id:'PR', label:'PR', freq:4, tier:2, pos:{x:300,y:350}, children:['P','R'] },
    { id:'MV', label:'MV', freq:2, tier:2, pos:{x:460,y:350}, children:['M','V'] },
    { id:'E_', label:'E_', freq:2, tier:2, pos:{x:620,y:350}, children:['E','_'] },
    { id:'MVE_', label:'MVE_', freq:4, tier:3, pos:{x:540,y:250}, children:['MV','E_'] },
    { id:'PRMVE_', label:'PRMVE_', freq:8, tier:4, pos:{x:420,y:150}, children:['PR','MVE_'] },
    { id:'ROOT', label:'ISPRMVE_', freq:17, tier:5, pos:{x:280,y: 50}, children:['IS','PRMVE_'] }
];
// Map for quick lookup
const nodeMap = Object.fromEntries(nodes.map(n=>[n.id,n]));
/* ----------------- UI HELPERS ----------------- */
function setActiveTierButton(){
    for(let i=1;i<=5;i++){
        const btn=document.getElementById('btn-tier-'+i);
        btn.classList.toggle('active', i===currentTier);
    }
}
function updateExplanation(){
    const exp = document.getElementById('explanation-text');
    if(currentTier===1){
        exp.textContent="After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.";
    }else if(currentTier>=2 && currentTier<=4){
        exp.textContent="We continue grouping until we get to the highest tier.";
    }else{
        exp.textContent="Once at the top, the total frequency becomes 17, which is the total number of characters.";
    }
}
function updateTableVisibility(){
    const tableDiv = document.getElementById('encoding-table');
    const encodedP = document.getElementById('encoded-text');
    tableDiv.style.display   = currentTier>=2 ? 'block':'none';
    encodedP.style.display   = currentTier===5 ? 'block':'none';
}
function buildEncodingTable(){
    const lines=[
        'I = 00',
        'S = 01',
        'P = 100',
        'R = 101',
        'M = 1100',
        'V = 1101',
        'E = 1110',
        '_ = 1111'
    ];
    const div=document.getElementById('encoding-table');
    div.innerHTML=lines.map(l=>`<p>${l}</p>`).join('');
}
/* ---------------- EVENTS ---------------- */
function attachEvents(){
    // Tier buttons
    for(let i=1;i<=5;i++){
        document.getElementById('btn-tier-'+i).addEventListener('click',()=>{
            currentTier=i;
            setActiveTierButton();
            updateExplanation();
            updateTableVisibility();
            redraw();   // p5
        });
    }
    // Checkbox
    document.getElementById('checkbox-binary').addEventListener('change',evt=>{
        showBinary=evt.target.checked;
        redraw();
    });
    // Radio buttons
    document.getElementById('radio-chars-yes').addEventListener('change',evt=>{
        if(evt.target.checked){
            showNodeChars=true;
            redraw();
        }
    });
    document.getElementById('radio-chars-no').addEventListener('change',evt=>{
        if(evt.target.checked){
            showNodeChars=false;
            redraw();
        }
    });
}
/* ---------------- p5.js SKETCH ---------------- */
let sketch = (p)=>{
    p.setup=()=>{
        const canvasHolder=document.getElementById('canvas-area');
        let c=p.createCanvas(canvasHolder.clientWidth, canvasHolder.clientHeight);
        c.parent(canvasHolder);
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(12);
        p.rectMode(p.CENTER);
        p.noLoop();
    };
    p.windowResized=()=>{
        const canvasHolder=document.getElementById('canvas-area');
        p.resizeCanvas(canvasHolder.clientWidth, canvasHolder.clientHeight);
        p.redraw();
    };
    p.draw=()=>{
        p.clear();
        p.background(255);
        p.stroke('#C70000');
        p.fill('#FEFDD5');

        // ------------ draw edges ------------
        nodes.forEach(parent=>{
            if(parent.tier>currentTier) return;
            parent.children.forEach((childId,idx)=>{
                const child=nodeMap[childId];
                if(child.tier>currentTier) return;
                p.stroke('#C70000');
                p.strokeWeight(2);
                p.line(parent.pos.x,parent.pos.y,child.pos.x,child.pos.y);
                // binary label
                if(showBinary){
                    const midX = (parent.pos.x+child.pos.x)/2;
                    const midY = (parent.pos.y+child.pos.y)/2;
                    p.noStroke();
                    p.fill('#000');
                    p.text(idx===0?'0':'1',midX,midY);
                }
            });
        });
        // ------------ draw nodes ------------
        nodes.forEach(node=>{
            if(node.tier>currentTier) return;
            const isLeaf = node.children.length===0;
            // determine text label
            let txt;
            if(isLeaf){
                txt = `${node.label}(${node.freq})`;
            }else{
                txt = showNodeChars ? `${node.label}(${node.freq})` : `(${node.freq})`;
            }
            const padding = 8;
            const w = p.textWidth(txt)+padding*2;
            const h = 26;
            // node box
            p.stroke('#C70000');
            p.fill('#FEFDD5');
            p.rect(node.pos.x,node.pos.y,w,h,6);
            // text
            p.noStroke();
            p.fill('#000');
            p.text(txt,node.pos.x,node.pos.y);
        });
    };
};
new p5(sketch);
/* ------------- INITIAL SETUP ------------- */
document.addEventListener('DOMContentLoaded',()=>{
    buildEncodingTable();
    setActiveTierButton();
    attachEvents();
    updateTableVisibility();
});
</script>
</body>
</html>
