 ********************************************************************/
const functionSketch = new p5(p => {
    let canvasW, canvasH;
    const xMin = 0, xMax = 10.5, yMin = 0, yMax = 2.2;

    /* --- coordinate mapping helpers --- */
    const mapX = x => p.map(x, xMin, xMax, 0, canvasW);
    const mapY = y => p.map(y, yMin, yMax, canvasH, 0);   // invert y-axis

    p.setup = function(){
        /* Remove placeholder canvas if it exists (avoid duplicate) */
        const placeholder = document.getElementById('canvas-function');
        if(placeholder) placeholder.remove();

        /* Dynamic sizing based on parent width */
        const parent = document.getElementById('function-plot-container');
        canvasW = parent.clientWidth  - 20; // padding adjustment
        canvasH = parent.clientHeight - 20;

        const c = p.createCanvas(canvasW, canvasH);
        c.parent('function-plot-container');
        c.id('canvas-function');

        p.noLoop();          // draw only on demand
    };

    p.redraw = function(){   // override to ensure custom canvas size on resize
        resizeCanvasIfNeeded();
        p._redraw();         // call the original internal redraw
    };

    function resizeCanvasIfNeeded(){
        const parent = document.getElementById('function-plot-container');
        const w      = parent.clientWidth  - 20;
        const h      = parent.clientHeight - 20;
        if(w !== canvasW || h !== canvasH){
            canvasW = w; canvasH = h;
            p.resizeCanvas(canvasW, canvasH);
        }
    }

    p.draw = function(){
        p.clear();
        p.background(255);

        /* ---- Axes ---- */
        p.stroke(0); p.strokeWeight(1);
        // x-axis
        p.line(mapX(xMin), mapY(0), mapX(xMax), mapY(0));
        // y-axis
        p.line(mapX(0), mapY(yMin), mapX(0), mapY(yMax));

        /* Ticks */
        p.textSize(12); p.fill(0); p.noStroke();
        for(let x = 1; x <= 10; x++){
            let px = mapX(x);
            p.stroke(0);
            p.line(px, mapY(0)-4, px, mapY(0)+4);
            p.noStroke(); p.text(x, px-3, mapY(0)+18);
        }
        for(let y = 0; y <= 2; y++){
            let py = mapY(y);
            p.stroke(0);
            p.line(mapX(0)-4, py, mapX(0)+4, py);
            p.noStroke(); p.text(y, mapX(0)-20, py+5);
        }

        /* ---- Function curve y=2/x ---- */
        p.noFill(); p.stroke('#0077ff'); p.strokeWeight(2);
        p.beginShape();
        for(let x = 0.5; x <= 10; x += 0.05){
            p.vertex(mapX(x), mapY(2/x));
        }
        p.endShape();

        /* ---- Rectangles for series ---- */
        for(let k=1; k<=currentN; k++){
            const col = colors[(k-1)%colors.length];
            const xLeft  = k;
            const xRight = k+1;
            const yTop   = 2/k;
            const yBot   = 2/(k+1);

            p.fill(col);
            p.stroke(150);
            p.rect(
                mapX(xLeft),
                mapY(yTop),
                mapX(xRight) - mapX(xLeft),
                mapY(yBot)  - mapY(yTop)
            );

            /* dashed guide line for first 3 rectangles */
            if(k <= 3){
                p.push();
                p.stroke(128);
                p.drawingContext.setLineDash([5,5]);
                p.line(mapX(k), mapY(0), mapX(k), mapY(2/k));
                p.pop();
            }
        }
    };
});

/********************************************************************
 *  p5.js  –  Summation Plot  (Right)
 ********************************************************************/
const sumSketch = new p5(p => {
    let canvasW, canvasH;
    const xMin = 0, xMax = 1.5, yMin = 0, yMax = 2.2;

    /* coordinate helpers */
    const mapX = x => p.map(x, xMin, xMax, 0, canvasW);
    const mapY = y => p.map(y, yMin, yMax, canvasH, 0);

    p.setup = function(){
        const placeholder = document.getElementById('canvas-sum');
        if(placeholder) placeholder.remove();

        const parent = document.getElementById('sum-plot-container');
        canvasW = parent.clientWidth  - 20;
        canvasH = parent.clientHeight - 20;

        const c = p.createCanvas(canvasW, canvasH);
        c.parent('sum-plot-container');
        c.id('canvas-sum');

        p.noLoop();
    };

    p.redraw = function(){
        resizeCanvasIfNeeded();
        p._redraw();
    };

    function resizeCanvasIfNeeded(){
        const parent = document.getElementById('sum-plot-container');
        const w = parent.clientWidth  - 20;
        const h = parent.clientHeight - 20;
        if(w !== canvasW || h !== canvasH){
            canvasW = w; canvasH = h;
            p.resizeCanvas(canvasW, canvasH);
        }
    }

    p.draw = function(){
        p.clear();
        p.background(255);

        /* Axes */
        p.stroke(0); p.strokeWeight(1);
        // x-axis
        p.line(mapX(xMin), mapY(0), mapX(xMax), mapY(0));
        // y-axis
        p.line(mapX(0), mapY(yMin), mapX(0), mapY(yMax));

        /* Ticks */
        p.textSize(12); p.fill(0); p.noStroke();
        // x ticks 0,1
        for(let x=0; x<=1; x++){
            let px = mapX(x);
            p.stroke(0);
            p.line(px, mapY(0)-4, px, mapY(0)+4);
            p.noStroke(); p.text(x, px-3, mapY(0)+18);
        }
        // y ticks 0,1,2
        for(let y=0; y<=2; y++){
            let py = mapY(y);
            p.stroke(0);
            p.line(mapX(0)-4, py, mapX(0)+4, py);
            p.noStroke(); p.text(y, mapX(0)-20, py+5);
        }

        /* Stacked rectangles */
        let yStack = 0;
        for(let k=currentN; k>=1; k--){
            const h = 2/k - 2/(k+1);
            const col = colors[(k-1)%colors.length];

            p.fill(col);
            p.stroke(150);
            p.rect(
                mapX(0),
                mapY(yStack + h),
                mapX(1) - mapX(0),
                mapY(yStack) - mapY(yStack + h)
            );

            yStack += h;
        }
    };
});

/* Initial draw */
functionSketch.redraw();
sumSketch.redraw();

/* window resize -> redraw */
window.addEventListener('resize', () => {
    functionSketch.redraw();
    sumSketch.redraw();
});
</script>
</body>
</html>
