    .hidden{display:none;}
</style>
</head>
<body>
<div id="main-container">
    <!-- CONTROL PANEL ------------------------------------------------------->
    <div id="control-panel">
        <!-- Model selector -->
        <div class="button-group" id="model-selector">
            <button id="btn-monod"  class="active">Monod</button>
            <button id="btn-si">substrate inhibition</button>
        </div>
        <!-- View selector -->
        <div class="button-group" id="view-selector">
            <button id="btn-bifurcation" class="active">bifurcation diagram</button>
            <button id="btn-nontrivial">nontrivial steady state</button>
        </div>
        <!-- PARAMETERS ------------------------------------------------------>
        <div class="slider-container" id="container-x2f">
            <label for="slider-x2f">x<sub>2f</sub> <span id="value-x2f"></span></label>
            <input type="range" id="slider-x2f" min="1" max="10" step="0.1" value="4.0">
        </div>
        <div class="slider-container" id="container-umax">
            <label for="slider-umax">μ<sub>max</sub> <span id="value-umax"></span></label>
            <input type="range" id="slider-umax" min="0.1" max="2" step="0.01" value="0.6">
        </div>
        <div class="slider-container" id="container-km">
            <label for="slider-km">k<sub>m</sub> <span id="value-km"></span></label>
            <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
        </div>
        <div class="slider-container hidden" id="container-k1">
            <label for="slider-k1">k<sub>1</sub> <span id="value-k1"></span></label>
            <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
        </div>
        <div class="slider-container" id="container-ds">
            <label for="slider-ds">D<sub>s</sub> <span id="value-ds"></span></label>
            <input type="range" id="slider-ds" min="0.01" max="1.0" step="0.01" value="0.6">
        </div>
    </div>

    <!-- VISUALIZATION AREA -------------------------------------------------->
    <div id="visualization-area">
        <div id="plot-container" style="width:95%;height:95%;"></div>
    </div>
</div>

<script>
/* ==================== GLOBAL STATE ===================================== */
let currentModel = 'monod';      // 'monod' or 'si'
let currentView  = 'bifurcation';// 'bifurcation' or 'nontrivial'

const params = {
    x2f : 4.0,
    umax: 0.6,
    km  : 0.12,
    k1  : 0.4545,
    ds  : 0.6
};

/* ==================== HELPER FUNCTIONS ================================= */
function formatVal(id, val){
    const decimals = (id==='x2f'||id==='k1') ? 4 : (id==='umax'||id==='km'||id==='ds') ? 3 : 2;
    return val.toFixed(decimals);
}

function updateValueDisplays(){
    document.getElementById('value-x2f').textContent = formatVal('x2f',params.x2f);
    document.getElementById('value-umax').textContent = formatVal('umax',params.umax);
    document.getElementById('value-km').textContent   = formatVal('km',params.km);
    document.getElementById('value-k1').textContent   = formatVal('k1',params.k1);
    document.getElementById('value-ds').textContent   = formatVal('ds',params.ds);
}

/* D(x) =============================================================== */
function D_monod(x){           // x = x2
    return params.umax * x / (params.km + x);
}
function D_si(x){
    return params.umax * x / (params.km + x + params.k1 * x * x);
}
function dDdx_si(x){
    const k = params.k1, km = params.km, umax = params.umax;
    const num = umax*(km + x + k*x*x) - umax*x*(1 + 2*k*x);
    const den = Math.pow(km + x + k*x*x, 2);
    return num/den;
}

/* µ(x) =============================================================== */
function mu_monod(x){ return D_monod(x); }
function mu_si(x){ return D_si(x); }

/* Root finding: simple scanning + bisection -------------------------- */
function findRoots(f, a, b, nSamples=1000, tol=1e-6, maxIter=50){
    const roots=[];
    const xs = [];
    for(let i=0;i<=nSamples;i++){
        xs.push(a + (b-a)*i/nSamples);
    }
    let prev = f(xs[0]);
    for(let i=1;i<=nSamples;i++){
        const curr = f(xs[i]);
        if(prev===0){ roots.push(xs[i-1]); }
        if(prev*curr<0){
            let low=xs[i-1], high=xs[i], mid;
            for(let k=0;k<maxIter;k++){
                mid = 0.5*(low+high);
                const fm = f(mid);
                if(Math.abs(fm)<tol) break;
                if(f(low)*fm<0) high=mid; else low=mid;
            }
            roots.push(mid);
        }
        prev=curr;
    }
    return roots;
}

/* ==================== PLOTTING FUNCTIONS =============================== */
function updatePlot(){
    if(currentView==='bifurcation'){
        drawBifurcationDiagram();
    }else{
        drawNontrivialStatePlot();
    }
}

function drawBifurcationDiagram(){
    const x2max = params.x2f;
    const N = 400;
    const x2 = Array.from({length:N+1}, (_,i)=> i*x2max/N);

    const Dvals = x2.map(x => currentModel==='monod'? D_monod(x): D_si(x));

    const traces=[];

    /* Nontrivial steady state curve ----------------------------------*/
    if(currentModel==='monod'){
        traces.push({
            x: Dvals,
            y: x2,
            mode:'lines',
            line:{color:'blue',width:3},
            name:'stable branch'
        });
    }else{ // SI model
        const x2crit = Math.sqrt(params.km/params.k1);
        const idxCrit = x2.findIndex(v=>v>=x2crit);
        const x2_stable = x2.slice(0, idxCrit+1);
        const D_stable  = Dvals.slice(0, idxCrit+1);
        const x2_unstab = x2.slice(idxCrit);
        const D_unstab  = Dvals.slice(idxCrit);

        traces.push({
            x: D_stable,
            y: x2_stable,
            mode:'lines',
            line:{color:'blue',width:3},
            name:'stable branch'
        });
        traces.push({
            x: D_unstab,
            y: x2_unstab,
            mode:'lines',
            line:{color:'red',width:3,dash:'dash'},
            name:'unstable branch'
        });
    }

    /* Trivial steady state line (y = x2f) ----------------------------*/
    const D_bif = currentModel==='monod'? D_monod(params.x2f): D_si(params.x2f);
    const Daxis=[0,1.0];
    traces.push({
        x:[Daxis[0], D_bif],
        y:[params.x2f, params.x2f],
        mode:'lines',
        line:{color:'red',width:3,dash:'dash'},
        name:'trivial unstable'
    });
    traces.push({
        x:[D_bif, Daxis[1]],
        y:[params.x2f, params.x2f],
        mode:'lines',
        line:{color:'blue',width:3},
        name:'trivial stable'
    });

    /* Intersection points -------------------------------------------*/
    const f = x => (currentModel==='monod'?D_monod(x):D_si(x)) - params.ds;
    const roots = findRoots(f, 0, params.x2f);
    roots.forEach(r=>{
        const dDdx = currentModel==='monod'? D_monod(r+1e-6)-D_monod(r): dDdx_si(r);
        const stable = dDdx>0;
        traces.push({
            x:[params.ds],
            y:[r],
            mode:'markers',
            marker:{size:10,color:stable?'magenta':'cyan'},
            name: stable?'stable SS':'unstable SS',
            showlegend:false
        });
    });
    // trivial root
    traces.push({
        x:[params.ds],
        y:[params.x2f],
        mode:'markers',
        marker:{size:10,color:'green'},
        name:'trivial SS',
        showlegend:false
    });

    /* Layout --------------------------------------------------------*/
    const layout={
        title:'Bifurcation Diagram',
        xaxis:{title:'dilution rate (D)', range:[0,1.0]},
        yaxis:{title:'X₂', range:[0,6]},
        shapes:[
            {type:'line',x0:params.ds,x1:params.ds,y0:0,y1:6,
             line:{color:'gray',width:2,dash:'dot'}}
        ],
        legend:{orientation:'h', x:0.02, y:-0.15},
        margin:{l:60,r:20,t:40,b:60}
    };

    Plotly.newPlot('plot-container', traces, layout, {responsive:true});
}

function drawNontrivialStatePlot(){
    /* µ(x) curve ----------------------------------------------------*/
    const xmax = 7;
    const N=400;
    const xs = Array.from({length:N+1}, (_,i)=> i*xmax/N);
    const mus = xs.map(x=> currentModel==='monod'?mu_monod(x):mu_si(x));

    const traces=[{
        x:xs,
        y:mus,
        mode:'lines',
        line:{color:'blue',width:3},
        name:'µ(x₂)'
    }];

    /* Horizontal Ds line -------------------------------------------*/
    traces.push({
        x:[0,xmax],
        y:[params.ds,params.ds],
        mode:'lines',
        line:{color:'gray',width:2},
        name:'Dₛ'
    });

    /* Bistability region (SI only) ---------------------------------*/
    if(currentModel==='si'){
        const D_upper = params.umax / (1 + 2*Math.sqrt(params.k1*params.km));
        const D_lower = mu_si(params.x2f);
        // dashed lines
        traces.push({
            x:[0,xmax], y:[D_upper,D_upper],
            mode:'lines',
            line:{color:'red',width:2,dash:'dash'},
            name:'D_upper'
        });
        traces.push({
            x:[0,xmax], y:[D_lower,D_lower],
            mode:'lines',
            line:{color:'red',width:2,dash:'dash'},
            name:'D_lower'
        });
        // shaded region
        traces.push({
            x:[0,xmax,xmax,0],
            y:[D_lower,D_lower,D_upper,D_upper],
            fill:'toself',
            fillcolor:'rgba(255,192,203,0.3)',
            line:{width:0},
            type:'scatter',
            hoverinfo:'skip',
            showlegend:false
        });
    }

    /* Intersection roots -------------------------------------------*/
    const g = x=> (currentModel==='monod'?mu_monod(x):mu_si(x)) - params.ds;
    const roots = findRoots(g, 0, xmax);
    roots.forEach(r=>{
        const dMudx = (currentModel==='monod' ? params.umax*params.km/Math.pow(params.km+r,2)
                                              : dDdx_si(r)); // derivative same as dDdx_si
        const stable = dMudx>0;
        traces.push({
            x:[r],
            y:[params.ds],
            mode:'markers',
            marker:{size:10,color:stable?'magenta':'cyan'},
            showlegend:false
        });
    });

    /* Layout --------------------------------------------------------*/
    const layout={
        title:'Nontrivial Steady-State Plot',
        xaxis:{title:'substrate concentration (X₂)', range:[0,xmax]},
        yaxis:{title:'specific growth rate (µ)', range:[0,1]},
        legend:{orientation:'h', x:0.02, y:-0.15},
        margin:{l:60,r:20,t:40,b:60}
    };

    Plotly.newPlot('plot-container', traces, layout,{responsive:true});
}

/* ==================== EVENT HANDLERS =================================== */
function addButtonHandlers(){
    document.getElementById('btn-monod').addEventListener('click',()=>{
        if(currentModel==='monod') return;
        currentModel='monod';
        toggleActive('btn-monod','btn-si');
        document.getElementById('container-k1').classList.add('hidden');
        updatePlot();
    });
    document.getElementById('btn-si').addEventListener('click',()=>{
        if(currentModel==='si') return;
        currentModel='si';
        toggleActive('btn-si','btn-monod');
        document.getElementById('container-k1').classList.remove('hidden');
        updatePlot();
    });
    document.getElementById('btn-bifurcation').addEventListener('click',()=>{
        if(currentView==='bifurcation')return;
        currentView='bifurcation';
        toggleActive('btn-bifurcation','btn-nontrivial');
        updatePlot();
    });
    document.getElementById('btn-nontrivial').addEventListener('click',()=>{
        if(currentView==='nontrivial')return;
        currentView='nontrivial';
        toggleActive('btn-nontrivial','btn-bifurcation');
        updatePlot();
    });
}
function toggleActive(activeID,inactiveID){
    document.getElementById(activeID).classList.add('active');
    document.getElementById(inactiveID).classList.remove('active');
}

function addSliderHandlers(){
    const sliders = [
        {id:'slider-x2f',key:'x2f'},
        {id:'slider-umax',key:'umax'},
        {id:'slider-km',key:'km'},
        {id:'slider-k1',key:'k1'},
        {id:'slider-ds',key:'ds'}
    ];
    sliders.forEach(s=>{
        const el = document.getElementById(s.id);
        el.addEventListener('input',()=>{
            params[s.key] = parseFloat(el.value);
            updateValueDisplays();
            updatePlot();
        });
    });
}

/* ==================== INITIALIZATION =================================== */
function init(){
    updateValueDisplays();
    addButtonHandlers();
    addSliderHandlers();
    updatePlot();
}
window.addEventListener('DOMContentLoaded',init);
</script>
</body>
</html>
