            <input type="range" id="slider-time" min="0" max="24" step="0.01" value="12">
            <span id="value-time">12.00</span>
        </div>
    </div>

    <!-- =================== MAIN CONTENT =================== -->
    <div id="main-content">
        <!-- globe -->
        <div id="globe-container"></div>

        <!-- plots -->
        <div id="plots-container">
            <div id="plot-instant"></div>
            <div id="plot-legend">
                <div class="legend-item">
                    <div class="legend-color red"></div>
                    <span>equator</span>
                </div>
                <div class="legend-item">
                    <div class="legend-color black"></div>
                    <span>selected latitude</span>
                </div>
                <div class="legend-item">
                    <div class="legend-color blue"></div>
                    <span>north pole</span>
                </div>
            </div>
            <div id="plot-cumulative"></div>
        </div>
    </div>
</div>

<script>
/* ================================================================
                        GLOBAL CONSTANTS
================================================================ */
const SOLAR_CONSTANT = 1361; // W/m^2
const DEG2RAD = Math.PI/180;
const rad = (deg)=>deg*DEG2RAD;
const hours = [...Array(25).keys()]; // 0..24

/* ================================================================
                        THREE  /  GLOBE
================================================================ */
let scene, camera, renderer, controls;
let earthGroup, earthMesh, dirLight;
let selectedLatLine=null;
let axisArrow, normalArrow, sunArrow;

function initGlobe(){
    const container = document.getElementById('globe-container');
    // Scene & Camera
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(35, container.clientWidth/container.clientHeight, 0.1, 100);
    camera.position.set(0,1.5,3);

    // Renderer
    renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setSize(container.clientWidth, container.clientHeight);
    renderer.setPixelRatio(window.devicePixelRatio);
    container.appendChild(renderer.domElement);

    // Controls
    controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    // Group that will rotate for time-of-day
    earthGroup = new THREE.Group();
    scene.add(earthGroup);

    // Earth sphere
    const geometry = new THREE.SphereGeometry(1,64,64);
    const material = new THREE.MeshPhongMaterial({map:generateEarthTexture()});
    earthMesh = new THREE.Mesh(geometry, material);
    earthGroup.add(earthMesh);

    // Simple graticule wireframe
    const wire = new THREE.WireframeGeometry(new THREE.SphereGeometry(1.005,24,12));
    const lineMat = new THREE.LineBasicMaterial({color:0xffffff,opacity:0.2,transparent:true});
    const wireframe = new THREE.LineSegments(wire, lineMat);
    earthGroup.add(wireframe);

    // Lights
    dirLight = new THREE.DirectionalLight(0xffffff, 1.2);
    dirLight.position.set(0,1,1);
    scene.add(dirLight);
    scene.add(new THREE.AmbientLight(0x222222));

    // Pole Axis Arrow (north)
    axisArrow = new THREE.ArrowHelper(new THREE.Vector3(0,1,0), new THREE.Vector3(0, -1, 0), 2, 0x000000, 0.1,0.05);
    scene.add(axisArrow);

    // Surface normal arrow placeholder
    normalArrow = new THREE.ArrowHelper(new THREE.Vector3(0,1,0), new THREE.Vector3(0,0,0), 0.8, 0x000000);
    scene.add(normalArrow);

    // Sun vector arrow placeholder
    sunArrow = new THREE.ArrowHelper(new THREE.Vector3(0,1,0), new THREE.Vector3(0,0,0), 1.2, 0xff0000);
    scene.add(sunArrow);

    window.addEventListener('resize', onWindowResize);
    animate();
}

function generateEarthTexture(){
    const canvas = document.createElement('canvas');
    canvas.width = 1024; canvas.height = 512;
    const ctx = canvas.getContext('2d');
    // ocean
    ctx.fillStyle = '#244b7b';
    ctx.fillRect(0,0,canvas.width, canvas.height);
    // crude continents (just green blotches)
    ctx.fillStyle = '#3b8c3b';
    for(let i=0;i<200;i++){
        const x = Math.random()*canvas.width;
        const y = Math.random()*canvas.height;
        const r = Math.random()*30+20;
        ctx.beginPath();
        ctx.ellipse(x,y,r,r*0.6,0,0,Math.PI*2);
        ctx.fill();
    }
    return new THREE.CanvasTexture(canvas);
}

function onWindowResize(){
    const container = document.getElementById('globe-container');
    camera.aspect = container.clientWidth/container.clientHeight;
    camera.updateProjectionMatrix();
    renderer.setSize(container.clientWidth, container.clientHeight);
}

function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
}

/* ================================================================
                        PLOTLY
================================================================ */
let instantFig, cumulativeFig;
function initPlots(){
    // Instantaneous
    const traceEquator = {x:hours, y:hours.map(()=>0), mode:'lines', name:'equator', line:{color:'#d62728'}};
    const traceLat     = {x:hours, y:hours.map(()=>0), mode:'lines', name:'selected', line:{color:'#111'}};
    const tracePole    = {x:hours, y:hours.map(()=>0), mode:'lines', name:'north pole', line:{color:'#1f77b4'}};
    const traceMarker  = {x:[12], y:[0], mode:'markers', marker:{color:'#000', size:8}, showlegend:false};

    const layoutInstant = {
        margin:{l:50,r:10,t:30,b:40},
        title:'instantaneous energy',
        xaxis:{title:'time (hr)', range:[0,24]},
        yaxis:{title:'watts/m²', range:[0,1400]},
        showlegend:false
    };
    Plotly.newPlot('plot-instant',[traceEquator,traceLat,tracePole,traceMarker], layoutInstant, {displayModeBar:false});

    // Cumulative
    const traceEquatorC = {x:hours, y:hours.map(()=>0), mode:'lines', name:'equator', line:{color:'#d62728'}};
    const traceLatC     = {x:hours, y:hours.map(()=>0), mode:'lines', name:'selected', line:{color:'#111'}};
    const tracePoleC    = {x:hours, y:hours.map(()=>0), mode:'lines', name:'north pole', line:{color:'#1f77b4'}};
    const layoutCum = {
        margin:{l:50,r:10,t:30,b:40},
        title:'cumulative energy',
        xaxis:{title:'time (hr)', range:[0,24]},
        yaxis:{title:'watt·hours/m²', range:[0,14000]},
        showlegend:false
    };
    Plotly.newPlot('plot-cumulative',[traceEquatorC,traceLatC,tracePoleC], layoutCum, {displayModeBar:false});
}

/* ================================================================
                        PHYSICS & UPDATE
================================================================ */
function solarDeclination(season){
    // δ = 23.5° * cos(2π*season)
    return 23.5 * Math.cos(2*Math.PI*season);
}
function hourAngle(hour){
    return 15*(hour - 12); // degrees
}
function solarEnergyAt(latDeg, declDeg, hour){
    const lat = rad(latDeg);
    const decl = rad(declDeg);
    const HRA = rad(hourAngle(hour));
    const szaCos = Math.sin(lat)*Math.sin(decl) + Math.cos(lat)*Math.cos(decl)*Math.cos(HRA); // cosine of zenith
    return SOLAR_CONSTANT * Math.max(0, szaCos);
}
function cumulative(arr){
    const out=[];
    let sum=0;
    const dt=1; // hour step
    for(let i=0;i<arr.length;i++){
        // integrate using rectangle (simple)
        sum += arr[i]*dt;
        out.push(sum);
    }
    return out;
}
function update(){
    // read sliders
    const season = parseFloat(document.getElementById('slider-season').value);
    const lat    = parseFloat(document.getElementById('slider-latitude').value);
    const time   = parseFloat(document.getElementById('slider-time').value);

    // display numeric
    document.getElementById('value-season').innerText = season.toFixed(3);
    document.getElementById('value-latitude').innerText = lat.toFixed(2);
    document.getElementById('value-time').innerText = time.toFixed(2);

    /* ---------- THREE / Globe Updates ---------- */
    // earth rotation
    earthGroup.rotation.y = (time/24)*Math.PI*2;

    // light position / direction determined by declination
    const decl = solarDeclination(season);
    const declRad = rad(decl);
    const sunDir = new THREE.Vector3(0, Math.sin(declRad), -Math.cos(declRad)).normalize();
    dirLight.position.copy(sunDir.clone().multiplyScalar(5));

    // update latitude line
    if(selectedLatLine) earthGroup.remove(selectedLatLine);
    selectedLatLine = makeLatitudeLine(lat, 0x1f77b4, true);
    earthGroup.add(selectedLatLine);

    // surface point (lat, lon 0°) in local (before rotation)
    const latRad = rad(lat);
    let surfaceLocal = new THREE.Vector3( Math.cos(latRad), Math.sin(latRad), 0); // unit sphere radius 1
    // rotate with earthGroup to world coords
    const rotMatrix = new THREE.Matrix4().makeRotationY(earthGroup.rotation.y);
    let surfaceWorld = surfaceLocal.clone().applyMatrix4(rotMatrix);

    // update arrows
    // surface normal
    normalArrow.position.copy(surfaceWorld.clone().multiplyScalar(1)); // at surface
    normalArrow.setDirection(surfaceWorld.clone().normalize());
    normalArrow.setLength(0.8);

    // sun vector arrow
    sunArrow.position.copy(surfaceWorld.clone().multiplyScalar(1));
    sunArrow.setDirection(sunDir.clone().normalize());
    sunArrow.setLength(1.2);

    /* ---------- Plot Updates ---------- */
    // arrays
    const declDeg = decl;
    const eqE   = hours.map(h=>solarEnergyAt(0, declDeg, h));
    const latE  = hours.map(h=>solarEnergyAt(lat, declDeg, h));
    const poleE = hours.map(h=>solarEnergyAt(90, declDeg, h));

    const eqCum   = cumulative(eqE);
    const latCum  = cumulative(latE);
    const poleCum = cumulative(poleE);

    // Instantaneous
    Plotly.react('plot-instant',[
        {x:hours, y:eqE, mode:'lines', line:{color:'#d62728'}},
        {x:hours, y:latE, mode:'lines', line:{color:'#111'}},
        {x:hours, y:poleE, mode:'lines', line:{color:'#1f77b4'}},
        {x:[time], y:[solarEnergyAt(lat, declDeg, time)], mode:'markers',
         marker:{color:'#000', size:8}, showlegend:false}
    ],{
        margin:{l:50,r:10,t:30,b:40},
        title:'instantaneous energy',
        xaxis:{title:'time (hr)', range:[0,24]},
        yaxis:{title:'watts/m²', range:[0,1400]},
        showlegend:false
    },{displayModeBar:false});

    // Cumulative
    Plotly.react('plot-cumulative',[
        {x:hours, y:eqCum, mode:'lines', line:{color:'#d62728'}},
        {x:hours, y:latCum, mode:'lines', line:{color:'#111'}},
        {x:hours, y:poleCum, mode:'lines', line:{color:'#1f77b4'}},
    ],{
        margin:{l:50,r:10,t:30,b:40},
        title:'cumulative energy',
        xaxis:{title:'time (hr)', range:[0,24]},
        yaxis:{title:'watt·hours/m²', range:[0,14000]},
        showlegend:false
    },{displayModeBar:false});
}

// helper to create latitude circle
function makeLatitudeLine(latDeg, color=0xffffff, dashed=false){
    const latRad = rad(latDeg);
    const radius = Math.cos(latRad); // projection on xy-plane
    const segments = 128;
    const points=[];
    for(let i=0;i<=segments;i++){
        const theta = i/segments*Math.PI*2;
        const x = radius*Math.cos(theta);
        const y = Math.sin(latRad);
        const z = radius*Math.sin(theta);
        points.push(new THREE.Vector3(x,y,z));
    }
    const geometry = new THREE.BufferGeometry().setFromPoints(points);
    let material;
    if(dashed){
        material = new THREE.LineDashedMaterial({color:color,dashSize:0.05,gapSize:0.025});
    }else{
        material = new THREE.LineBasicMaterial({color:color});
    }
    const circle = new THREE.Line(geometry, material);
    if(dashed) circle.computeLineDistances();
    return circle;
}

/* ================================================================
                        EVENT LISTENERS
================================================================ */
document.getElementById('slider-season')  .addEventListener('input', update);
document.getElementById('slider-latitude').addEventListener('input', update);
document.getElementById('slider-time')    .addEventListener('input', update);

/* ================================================================
                        INITIALIZE EVERYTHING
================================================================ */
initGlobe();
initPlots();
update();
</script>
</body>
</html>
