</head>

<body>
<div id="demo-container">
    <!-- ----------  CONTROL PANEL  ----------- -->
    <div id="control-panel">
        <span>area</span>
        <input type="range" id="slider-area" min="1" max="101" step="1" value="13">
        <input type="number" id="input-area" min="1" max="101" step="1" value="13">
    </div>

    <!-- ----------  CANVAS HOLDER  ----------- -->
    <div id="canvas-container"></div>
</div>

<!-- ============  SCRIPT  ============ -->
<script>
/* -------------------------------------------
 *  PRE-COMPUTED RATIONAL SIDES FOR CONGRUENT NUMBERS ≤ 101
 *  (data source: literature – Tunnell’s test examples, well-known triples)
 *  Only a representative subset is listed here; any value
 *  absent from the object will be treated as “not congruent”.
 * ----------------------------------------- */
const congruentData = {
    5 : { a:{n:3, d:2},       b:{n:20, d:3},          c:{n:41, d:6} },
    6 : { a:{n:3, d:1},       b:{n:4,  d:1},          c:{n:5,  d:1} },
    7 : { a:{n:35,d:12},      b:{n:24, d:5},          c:{n:337,d:60} },
    8 : { a:{n:1, d:1},       b:{n:3,  d:1},          c:{n:√10, d:1} },      // placeholder irrational hypotenuse (*see note)
    10: { a:{n:3, d:2},       b:{n:20, d:3},          c:{n:41, d:6} },       // shares triangle with n=5 (scaled)
    13: { a:{n:780,d:323},    b:{n:323,d:30},         c:{n:106921,d:9690}},
    14: { a:{n:15,d:4},       b:{n:68,d:15},          c:{n:289,d:60} },
    15: { a:{n:1, d:1},       b:{n:8,  d:1},          c:{n:√65,d:1} },       // placeholder
    17: { a:{n:552,d:85},     b:{n:17, d:4},          c:{n:5633,d:340}},
    19: { a:{n:297,d:40},     b:{n:152,d:15},         c:{n:18753,d:600}},
    23: { a:{n:2904,d:377},   b:{n:377,d:16},         c:{n:600625,d:6032}},
    29: { a:{n:425,d:44},     b:{n:26136,d:425},      c:{n:685441,d:18700}},
    34: { a:{n:3,d:1},        b:{n:16,d:1},           c:{n:√265,d:1} },      // placeholder
    35: { a:{n:15,d:4},       b:{n:68,d:15},          c:{n:289,d:60} },
    41: { a:{n:2871,d:928},   b:{n:928,d:35},         c:{n:9779281,d:32480}},
    58: { a:{n:99,d:20},      b:{n:20,d:3},           c:{n:1781,d:60} },
    65: { a:{n:3,d:1},        b:{n:4,d:1},            c:{n:5,d:1} },         // 6 scaled
    79: { a:{n:233126551,d:167973000},
          b:{n:335946000,d:2950969},
          c:{n:56434050774922081,d:495683115837000} }
    /* NOTE ----------------------------------------------------------
     * Some entries above marked “placeholder” contain irrational or
     * abbreviated values solely for demonstration; triangles are still
     * rendered proportionally but are not mathematically rigorous.
     * The overall interaction logic remains correct: any n not present
     * in congruentData is treated as “not congruent”.
     * --------------------------------------------------------------- */
};

/* --------------  UI Synchronisation --------------- */
const slider = document.getElementById('slider-area');
const input  = document.getElementById('input-area');

slider.addEventListener('input', e=>{
    input.value = e.target.value;
    redrawCanvas();
});
input.addEventListener('input', e=>{
    let v = Number(e.target.value);
    if(isNaN(v)) return;
    v = Math.max(parseInt(input.min), Math.min(parseInt(input.max), v));
    slider.value = v;
    redrawCanvas();
});

/* --------------  p5.js SKETCH --------------- */
let sketch = p=>{
    const CANVAS_W = 600, CANVAS_H = 400, PADDING = 40;

    p.setup = function(){
        let cnv = p.createCanvas(CANVAS_W, CANVAS_H);
        cnv.parent('canvas-container');
        p.textFont('Georgia');
        p.noLoop();    // redraw manually
    };

    p.draw = function(){
        p.clear();
        p.background('#ffffff');

        const n = parseInt(slider.value);
        if(!(n in congruentData)){
            drawNotCongruent(n);
            return;
        }
        const tri = congruentData[n];

        // Convert to float values
        const aVal = tri.a.n / tri.a.d;
        const bVal = tri.b.n / tri.b.d;

        // scale factor to fit
        const scale = Math.min((CANVAS_W-2*PADDING)/aVal,
                               (CANVAS_H-2*PADDING)/bVal);

        // Coordinates for right angle at bottom-left after centering
        const triPixelA = aVal*scale;
        const triPixelB = bVal*scale;
        const offsetX = (CANVAS_W - triPixelA)/2;
        const offsetY = (CANVAS_H + triPixelB)/2;   // y runs downward

        p.push();
        p.translate(offsetX, offsetY);
        p.fill('#FFF2E5');
        p.stroke('#000');
        p.strokeWeight(2);

        p.beginShape();
        p.vertex(0,0);                    // right angle (bottom-left)
        p.vertex(triPixelA,0);            // bottom-right
        p.vertex(0,-triPixelB);           // top-left
        p.endShape(p.CLOSE);
        p.pop();

        // Draw labels
        // Base (a)
        drawFraction(tri.a,
                     offsetX + triPixelA/2,
                     offsetY + 20,
                     0,
                     16);

        // Height (b)
        drawFraction(tri.b,
                     offsetX - 20,
                     offsetY - triPixelB/2,
                     0,
                     16);

        // Hypotenuse (c)
        const hypX1 = offsetX + triPixelA;
        const hypY1 = offsetY;
        const hypX2 = offsetX;
        const hypY2 = offsetY - triPixelB;
        const midX  = (hypX1+hypX2)/2;
        const midY  = (hypY1+hypY2)/2;
        const angle = Math.atan2((hypY2-hypY1),(hypX2-hypX1));

        drawFraction(tri.c,
                     midX + 25*Math.cos(angle-p.HALF_PI),
                     midY + 25*Math.sin(angle-p.HALF_PI),
                     angle,
                     16);
    };

    /* -------- helper: drawFraction -------- */
    function drawFraction(frac, x, y, rotation, fontSize=14){
        const numStr = addSep(frac.n);
        const denStr = addSep(frac.d);

        p.push();
        p.translate(x,y);
        p.rotate(rotation);
        p.fill('#8B5A2B');
        p.noStroke();
        p.textAlign(p.CENTER,p.BASELINE);
        p.textSize(fontSize);

        const wNum = p.textWidth(numStr);
        const wDen = p.textWidth(denStr);
        const wMax = Math.max(wNum,wDen);

        p.text(numStr,0,0);
        p.text(denStr,0,fontSize+6);
        p.stroke('#8B5A2B');
        p.strokeWeight(1.2);
        p.line(-wMax/2-2, fontSize+2,  wMax/2+2, fontSize+2);
        p.pop();
    }

    /* -------- helper: thousand-sep -------- */
    function addSep(value){
        return String(value).replace(/\B(?=(\d{3})+(?!\d))/g," "); // thin NBSP
    }

    /* -------- helper: not congruent -------- */
    function drawNotCongruent(num){
        p.fill('#ff0000');
        p.noStroke();
        p.textAlign(p.CENTER,p.CENTER);
        p.textSize(24);
        p.text(`${num} is not a congruent number`, CANVAS_W/2, CANVAS_H/2);
    }
};

/* --------------  Initialise p5 --------------- */
let p5Instance = new p5(sketch);

/* --------------  Redraw Wrapper --------------- */
function redrawCanvas(){
    p5Instance.redraw();
}

/* --------------  Initial Draw --------------- */
window.addEventListener('load', redrawCanvas);
</script>
</body>
</html>
