        <div id="output-primal" class="output-box">x = 3.00, y = 3.00, C = 6.00</div>
        <div id="output-dual" class="output-box">x = 2.00, y = 2.00, Q = 2.00</div>
    </div>

</div>

<!-- THREE.js & OrbitControls -->
<script src="https://unpkg.com/three@0.155.0/build/three.min.js"></script>
<script src="https://unpkg.com/three@0.155.0/examples/js/controls/OrbitControls.js"></script>

<script>
// === GLOBAL VARIABLES & HELPERS === //
const defaults = {
    alpha:0.5, beta:0.5, w:1.0, r:1.0,
    Q:3.0, C:4.0
};
const maxXY = 10;          // computational / plotting bound

// References to dynamic 3D objects so that they can be removed & rebuilt
let primalMeshes = [];
let dualMeshes   = [];

// === DOM REFERENCES === //
const sliders = {
    alpha: document.getElementById('slider-alpha'),
    beta : document.getElementById('slider-beta'),
    w    : document.getElementById('slider-w'),
    r    : document.getElementById('slider-r'),
    Q    : document.getElementById('slider-Q'),
    C    : document.getElementById('slider-C')
};
const labels  = {
    alpha: document.getElementById('label-alpha'),
    beta : document.getElementById('label-beta'),
    w    : document.getElementById('label-w'),
    r    : document.getElementById('label-r'),
    Q    : document.getElementById('label-Q'),
    C    : document.getElementById('label-C')
};
const outputs = {
    primal: document.getElementById('output-primal'),
    dual  : document.getElementById('output-dual')
};
document.getElementById('btn-reset').addEventListener('click', ()=>{
    Object.keys(sliders).forEach(k=>{
        sliders[k].value = defaults[k];
        labels[k].textContent = defaults[k];
    });
    update();
});
document.getElementById('btn-C-from-primal').addEventListener('click', ()=>{
    sliders.C.value = currentResults.C_out.toFixed(2);
    labels.C.textContent = parseFloat(sliders.C.value).toFixed(2);
    update();
});
document.getElementById('btn-Q-from-dual').addEventListener('click', ()=>{
    sliders.Q.value = currentResults.Q_out.toFixed(2);
    labels.Q.textContent = parseFloat(sliders.Q.value).toFixed(2);
    update();
});

// attach oninput listeners for sliders
Object.keys(sliders).forEach(k=>{
    sliders[k].addEventListener('input', ()=>{
        labels[k].textContent = parseFloat(sliders[k].value).toFixed( k==='alpha'||k==='beta'?2: (k==='w'||k==='r'?2:1) );
        update();
    });
});

// === 3D SETUP === //
function buildScene(container){
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f0f0);

    const camera = new THREE.PerspectiveCamera(45, container.clientWidth/container.clientHeight, 0.1, 100);
    camera.position.set(12,12,12);

    const renderer = new THREE.WebGLRenderer({antialias:true, alpha:false});
    renderer.setSize(container.clientWidth, container.clientHeight);
    container.appendChild(renderer.domElement);

    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    // axes
    scene.add(new THREE.AxesHelper(12));
    // bounding box
    const box = new THREE.Box3().set(new THREE.Vector3(0,0,0), new THREE.Vector3(maxXY,maxXY,maxXY));
    const boxHelper = new THREE.Box3Helper(box, 0x999999);
    scene.add(boxHelper);
    // light
    scene.add(new THREE.AmbientLight(0xffffff,0.9));

    return {scene,camera,renderer,controls};
}

const primalPlot = buildScene(document.getElementById('plot-primal'));
const dualPlot   = buildScene(document.getElementById('plot-dual'));

// resize handling
window.addEventListener('resize', ()=>{
    [primalPlot, dualPlot].forEach(p=>{
        const c = p.renderer.domElement.parentElement;
        p.camera.aspect = c.clientWidth / c.clientHeight;
        p.camera.updateProjectionMatrix();
        p.renderer.setSize(c.clientWidth, c.clientHeight);
    });
});

function animate(){
    requestAnimationFrame(animate);
    [primalPlot, dualPlot].forEach(p=>p.controls.update());
    primalPlot.renderer.render(primalPlot.scene, primalPlot.camera);
    dualPlot.renderer.render(dualPlot.scene, dualPlot.camera);
}
animate();

// === CORE CALCULATIONS === //
let currentResults = {x_p:0,y_p:0,C_out:0,x_d:0,y_d:0,Q_out:0};

function solvePrimal(alpha,beta,w,r,Qin){
    const k = (w*beta)/(r*alpha);
    const x = Math.pow( Qin / Math.pow(k,beta), 1/(alpha+beta) );
    const y = x * k;
    const C = w*x + r*y;
    return {x,y,C};
}
function solveDual(alpha,beta,w,r,Cin){
    const x = (Cin*alpha)/(w*(alpha+beta));
    const y = (Cin*beta)/(r*(alpha+beta));
    const Q = Math.pow(x,alpha) * Math.pow(y,beta);
    return {x,y,Q};
}

// === GEOMETRY BUILDERS === //
function clearObjects(arr, scene){
    arr.forEach(o=>scene.remove(o));
    arr.length = 0;
}

function buildPrimalGeometry(alpha,beta,w,r,Qin,res){
    clearObjects(primalMeshes, primalPlot.scene);

    // orange isoquant curtain (wall)
    const isoGeometry = new THREE.BufferGeometry();
    const steps = 80;
    const verts = [];
    const indices=[];
    const curtainHeight = maxXY;
    for(let i=0;i<=steps;i++){
        const x = (i/steps)*maxXY;
        const y = Math.pow( Qin / Math.pow(x===0?0.0001:x,alpha), 1/beta);
        verts.push(x, y, 0);
        verts.push(x, y, curtainHeight);
    }
    // build faces
    for(let i=0;i<steps;i++){
        const a = i*2, b=a+1, c=a+2, d=a+3;
        indices.push(a,b,d); indices.push(a,d,c);
    }
    isoGeometry.setIndex(indices);
    isoGeometry.setAttribute('position', new THREE.Float32BufferAttribute(verts,3));
    isoGeometry.computeVertexNormals();
    const isoMaterial = new THREE.MeshLambertMaterial({color:0xff9800, side:THREE.DoubleSide, transparent:true, opacity:0.6});
    const isoMesh = new THREE.Mesh(isoGeometry, isoMaterial);
    primalMeshes.push(isoMesh); primalPlot.scene.add(isoMesh);

    // cost plane (semi-transparent gray)
    const planeSize=maxXY;
    const planeSeg=32;
    const planeGeom=new THREE.PlaneGeometry(planeSize,planeSize,planeSeg,planeSeg);
    // re-map vertices: planeGeom in x(-s/2..s/2) y(-s/2..s/2) z=0 (facing +z)
    const pos=planeGeom.attributes.position;
    for(let i=0;i<pos.count;i++){
        let x=pos.getX(i)+planeSize/2;     // shift to 0..max
        let y=pos.getY(i)+planeSize/2;
        let z=w*x + r*y;
        pos.setZ(i,z);
    }
    planeGeom.computeVertexNormals();
    const planeMat=new THREE.MeshLambertMaterial({color:0x9e9e9e, transparent:true, opacity:0.4, side:THREE.DoubleSide});
    const planeMesh=new THREE.Mesh(planeGeom, planeMat);
    primalMeshes.push(planeMesh); primalPlot.scene.add(planeMesh);
    // shift so plane bottom aligns with (0,0,0)
    planeMesh.position.set(0,0,0);
    planeMesh.rotation.x=Math.PI; // flip so +y is depth (consistent orientation)

    // blue solution vector
    const p0=new THREE.Vector3(0,0,0);
    const p1=new THREE.Vector3(res.x,res.y,res.C);
    const solGeom=new THREE.BufferGeometry().setFromPoints([p0,p1]);
    const solMat=new THREE.LineBasicMaterial({color:0x1976d2, linewidth:4});
    const solLine=new THREE.Line(solGeom, solMat);
    primalMeshes.push(solLine); primalPlot.scene.add(solLine);
}

function buildDualGeometry(alpha,beta,w,r,Cin,res){
    clearObjects(dualMeshes, dualPlot.scene);

    // production surface (orange)
    const steps=80;
    const prodGeom=new THREE.BufferGeometry();
    const verts=[];
    const indices=[];
    for(let i=0;i<=steps;i++){
        for(let j=0;j<=steps;j++){
            const x=(i/steps)*maxXY;
            const y=(j/steps)*maxXY;
            const z=Math.pow(x,alpha)*Math.pow(y,beta);
            verts.push(x, y, z);
        }
    }
    for(let i=0;i<steps;i++){
        for(let j=0;j<steps;j++){
            const a=i*(steps+1)+j;
            const b=a+1;
            const c=a+(steps+1);
            const d=c+1;
            indices.push(a,c,b); indices.push(b,c,d);
        }
    }
    prodGeom.setIndex(indices);
    prodGeom.setAttribute('position', new THREE.Float32BufferAttribute(verts,3));
    prodGeom.computeVertexNormals();
    const prodMat=new THREE.MeshLambertMaterial({color:0xff9800, side:THREE.DoubleSide, transparent:false, opacity:0.8});
    const prodMesh=new THREE.Mesh(prodGeom, prodMat);
    dualMeshes.push(prodMesh); dualPlot.scene.add(prodMesh);

    // cost curtain (gray vertical wall)
    const curtainGeom=new THREE.BufferGeometry();
    const verts2=[];
    const indices2=[];
    const curtainHeight=maxXY;
    for(let i=0;i<=steps;i++){
        const x=(i/steps)*maxXY;
        const y= (Cin - w*x)/r;
        if(y<0 || y>maxXY) continue;
        verts2.push(x,y,0);
        verts2.push(x,y,curtainHeight);
        if(i>0){
            const base=(verts2.length/3)-2;
            indices2.push(base,base+1,base-1);
            indices2.push(base-1,base+1,base-2);
        }
    }
    curtainGeom.setIndex(indices2);
    curtainGeom.setAttribute('position', new THREE.Float32BufferAttribute(verts2,3));
    curtainGeom.computeVertexNormals();
    const curtainMat=new THREE.MeshLambertMaterial({color:0x9e9e9e, side:THREE.DoubleSide, transparent:true, opacity:0.4});
    const curtainMesh=new THREE.Mesh(curtainGeom, curtainMat);
    dualMeshes.push(curtainMesh); dualPlot.scene.add(curtainMesh);

    // red intersection curve (budget line on surface)
    const curvePts=[];
    for(let i=0;i<=steps;i++){
        const x=(i/steps)*maxXY;
        const y= (Cin - w*x)/r;
        if(y<0) continue;
        const z=Math.pow(x,alpha)*Math.pow(y,beta);
        curvePts.push(new THREE.Vector3(x,y,z));
    }
    const curveGeom=new THREE.BufferGeometry().setFromPoints(curvePts);
    const curveMat=new THREE.LineBasicMaterial({color:0xd32f2f, linewidth:3});
    const curveLine=new THREE.Line(curveGeom, curveMat);
    dualMeshes.push(curveLine); dualPlot.scene.add(curveLine);

    // optimal point marker (small sphere)
    const sphereGeom=new THREE.SphereGeometry(0.2,16,16);
    const sphereMat=new THREE.MeshStandardMaterial({color:0x757575});
    const sphereMesh=new THREE.Mesh(sphereGeom, sphereMat);
    sphereMesh.position.set(res.x,res.y,res.Q);
    dualMeshes.push(sphereMesh); dualPlot.scene.add(sphereMesh);
}

// === UPDATE FUNCTION === //
function update(){
    const alpha=parseFloat(sliders.alpha.value);
    const beta =parseFloat(sliders.beta.value);
    const w    =parseFloat(sliders.w.value);
    const r    =parseFloat(sliders.r.value);
    const Qin  =parseFloat(sliders.Q.value);
    const Cin  =parseFloat(sliders.C.value);

    // solve problems
    const primal = solvePrimal(alpha,beta,w,r,Qin);
    const dual   = solveDual(alpha,beta,w,r,Cin);

    currentResults = {x_p:primal.x,y_p:primal.y,C_out:primal.C,x_d:dual.x,y_d:dual.y,Q_out:dual.Q};

    outputs.primal.textContent=`x = ${primal.x.toFixed(2)}, y = ${primal.y.toFixed(2)}, C = ${primal.C.toFixed(2)}`;
    outputs.dual  .textContent=`x = ${dual.x.toFixed(2)}, y = ${dual.y.toFixed(2)}, Q = ${dual.Q.toFixed(2)}`;

    // rebuild 3D geometry
    buildPrimalGeometry(alpha,beta,w,r,Qin, primal);
    buildDualGeometry(alpha,beta,w,r,Cin, dual);
}

// === INITIALIZATION === //
update();
</script>
</body>
</html>
