<body>

<div id="demo-container">

    <!-- Section 1: Control Panel -->
    <div id="control-panel">
        <!-- Flash Pressure Slider -->
        <div class="control-row">
            <label for="slider-pressure">flash pressure</label>
            <input type="range" id="slider-pressure" min="100" max="1000" step="1" value="180">
            <span id="pressure-value">180 kPa</span>
        </div>
        <!-- Heat Load Slider -->
        <div class="control-row">
            <label for="slider-heat">heat load</label>
            <input type="range" id="slider-heat" min="0" max="10000" step="100" value="0">
            <span id="heat-value">0 kJ/hr</span>
        </div>
    </div>

    <!-- Section 2: Visualization Area -->
    <div id="visualization-area"></div>

</div>

<!-- CDN for p5.js -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

<script>
/* --------------------------- GLOBAL CONSTANTS --------------------------- */
const feedFlow = 10;               // kmol/h
const T_F = 323.2;                 // K
const P_F = 1000;                  // kPa
const z = [0.25,0.25,0.25,0.25];   // overall feed mole fractions

// Antoine constants A, B, C  (for P_sat in kPa, T in K)
const antoine = [
    {A:6.80165, B:1005.157, C:-27.159},  // n-butane
    {A:6.86177, B:1184.249, C:-35.223},  // n-pentane
    {A:6.90237, B:1358.59 , C:-51.303},  // n-heptane
    {A:6.92383, B:1431.149, C:-59.501}   // n-octane
];
// Liquid heat capacity (Cp) kJ/kmol·K and heat of vaporization ΔH_vap kJ/kmol
const CpL =   [132,161,225,255];
const Hvap =  [22400,25800,31800,34400];

/* --------------------------- STATE --------------------------- */
let P_flash = 180;   // kPa   (default)
let Q_hr    = 0;     // kJ/hr (default)

let flashResult = {};  // will hold latest calculation

/* --------------------------- UTILITY FUNCTIONS --------------------------- */

// Antoine equation => returns P_sat in kPa
function Psat(i, T){
    const {A,B,C} = antoine[i];
    return Math.pow(10, A - B / (T + C));
}

// calculate K-values array
function Kvalues(T, P){
    return antoine.map((_,i)=> Psat(i,T)/P);
}

// Solve Rachford-Rice for vapor fraction V using bisection
function solveV(z, K){
    // check trivial cases
    const f0 = z.reduce((sum,zi, i)=> sum + zi*(K[i]-1),0); // at V=0 denom=1
    if (Math.abs(f0) < 1e-12) return 0;
    const f1 = z.reduce((sum,zi,i)=> sum + zi*(K[i]-1)/(1+ (K[i]-1)),0); // V=1
    // Ensure solution exists in (0,1)
    let low=0, high=1, mid;
    // If f0 and f1 same sign, return closest bound
    if (f0 * f1 > 0){
        return (Math.abs(f0)<Math.abs(f1))?0:1;
    }
    for(let iter=0; iter<100; iter++){
        mid = 0.5*(low+high);
        let fmid = 0;
        for(let i=0;i<z.length;i++){
            fmid += z[i]*(K[i]-1)/(1+mid*(K[i]-1));
        }
        if (Math.abs(fmid) < 1e-10) break;
        if (fmid*f0>0){
            low = mid; f0 = fmid;
        }else{
            high = mid;
        }
    }
    return mid;
}

// Main flash calculation
function calculateFlash(P_kPa, Q){
    // search for temperature that satisfies energy balance
    let Tmin = 250;   // K
    let Tmax = 500;   // K
    let Tmid, V, Ks, x, y;

    // helper residual function
    function energyResidual(T){
        Ks = Kvalues(T, P_kPa);
        V = solveV(z, Ks);

        // x_i and y_i
        x = z.map((zi, i)=> zi / (1 + V*(Ks[i]-1)));
        y = x.map((xi,i)=> Ks[i]*xi);

        // left side: input heat + sensible cooling of feed (positive if feed hotter)
        const sens = z.reduce((sum,zi,i)=> sum + zi*CpL[i]*(T_F - T), 0);
        const lhs = Q/feedFlow + sens; // kJ/kmol
        // right side: vaporisation enthalpy requirement
        const rhs = V * y.reduce((sum,yi,i)=> sum + yi*Hvap[i], 0);
        return lhs - rhs; // want zero
    }

    let fmin = energyResidual(Tmin);
    let fmax = energyResidual(Tmax);

    // Expand range if same sign
    if (fmin*fmax>0){
        // simple fallback: return Tmin
        Tmid = T_F;
    }else{
        // bisection
        for(let it=0; it<100; it++){
            Tmid = 0.5*(Tmin+Tmax);
            const fmid = energyResidual(Tmid);
            if (Math.abs(fmid) < 1e-6) break;
            if (fmin*fmid>0){
                Tmin = Tmid; fmin = fmid;
            }else{
                Tmax = Tmid; fmax = fmid;
            }
        }
    }
    // Final properties already computed during last residual call
    return {
        T_flash: Tmid,
        V_frac: V,
        x: x,
        y: y
    };
}

/* --------------------------- EVENT HANDLERS --------------------------- */
const pressureSlider = document.getElementById('slider-pressure');
const heatSlider     = document.getElementById('slider-heat');
const pressureDisplay= document.getElementById('pressure-value');
const heatDisplay    = document.getElementById('heat-value');

function updateValues(){
    P_flash = parseFloat(pressureSlider.value);
    Q_hr    = parseFloat(heatSlider.value);
    pressureDisplay.textContent = P_flash + ' kPa';
    heatDisplay.textContent     = Q_hr + ' kJ/hr';

    flashResult = calculateFlash(P_flash, Q_hr);
}

pressureSlider.addEventListener('input', ()=>{ updateValues(); });
heatSlider    .addEventListener('input', ()=>{ updateValues(); });

/* --------------------------- INITIAL CALCULATION --------------------------- */
updateValues();

/* --------------------------- p5.js SKETCH --------------------------- */
new p5( (p)=>{
    p.setup = function(){
        const canvas = p.createCanvas(800,500);
        canvas.parent('visualization-area');
        p.textFont('Arial');
        p.textSize(14);
        p.textAlign(p.LEFT, p.CENTER);
    };

    // helper to draw arrow
    function drawArrow(x1,y1,x2,y2,color,thickness=3, dashed=false){
        p.push();
        p.stroke(color);
        p.fill(color);
        p.strokeWeight(thickness);
        if(dashed){
            const dash=10, gap=6;
            const len= p.dist(x1,y1,x2,y2);
            const nx = (x2-x1)/len;
            const ny = (y2-y1)/len;
            let drawn=0;
            while(drawn < len - dash){
                p.line(x1+nx*drawn, y1+ny*drawn, x1+nx*(drawn+dash), y1+ny*(drawn+dash));
                drawn += dash+gap;
            }
        }else{
            p.line(x1,y1,x2,y2);
        }
        // arrowhead
        const angle = p.atan2(y2-y1, x2-x1);
        p.translate(x2,y2);
        p.rotate(angle);
        p.triangle(0,0, -10,4, -10,-4);
        p.pop();
    }

    p.draw = function(){
        p.background('#F0F0F0');

        // Flash vessel
        p.fill('#E6E6FA');
        p.stroke(0);
        p.rect(350,200,100,100);
        p.fill(0);
        p.noStroke();
        p.textAlign(p.CENTER, p.CENTER);
        p.text('Flash', 400,250);

        // Feed arrow (left -> vessel)
        drawArrow(100,250,350,250,'#9ACD32');

        // Vapor arrow (up then right)
        drawArrow(400,200,400,100,'#9370DB');
        drawArrow(400,100,550,100,'#9370DB');

        // Liquid arrow (down then right)
        drawArrow(400,300,400,400,'#FFA500');
        drawArrow(400,400,550,400,'#FFA500');

        // Heat input (dashed red)
        drawArrow(300,350,350,300,'#FF0000',2,true);

        /* ---------- TEXT INFORMATION ----------- */
        p.textAlign(p.LEFT, p.CENTER);
        // Feed info
        p.fill(0);
        p.text('Feed:', 120, 220);
        p.text(`F = ${feedFlow.toFixed(1)} kmol/h`, 120, 235);
        p.text(`T = ${T_F.toFixed(1)} K`, 120, 250);
        p.text(`P = ${P_F.toFixed(0)} kPa`, 120, 265);
        p.text('z = [0.25,0.25,0.25,0.25]', 120, 280);

        if(flashResult && flashResult.T_flash){
            const {T_flash,V_frac,x,y} = flashResult;
            // Process info (middle right)
            let ypos = 140;
            p.text(`Flash P = ${P_flash.toFixed(0)} kPa`, 560, ypos); ypos+=18;
            p.text(`Flash T = ${T_flash.toFixed(1)} K`, 560, ypos); ypos+=18;
            p.text(`Vapor frac = ${V_frac.toFixed(3)}`, 560, ypos);

            // Vapor info (top right)
            p.text(`y = [${y.map(v=>v.toFixed(2)).join(', ')}]`, 560, 70);

            // Liquid info (bottom right)
            p.text(`x = [${x.map(v=>v.toFixed(2)).join(', ')}]`, 560, 430);
        }

        // Heat info near arrow
        p.text(`Q = ${Q_hr.toFixed(0)} kJ/h`, 260, 365);
    };
});
</script>
</body>
</html>
