        <label>options</label>
        <div>
            <input type="checkbox" id="checkbox-constellations" checked>
            <label for="checkbox-constellations">constellations</label>
        </div>
        <div>
            <input type="checkbox" id="checkbox-realistic-size">
            <label for="checkbox-realistic-size">realistic size</label>
        </div>
    </div>

    <!-- =====================  CANVAS  ===================== -->
    <div id="visualization-container">
        <canvas id="scene-canvas"></canvas>
    </div>
</div>

<!-- =====================  JAVASCRIPT  ===================== -->
<script>
/* ==========================================================
   GLOBALS & STATE
========================================================== */
const params = {
    time: 0,
    nodalAngle: 0,
    sunEarthAngle: 1.571,
    zodiac: 0
};
const isAnimating = {
    time: false,
    nodalAngle: false,
    sunEarthAngle: false,
    zodiac: false
};
let viewMode = 'land'; // 'land' | 'space'

/* ==========================================================
   THREE.JS INITIALISATION
========================================================== */
let scene, camera, renderer, clock;
let earth, atmosphere, moonSurface, moonSphere, constellationGroup, sunLight;
initThree();
animate();

function initThree(){
    /* Scene & Renderer */
    scene = new THREE.Scene();
    renderer = new THREE.WebGLRenderer({canvas: document.getElementById('scene-canvas'), antialias:true, alpha:false});
    renderer.setPixelRatio(window.devicePixelRatio);
    resize();
    window.addEventListener('resize', resize);

    clock = new THREE.Clock();

    /* Camera */
    camera = new THREE.PerspectiveCamera(50, window.innerWidth/window.innerHeight, 0.1, 1000);

    /* Lights */
    sunLight = new THREE.DirectionalLight(0xffffff, 1.0);
    sunLight.position.set(5,2,2);
    scene.add(sunLight);
    const ambient = new THREE.AmbientLight(0x404040, 1.2);
    scene.add(ambient);

    /* Earth */
    const earthGeom = new THREE.SphereGeometry(1, 48, 32);
    const earthMat = new THREE.MeshPhongMaterial({color:0x0044ff, shininess:20});
    earth = new THREE.Mesh(earthGeom, earthMat);
    earth.castShadow = true;
    earth.receiveShadow = true;
    scene.add(earth);

    /* Atmosphere */
    const atmGeom = new THREE.SphereGeometry(1.05, 48, 32);
    const atmMat = new THREE.MeshBasicMaterial({color:0x3388ff, transparent:true, opacity:0.15, blending:THREE.AdditiveBlending});
    atmosphere = new THREE.Mesh(atmGeom, atmMat);
    earth.add(atmosphere);

    /* Moon Surface (land view) */
    const planeGeom = new THREE.PlaneGeometry(100,100,50,50);
    const planeMat = new THREE.MeshStandardMaterial({color:0x666666, roughness:1, metalness:0});
    moonSurface = new THREE.Mesh(planeGeom, planeMat);
    moonSurface.rotation.x = -Math.PI/2;
    moonSurface.position.y = -1;
    scene.add(moonSurface);

    /* Moon Sphere (space view) */
    const moonGeom = new THREE.SphereGeometry(2, 48, 32);
    const moonMat = new THREE.MeshStandardMaterial({color:0x888888});
    moonSphere = new THREE.Mesh(moonGeom, moonMat);
    scene.add(moonSphere);

    /* Constellations (simple random stars placeholder) */
    constellationGroup = new THREE.Group();
    const starPositions = new Float32Array(300*3);
    for(let i=0;i<300;i++){
        const r = 50;
        const phi = Math.random()*Math.PI*2;
        const costheta = Math.random()*2-1;
        const theta = Math.acos(costheta);
        starPositions[i*3]   = r*Math.sin(theta)*Math.cos(phi);
        starPositions[i*3+1] = r*Math.sin(theta)*Math.sin(phi);
        starPositions[i*3+2] = r*Math.cos(theta);
    }
    const starGeom = new THREE.BufferGeometry();
    starGeom.setAttribute('position', new THREE.BufferAttribute(starPositions,3));
    const starMat = new THREE.PointsMaterial({color:0xffffff, size:0.2});
    const stars = new THREE.Points(starGeom, starMat);
    constellationGroup.add(stars);
    scene.add(constellationGroup);

    setView('land');
}

/* ==========================================================
   UI HOOK-UP
========================================================== */
function $(id){ return document.getElementById(id); }

/* -----  VIEW BUTTONS  ----- */
$('btn-land').addEventListener('click', ()=>{ setView('land'); });
$('btn-space').addEventListener('click', ()=>{ setView('space'); });

function setView(mode){
    viewMode = mode;

    if(mode==='land'){
        $('btn-land').classList.add('active');
        $('btn-space').classList.remove('active');

        moonSurface.visible = true;
        moonSphere.visible = false;

        camera.position.set(0, 1.6, 0.1);
        camera.lookAt(earth.position);

    }else{
        $('btn-space').classList.add('active');
        $('btn-land').classList.remove('active');

        moonSurface.visible = false;
        moonSphere.visible = true;

        camera.position.set(0, 4, 8);
        camera.lookAt(0,0,0);
    }
    renderScene();
}

/* -----  SLIDERS  ----- */
bindSlider('time');
bindSlider('nodal-angle');
bindSlider('sun-earth-angle');
bindSlider('zodiac');

function bindSlider(name){
    const slider = $('slider-'+name);
    const valueSpan = $('slider-'+name+'-value');
    slider.addEventListener('input', ()=>{
        const floatVal = parseFloat(slider.value);
        switch(name){
            case 'time': params.time = floatVal; break;
            case 'nodal-angle': params.nodalAngle = floatVal; break;
            case 'sun-earth-angle': params.sunEarthAngle = floatVal; break;
            case 'zodiac': params.zodiac = floatVal; break;
        }
        valueSpan.textContent = floatVal.toFixed(2);
        if(name==='sun-earth-angle'){ MathJax.typesetPromise(); }
        renderScene();
    });
}

/* -----  PLAY BUTTONS  ----- */
bindPlayButton('time');
bindPlayButton('nodal-angle');
bindPlayButton('sun-earth-angle');
bindPlayButton('zodiac');

function bindPlayButton(name){
    const btn = $('btn-'+name+'-play');
    btn.addEventListener('click', ()=>{
        isAnimating[name.replace(/-/g,'')] = !isAnimating[name.replace(/-/g,'')];
        btn.textContent = isAnimating[name.replace(/-/g,'')] ? '⏸' : '+';
    });
}

/* -----  CHECKBOXES  ----- */
$('checkbox-constellations').addEventListener('change', (e)=>{
    constellationGroup.visible = e.target.checked;
    renderScene();
});
$('checkbox-realistic-size').addEventListener('change', (e)=>{
    updateEarthScale(e.target.checked);
    renderScene();
});

/* ==========================================================
   RENDER LOGIC
========================================================== */
function updateScenePositions(){
    /* Earth orbital position around origin (Moon). */
    const radius = 4;
    const ang = params.time*0.05 + params.sunEarthAngle;
    /* Apply nodal angle tilt */
    const yTilt = Math.sin(params.nodalAngle)*0.5;
    earth.position.set(
        radius * Math.cos(ang),
        yTilt,
        radius * Math.sin(ang)
    );
    /* Constellations rotation */
    constellationGroup.rotation.y = params.zodiac;
    /* Sun light direction (opposite of Earth for demo) */
    const sunDir = new THREE.Vector3(-Math.cos(ang), -0.2, -Math.sin(ang)).normalize();
    sunLight.position.copy(sunDir.multiplyScalar(10));
    sunLight.target.position.set(0,0,0);
    sunLight.target.updateMatrixWorld();
}

/* scale Earth */
function updateEarthScale(realistic){
    if(realistic){
        earth.scale.set(0.27,0.27,0.27);
    }else{
        earth.scale.set(1,1,1);
    }
}

function renderScene(){
    updateScenePositions();
    renderer.render(scene,camera);
}

/* ==========================================================
   ANIMATION LOOP
========================================================== */
function animate(){
    requestAnimationFrame(animate);

    const delta = clock.getDelta();

    /* Update sliders if animating */
    if(isAnimating.time){
        params.time += delta*5;
        if(params.time>100) params.time=0;
        $('slider-time').value = params.time;
        $('slider-time-value').textContent = params.time.toFixed(2);
    }
    if(isAnimating.nodalangle){
        params.nodalAngle = (params.nodalAngle + delta)%6.283;
        $('slider-nodal-angle').value = params.nodalAngle;
        $('slider-nodal-angle-value').textContent = params.nodalAngle.toFixed(2);
    }
    if(isAnimating.sunearthangle){
        params.sunEarthAngle = (params.sunEarthAngle + delta)%6.283;
        $('slider-sun-earth-angle').value = params.sunEarthAngle;
        $('slider-sun-earth-angle-value').textContent = params.sunEarthAngle.toFixed(2);
    }
    if(isAnimating.zodiac){
        params.zodiac = (params.zodiac + delta)%6.283;
        $('slider-zodiac').value = params.zodiac;
        $('slider-zodiac-value').textContent = params.zodiac.toFixed(2);
    }

    updateScenePositions();
    renderer.render(scene,camera);
}

/* ==========================================================
   UTILITIES
========================================================== */
function resize(){
    const width = document.getElementById('visualization-container').clientWidth;
    const height = document.getElementById('visualization-container').clientHeight;
    renderer.setSize(width,height);
    camera.aspect = width/height;
    camera.updateProjectionMatrix();
}
</script>
</body>
</html>
