    </style>
</head>

<body>
    <h1>Best Effort Global Warming Trajectories</h1>
    <p>
        Adjust the sliders to explore how atmospheric CO₂ residence time, the pace of transition to emission reductions,
        and the number of “stabilization wedges” influence long-term atmospheric carbon and emissions.
    </p>

    <!-- Control Panel -->
    <div id="controls-container">
        <div class="control">
            <label for="slider-residence-time">Atmospheric CO2 residence time (years)</label>
            <input type="range" id="slider-residence-time" min="50" max="500" step="1" value="200">
            <span id="value-residence-time">200</span>
        </div>

        <div class="control">
            <label for="slider-transition-pace">Transition to constant reduction pace (years)</label>
            <input type="range" id="slider-transition-pace" min="0" max="100" step="1" value="25">
            <span id="value-transition-pace">25</span>
        </div>

        <div class="control">
            <label for="slider-wedges">Number of constant pace wedges (above emission stabilization at t=0)</label>
            <input type="range" id="slider-wedges" min="0" max="10" step="0.01" value="4">
            <span id="value-wedges">4.00</span>
        </div>
    </div>

    <!-- Plot Area -->
    <div id="plot-div"></div>

    <!-- JavaScript Logic -->
    <script>
        // Slider elements
        const sliderResidence = document.getElementById('slider-residence-time');
        const sliderTransition = document.getElementById('slider-transition-pace');
        const sliderWedges = document.getElementById('slider-wedges');

        // Value display elements
        const valueResidence = document.getElementById('value-residence-time');
        const valueTransition = document.getElementById('value-transition-pace');
        const valueWedges = document.getElementById('value-wedges');

        // Constants for the model
        const C0 = 850;      // GtC
        const E0 = 8.5;      // GtC/yr
        const C_eq = 586;    // GtC
        const E_floor = 1.5; // GtC/yr
        const rise_rate = 0.02; // GtC/yr^2
        const time_step = 0.5;  // years
        const max_time = 300;   // years

        // Pre-compute the time vector once
        const time = [];
        for(let t = 0; t <= max_time + 1e-9; t += time_step){
            time.push(parseFloat(t.toFixed(3)));
        }

        // Update numeric display helper
        function updateDisplay(){
            valueResidence.textContent = sliderResidence.value;
            valueTransition.textContent = sliderTransition.value;
            valueWedges.textContent = parseFloat(sliderWedges.value).toFixed(2);
        }

        // Core simulation and plotting
        function updatePlot(){
            // Read slider values
            const t_res = parseFloat(sliderResidence.value);
            const t_trans = parseFloat(sliderTransition.value);
            const N_wedges = parseFloat(sliderWedges.value);

            const E_peak = E0 + rise_rate * t_trans;
            const reduction_rate = N_wedges / 50; // GtC/yr^2

            const emissions = [];
            const carbon = [];

            let C_prev = C0;

            // Loop over time steps
            for(let i = 0; i < time.length; i++){
                const t = time[i];

                // Emission rate E(t)
                let E_t;
                if(t < t_trans){
                    E_t = E0 + rise_rate * t;
                } else {
                    const E_linear = E_peak - reduction_rate * (t - t_trans);
                    E_t = Math.max(E_floor, E_linear);
                }

                // Carbon concentration C(t) via Euler
                const dC = (E_t - (C_prev - C_eq) / t_res) * time_step;
                const C_curr = C_prev + dC;

                // Store values
                emissions.push(100 * E_t); // scaled for plotting
                carbon.push(C_curr);

                // Prepare for next iteration
                C_prev = C_curr;
            }

            // Traces
            const traces = [
                {
                    x: time,
                    y: carbon,
                    mode: 'lines',
                    line: {color:'blue', width:2},
                    name: 'Atmospheric Carbon'
                },
                {
                    x: time,
                    y: emissions,
                    mode: 'lines',
                    line: {color:'purple', width:2},
                    name: 'Annual Emissions ×100'
                },
                {
                    x: [0, max_time],
                    y: [1200, 1200],
                    mode: 'lines',
                    line: {color:'black', width:2, dash:'dash'},
                    hoverinfo:'skip',
                    showlegend:false
                }
            ];

            // Layout
            const layout = {
                title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
                xaxis: {title:'years', range:[0, max_time], zeroline:true},
                yaxis: {title:'GtC or GtC/yr × 100', range:[0, 1600], zeroline:true},
                showlegend:false,
                margin:{l:60, r:20, b:40, t:80},
                paper_bgcolor:'#f9f9f9',
                plot_bgcolor:'white'
            };

            Plotly.react('plot-div', traces, layout, {displayModeBar:false});
        }

        // Event listeners
        [sliderResidence, sliderTransition, sliderWedges].forEach(slider=>{
            slider.addEventListener('input', ()=>{
                updateDisplay();
                updatePlot();
            });
        });

        // Initial render
        updateDisplay();
        updatePlot();
    </script>
</body>
</html>
