const valueTheta0   = document.getElementById('value-theta0');
const valueV0       = document.getElementById('value-v0');
const valueLength   = document.getElementById('value-length');

const btnPlayPause  = document.getElementById('btn-play-pause');
const btnReset      = document.getElementById('btn-reset');

/* -------------------- SIMULATION STATE -------------------- */
let isPlaying   = false;
let t           = 0;
let theta       = parseFloat(sliderTheta0.value);
let theta_dot   = 0;
let x           = 0;
let x_dot       = parseFloat(sliderV0.value);
let l           = parseFloat(sliderLength.value);
let v0          = parseFloat(sliderV0.value);

// constants
const g = 9.8;
const m = 1;
const M = 1;
const scale = 50;    // pixels per meter

/* -------------------- UTILITIES -------------------- */
function formatAngle(rad){
    const fracList = [
        {v: -Math.PI   , label:"-π"},
        {v: -3*Math.PI/4, label:"-3π/4"},
        {v: -2*Math.PI/3, label:"-2π/3"},
        {v: -Math.PI/2 , label:"-π/2"},
        {v: -Math.PI/3 , label:"-π/3"},
        {v: -Math.PI/4 , label:"-π/4"},
        {v: -Math.PI/6 , label:"-π/6"},
        {v: 0          , label:"0"},
        {v:  Math.PI/6 , label:"π/6"},
        {v:  Math.PI/4 , label:"π/4"},
        {v:  Math.PI/3 , label:"π/3"},
        {v:  Math.PI/2 , label:"π/2"},
        {v: 2*Math.PI/3, label:"2π/3"},
        {v: 3*Math.PI/4, label:"3π/4"},
        {v:  Math.PI   , label:"π"}
    ];
    const tol = 0.02; // radians
    for(let f of fracList){
        if(Math.abs(rad - f.v) < tol) return f.label;
    }
    return rad.toFixed(2);
}

function updateSliderDisplays(){
    valueTheta0.textContent = formatAngle(parseFloat(sliderTheta0.value));
    valueV0.textContent     = parseFloat(sliderV0.value).toFixed(1);
    valueLength.textContent = parseFloat(sliderLength.value).toFixed(1);
}

function resetSimulation(){
    isPlaying   = false;
    btnPlayPause.textContent = "▶";

    t           = 0;
    theta       = parseFloat(sliderTheta0.value);
    theta_dot   = 0;

    x           = 0;
    v0          = parseFloat(sliderV0.value);
    x_dot       = v0;

    l           = parseFloat(sliderLength.value);
}

/* -------------------- EVENT LISTENERS -------------------- */
[sliderTheta0, sliderV0, sliderLength].forEach(slider=>{
    slider.addEventListener('input', ()=>{
        updateSliderDisplays();
        resetSimulation();
    });
});

btnPlayPause.addEventListener('click', ()=>{
    isPlaying = !isPlaying;
    btnPlayPause.textContent = isPlaying ? "||" : "▶";
});

btnReset.addEventListener('click', ()=>{
    resetSimulation();
});

/* -------------------- P5.JS SKETCH -------------------- */
let sketch = (p)=>{

    p.setup = function(){
        const canvas = p.createCanvas(500, 500);
        // attach to visualization container
        canvas.parent('viz-container');
        p.frameRate(60);
        updateSliderDisplays();
        resetSimulation();
    };

    p.draw = function(){
        // background
        p.background('#F0F0F0');

        // update physics if playing
        if(isPlaying){
            let dt = p.deltaTime/1000; // seconds
            // angular acceleration (equation provided)
            let sinT = Math.sin(theta);
            let cosT = Math.cos(theta);
            let denom = l * (M + m * sinT * sinT);
            let theta_ddot = (-m * l * sinT * cosT * theta_dot * theta_dot - g * (M + m) * sinT) / denom;

            // integrate
            theta_dot += theta_ddot * dt;
            theta     += theta_dot * dt;

            // pivot velocity from conservation
            x_dot = v0 - (m * l * cosT * theta_dot) / (M + m);
            x     += x_dot * dt;

            t += dt;
        }

        // screen coordinates
        const px = p.width/2 + x * scale;
        const py = p.height/2;

        const bx = px + (l * scale) * Math.sin(theta);
        const by = py + (l * scale) * Math.cos(theta);

        // draw connector
        p.stroke('black');
        p.strokeWeight(2);
        p.line(px, py, bx, by);

        // draw pivot
        p.noStroke();
        p.fill('red');
        const pivotW = 24, pivotH = 12;
        p.rectMode(p.CENTER);
        p.rect(px, py, pivotW, pivotH);

        // draw bob
        p.stroke('black');
        p.fill('orange');
        p.strokeWeight(2);
        p.circle(bx, by, 30);
    };
};

new p5(sketch);
</script>
</body>
</html>
